INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(2, 'media/SanPedrodeRocas1.avi', '', 'Vídeo', '', 'Video', '', '', 1, 100, '', '', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(3, 'media/SanPedrodeRocas2.jpg', '', 'Casa Rectoral', 'Construcción del s.XVII, levantada con los restos del monasterio primitivo, derribado en parte después de un incendio. Su función original era la de dar cobijo a los monjes. En la actualidad alberga un museo etnográfico.', 'Imagen', '', '', 2, 100, 'Rectory', 'Dating from 18th century, it was erected from the ruins of an ancient monastery, destroyed after a great fire.  The Rectory was mainly used as a house for the clergy. At present, there is an ethnographic museum.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(4, 'media/SanPedrodeRocas3.jpg', '', 'Muro exterior', 'Nave construida en el s.XV como ampliación, en pleno auge del monasterio. En sus muros podemos leer la siguiente inscripción: ESTA OBRA FIZO FACER D. FR. GONZLO DE PENALVA, PRIOR DE ROCAS. ERA MCCCCXVIII (1418).', 'Imagen', '', '', 3, 100, 'Outside Wall', 'The nave was constructed as an extension of the monastery in the 15th century, when the life in the monastery was growing strongly. In the wall it can be read the following: ESTA OBRA FIZO FACER D. FR. GONZLO DE PENALVA, PRIOR DE ROCAS. ERA MCCCCXVIII (1418) [This work was done by D.FR. Gonzalo de Penalva, Priest in Rocas. Period MCCCCXVIII (1418)].', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(5, 'media/SanPedrodeRocas4.jpg', '', 'Sepulcros antropomorfos (forma humana)', 'La mayor parte pertenecieron a monjes del monasterio que, según la tradición, labraban su propia tumba como acto de reflexión. Los más antiguos miran hacia el altar mayor de las capillas.', 'Imagen', '', '', 4, 100, 'Anthropomorphic Tombs (human shape)', 'Most of them belonged to the monks who lived in the monastery. According to the legend, they dug their own tomb in order to meditate. The old ones were built facing the main altar in the chapels.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(6, 'media/SanPedrodeRocas5.jpg', '', 'Las tres capillas', 'Las cuevas trogloditas que daban cobijo a los primeros monjes, se encuentran labradas en la misma montaña. La central es de mayores dimensiones, y cuenta con una abertura en el techo para recibir la luz del día. Destacan los arcos de medio punto que les dan acceso, el central y el derecho, más antiguos, de estilo románico.', 'Imagen', '', '', 5, 100, 'Three Chapels', 'The troglodyte caves that were home to the first monks were excavated in the same rock of the mountain. The one in the middle is the biggest one and it has a hole in the top part so as to let the daylight enter. It is noticeable for its round arches which serve as an entrance, the central and the right one are older and belong to the Romanesque style.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(7, 'media/SanPedrodeRocas6.jpg', '', 'Tumbas', 'Varios condes se rebelaron en esta zona contra el rey, invadiendo, entre otras, algunas tierras del monasterio. Dos de ellos fueron heridos en batalla, Suero de Gondomáriz y Oveco. Los monjes los acogieron a pesar de sus actos y curaron sus heridas, por lo que los caballeros, en agradecimiento, devolvieron los dominios y pidieron su entierro en este lugar. ', 'Imagen', '', '', 6, 100, 'Tombs', 'Several counts rose up against the King in the area. They occupied some pieces of land belonging to the clergy. Two of these counts were wounded in the battle, Suero de Gondomáriz and Oveco. Although the counts had acted against the clergy, the monks gave them shelter and took care of them. Hence, the counts were very pleased with the monks and they gave them back their territories and they asked to be buried in this place. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(8, 'media/SanPedrodeRocas7.jpg', '', 'El altar', 'En el Museo Arqueolóxico de Ourense se conserva un pié de altar, que algunos datan, por su decoración, ya en época romana. Debido a su alto valor histórico y artístico, y después de sufrir varias agresiones vandálicas, se optó por su traslado a la capital. Su ubicación original estaba junto al cristo.', 'Imagen', '', '', 7, 100, 'Altar', 'The Archaeological Museum contains an altarpiece which supposedly dates back to the Roman Empire, considering its ornaments. It is a very important archaeological piece so it was moved on to the capital city because it had suffered some acts of vandalism previously. Before these acts of vandalism it was placed next to the Christ. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(9, 'media/SanPedrodeRocas8.jpg', '', 'Lápida fundacional', 'También protegida en el Museo Arqueolóxico de Ourense por su gran valor y antigüedad. En ella podemos leer lo siguiente: HEREDITAS:N EVFRAXI:EVSANI:QVINEDI:EATI:FLAVI:RVVE:ERA.dªCXªI: (El patronato hereditario de esta iglesia es de nosotros cinco, Eufrasio, Euga, Quinedio, Eato y Flavio, que la hemos edificado y dotado siendo Vitimer obispo, en el año 573).', 'Imagen', '', '', 8, 100, 'Memorial Stone', 'The Archaeological Museum contains an altarpiece which supposedly dates back to the Roman Empire, considering its ornaments. It is a very important archaeological piece so it was moved on to the capital city because it had suffered some acts of vandalism previously. Before these acts of vandalism it was placed next to the Christ. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(10, 'media/SanPedrodeRocas9.jpg', '', 'Pinturas murales', 'Se trata del mapamundi mural más antiguo del que se tiene constancia y el único ejemplo de pintura románica que se conserva en Galicia. En él aparecen los apóstoles predicando por el mundo (el conocido en aquel entonces), con los nombres de las ciudades donde ejercieron su tarea. Se encuentra muy deteriorado y únicamente se distinguen algunos detalles. ', 'Imagen', '', '', 9, 100, 'Mural Paintings', 'It is considered as the old registered mural world map and as the only evidence of Romanesque painting preserved in Galicia. The apostles are depicted in it, preaching everywhere (as far as it had been discovered) with the names of the cities where they worked. It is highly damaged and few details are perceived. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(11, 'media/SanPedrodeRocas10.jpg', '', 'Fuente de San Benito', 'Se encuentra a unos 150 metros del monasterio, a través de un agradable sendero. Según los vecinos, el manantial cuenta con propiedades milagrosas, en especial la cura de verrugas, previa condición del rezo de un padrenuestro.', 'Imagen', '', '', 10, 100, 'San Benito’s Fountain', 'Located at about 150 meters from the monastery, you can get there through a pleasant path. According to the local residents, the spring has some miraculous attributes. It can be used to treat warts, providing the user has already prayed a Lord’s Prayer.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(12, 'media/PenedosdoCastro1.jpg', '', 'Caseta', 'Sencillo paseo de unos 400 metros, que nos llevará hasta el yacimiento arqueológico y mirador natural de Penedos do Castro. Aparcaremos en una caseta junto al cruce de Paradela, siguiendo a continuación la senda que sale desde la parte derecha.', 'Imagen', '', '', 1, 101, 'Hut', 'Pleasant 400 metres walking which leads us to the archaeological site considered as a natural viewpoint in Penedos do Castro.  We can park in the hut next to the Paradela crossing. Then, we go through the path on the right. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(13, 'media/SantoEstevoRibasSil3.jpg', '', 'Retablo pétreo', 'Entre las obras de arte que encontramos en la iglesia, destaca un retablo de piedra, románico del s.XII, encontrado casualmente durante unas obras de restauración. En él aparecen Cristo y los apóstoles, destacando Pedro con las llaves del cielo, Judas, que a diferencia del resto, carece de aureola, y una de las imágenes más antiguas que existen de Santiago con las conchas de peregrino.', 'Imagen', '', '', 3, 102, 'Stoned Altarpiece', 'Among the art works that can be found in the church, it is noticeable the stoned altarpiece, dating from the Romanesque in the 12th century. It was found by chance while there were restoration works. It features religious scenes. Christ can be seen with the apostles, where St. Peter is holding the heaven’s keys. It is also noticeable that Judas does not have a halo.  We can also see one of the most ancient existing images about St. James holding the Scallop Shell. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(14, 'media/SantoEstevoRibasSil4.jpg', '', 'Retablo de la iglesia', 'Retablo renacentista del siglo XVI, obra de Juan de Angés. Se encuentran representadas varias escenas de la vida de Jesús: la Anunciación, la Adoración de los Reyes y de los pastores, la Circuncisión, la presentación en el templo o las bodas de Canaan', 'Imagen', '', '', 4, 102, 'Church Altarpiece', 'Renaissance Altarpiece of the 16th century, built by Juan de Angés.  It symbolizes some scenes belonging to the Jesus life. It can be seen the Annunciation, the Adoration of the Magi and the shepherds, the Circumcision, the appearance in the temple and the Canaan’s nuptials. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(15, 'media/SantoEstevoRibasSil5.jpg', '', 'Ábside', 'La cabecera románica de la iglesia es la parte más interesante de su exterior. En él destacan numerosas figuras esculpidas, representando motivos vegetales, animales y humanos.', 'Imagen', '', '', 5, 102, 'Apse', 'The Romanesque header of the church is the most interesting piece of its outer part.  There are some carved figures performing vegetable, animal and human elements. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(16, 'media/SantoEstevoRibasSil6.jpg', '', 'Claustro dos Cabaleiros', 'De estilo renacentista, se le conoce con este nombre porque en sus habitaciones se alojaban los nobles que visitaban el monasterio. Sin embargo, su función principal fue la de dar cobijo a los colegiales que acudían a estudiar a la abadía. El gran muro de Cristal procede de la última restauración, en 2004, y fue diseñado por el arquitecto Javier Suances. ', 'Imagen', '', '', 6, 102, 'Cloister of the Knights', 'The Renaissance cloister is known like the Cloister of the Knights due to the fact that in its rooms were hosted nobles that visited the monastery. Nevertheless, its main role was the one of giving shelter to the schoolchildren who went to the abbey in order to study.  The great glass wall was designed in the last renovation in 2004 by the architect Javier Suances. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(17, 'media/SantoEstevoRibasSil7.jpg', '', 'Claustro dos Bispos', 'El más antiguo del monasterio, con importantes restos románicos en muchos de los 120 arcos de los que consta el piso inferior. Comenzó su construcción en el año 1220 como homenaje a los nueve obispos santos. Aquí se encontraba hasta 1856 la famosa fuente de la Plaza do Ferro, en Ourense.', 'Imagen', '', '', 7, 102, 'Cloister of the Bishops', 'It is the oldest cloister in the monastery. It has 120 arches in the ground floor, containing important Romanesque remains. Its construction began in 1220 as a tribute to the nine saint bishops. Here stood until 1856 the famous fountain which is now placed in the Plaza do Ferro in Ourense.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(18, 'media/SantoEstevoRibasSil8.jpg', '', 'El perro y la liebre', 'Representación de una escena de caza, en la que un perro persigue a una liebre (este animal era considerado en la época como impuro, y su fecundidad evocaba aspectos como despilfarro, exceso y desorden). ', 'Imagen', '', '', 8, 102, 'The dog and the hare', 'It is a performance of a hunting scene. A dog runs after a hare, an animal that was considered as impure and its fertility was related to waste, excess and chaos. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(19, 'media/SantoEstevoRibasSil9.jpg', '', 'La Arpía', 'En el románico, las arpías simbolizan las bajas pasiones, así como el dolor y el sentimiento de culpa por la satisfacción de los vicios. El nudo en la cola representa el triunfo de la fuerza espiritual frente a los instintos naturales del hombre. En la parte superior, la imagen de un apóstol portando un libro.', 'Imagen', '', '', 9, 102, 'The Harpy', 'In the Romanesque harpies were considered as representative of lower passions, the pain and the guilty because of the vices satisfaction.  The tail knot represents the spiritual strength versus the natural human instincts.  Over the top of the image it can be seen an apostle holding a book. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(20, 'media/SantoEstevoRibasSil10.jpg', '', 'Las Vieiras', 'Por todo el monasterio encontramos numerosas referencias a la vieira de Santiago, lo que evidencia una cercana relación entre Santo Estevo y Compostela.', 'Imagen', '', '', 10, 102, 'The Scallops', 'Large allusions to the St. James Scallop can be found around the whole monastery. It is clear that there was a close connection between Santo Estevo and Compostela.   ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(21, 'media/SantoEstevoRibasSil11.jpg', '', 'Restos pictóricos', 'Pinturas del siglo XVI, muy deterioradas. Representan escenas de la anunciación del arcángel Gabriel a la virgen y de la tortura a San Esteban, primer mártir cristiano.', 'Imagen', '', '', 11, 102, 'Pictorial Remains', 'Paintings belonging to the 16th century, which are extremely damaged. They perform Annunciation scenes with the archangel Gabriel and the virgin but there are also images of the St. Stephen torture (first Christian martyr). ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(22, 'media/SantoEstevoRibasSil12.jpg', '', 'Claustro do Viveiro', 'De estilo renacentista, el origen de su nombre se remonta a la presencia de una gran fuente, que ocupaba toda la superficie del patio, y en la que los monjes conservaban salmones, lampreas, truchas y anguilas, que traían vivas de las pesqueras del río.', 'Imagen', '', '', 12, 102, 'Viveiro Cloister', 'It is a Renaissance cloister and its origin goes back to the presence of a huge fountain which occupied the whole yard. Salmons, lampreys and other fish were stored there by the monks who caught them in the river.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(23, 'media/SantoEstevoRibasSil13.jpg', '', 'La cocina', 'Destaca la chimenea o lareira, apoyada en cuatro columnas, donde se hacía el fuego para cocinar y calentar agua. Se conservan además tres pilas de agua, dos alacenas, un horno, un torno de servir y canalizaciones de agua corriente. Se complementa con dos instalaciones situadas en el bosque, la panadería, aislada para prevenir incendios, y el matadero. ', 'Imagen', '', '', 13, 102, 'Kitchen', 'The chimney is very important. It is supported by four pillars where people used to light a fire and cook or heat water.  It has been preserved the following: three water fonts, two cupboards, an oven, a lathe used to serve food and finally some water channellings. There is also a bakery and a slaughterhouse placed in the forest in order to avoid fires.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(24, 'media/EmbarcaderoSantoEstevo1.jpg', '', 'El Catamarán de Santo Estevo', 'Embarcación turística que recorre parte de los Cañones del Sil.', 'Imagen', '', '', 1, 103, 'The Santo Estevo Catamaran', 'It is a touristy boat that goes all over almost all the Sil Canyons.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(25, 'media/MiradordeCastro1.jpg', '', 'Como llegar', 'Al llegar al pueblo de Castro, entre Parada de Sil y Santa Cristina, buscaremos la entrada al camping. Aquí encontraremos ya indicaciones sobre el mirador. ', 'Imagen', '', '', 1, 106, 'How to get there', 'When we arrive in Castro village, which is located between Parada de Sil and Santa Cristina, we have to look for the entrance of the camp site. There are some indications about the viewpoint', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(26, 'media/MiradordeCastro2.jpg', '', 'El Mirador', 'Se trata de una espectacular estructura de madera, colgada sobre el río Sil, y ubicada en el interior del camping. Si el camping está cerrado también podemos acceder al mirador. Mirando a la entrada del camping, a la izquierda, vemos una casa con una gran imagen de una Virgen. Justo después de esa casa sale una pista, que nos conduce hasta el mirador', 'Imagen', '', '', 2, 106, 'Viewpoint', 'It is a great wooden structure hanging over the Sil River and it is located inside the cap site. It the camp site is closed, you can also get to the viewpoint. (If you look to the left side of the camp site, there is a house with a huge virgin. Past that house, you can find a clue which leads you to the viewpoint). ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(27, 'media/SantaCristina2.jpg', '', 'San Bieito', 'En un viejo castaño, junto al monasterio, encontraremos un altar improvisado con una figura de San Bieito, motivo de devoción de la romería que se celebra aquí cada 11 de Julio. La imagen data de 1990, obra de un vecino de Parada de Sil, y poco a poco, sobre todo turistas y visitantes, consolidan una nueva tradición, dejando todo tipo de objetos como ofrenda. ', 'Imagen', '', '', 2, 107, 'San Bieito', 'It can be found an altarpiece in a chestnut tree next to the monastery. It contains a St. Bieito image, and it is celebrated a pilgrimage every 11th July in its honour. The image dates back to 1990 and it was made by a local. Currently, it is being established a new tradition gradually; Visitors tend to leave some objects as an offering.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(28, 'media/SantaCristina4.jpg', '', 'El altar románico', 'En el ábside izquierdo de la iglesia se conserva un pequeño altar románico, sencillamente decorado con estrellas. Esta pieza es más antigua que el resto del conjunto, por lo que podría proceder del momento de la fundación del monasterio. ', 'Imagen', '', '', 4, 107, 'Romanesque Altarpiece', 'It is preserved a small Romanesque altarpiece furnished with stars in the left apse.  This is the oldest piece in the group. It could belong to the period when the monastery was founded. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(29, 'media/SantaCristina5.jpg', '', 'Pinturas murales', 'El ábside principal conserva restos de pinturas del s.XVI. Representan a Santa Lucia (fue torturada por ser cristiana y desposeída de sus ojos, pero conservó la vista), Santa Bárbara (encerrada por su padre en una torre), San Domingos (con un rosario), San Antonio (patrón de los animales), San Benito (con el libro de normas benedictinas) y Santo Tomás (con escuadra de arquitecto).', 'Imagen', '', '', 5, 107, 'Mural Paintings', 'The main apse contains some 16th century paintings. They represent Saint Lucy (tortured because she was Christian; she was left without eyes but she kept the sight), Saint Barbara (confined in a tower by her father), Saint Domingos (holding a rosary), Saint Anthony (animals’ patron), Saint Benedict (with the book of the Benedictine rules) and Saint Thomas (with an architect set-square).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(30, 'media/SantaCristina6.jpg', '', 'Decoración', 'En la pared de la iglesia se conservan restos de una columna decorada en el s.XVIII. En este momento el templo se encontraba recubierto por pinturas coloristas y populares, hecho que se puede observar también en la sacristía (se accede desde el claustro).', 'Imagen', '', '', 6, 107, 'Ornament', 'There are some remains of a ornamented column of the 18th century in the church wall. At that moment, the temple was decorated with colouristic and popular paintings what can be also found in the sacristy (you can get into by the cloister).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(31, 'media/SantaCristina8.jpg', '', 'Ábside', 'Uno de los elementos más interesantes del templo románico, es la cabecera, en la que destaca el ábside central, rodeado por los secundarios. Se conservan en este tramo algunos ejemplos interesantes de escultura medieval. ', 'Imagen', '', '', 8, 107, 'Apse', 'The header is one of the most impressive elements of the Romanesque temple. It can be highlighted the central apse enclosed by the rest of the apses.  Here can be found some interesting samples of medieval sculptures.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(32, 'media/SantaCristina9.jpg', '', 'Vasija', 'Tenemos dos teorías sobre esta figura. El personaje, con pezuñas de animal, sostiene una vasija boca abajo. Podría representar el consumo exagerado de alcohol o la utilización del recipiente como instrumento musical, pero en ambos casos haría referencia a los excesos y al vicio como pecado.', 'Imagen', '', '', 9, 107, 'Vessel', 'There are two theories about this image. The main character, which has animal hoof, holds a facing down vessel. It could represent the extreme alcohol consumption or the use of the vessel as a musical instrument. Both options would suggest references to vices and excesses. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(33, 'media/SantaCristina10.jpg', '', 'Gato', 'El gato era un animal maligno en el románico, ya que se asociaba con la traición, la pereza y la lujuria. En este caso parece que vigilase a los parroquianos, advirtiéndoles sobre un castigo en caso de caer en la tentación.', 'Imagen', '', '', 10, 107, 'Cat', 'The cat was an evil animal in the Romanesque because it was related to betrayal, laziness and lust. In this image it seems to be controlling the parishioners so as not to fall in the temptation or they would be cruelly punished. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(34, 'media/SantaCristina11.jpg', '', 'Hombre sentado', 'Podría ser un hombre pensando o sentado en un orinal. Sin embargo, la postura es similar a otras esculturas de la época, en las que se representa a hombres desfogándose en solitario. Sus atributos podrían estar desgastados o amputados en algún momento, por ser consideraron ofensivos. Nos encontraríamos de nuevo ante una advertencia sobre el vicio y la lujuria.', 'Imagen', '', '', 11, 107, 'The sitting man', 'It could be a man who was sitting or a man urinating. However, as it is similar to other sculptures of that time, it can represent a man releasing in solitude.  His attributes could be worn-out or they could have been amputated because they were considered as an offence.  It would be a warning of vice and lust as well. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(35, 'media/SantaCristina12.jpg', '', 'Pareja', 'De nuevo nos encontramos con una figura de carácter erótico, tema de gran extensión en el arte románico. En este caso una escena sexual entre dos personajes, advertencia sobre los castigos del adulterio, el vicio y la perdición.', 'Imagen', '', '', 12, 107, 'Couple', 'It is depicted an erotic figure again, which was a popular subject in the Romanesque art. In this case it is a sexual scene that warns about the punishments that pursue people who are involved in adultery, vices and downfall.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(36, 'media/BalconesdeMadrid2.jpg', '', 'Pozo do Lobo', 'En las proximidades del mirador podemos encontrar un foso antiguo. Utilizando un animal vivo como señuelo, se atraía al lobo hacia el agujero, que se encontraba cubierto con maderas y camuflado con arbustos y hojas. La tapa cedía con el peso del depredador, que caía y era abatido por los cazadores.', 'Imagen', '', '', 2, 108, 'Wolf Well', 'It can be found an old well near the viewpoint. It was used in the hunt. A live animal was used as bait and it was hidden with wood, bushes and leaves. The wolf was led to the hole and then the cover collapsed because of the weight and the wolf was downed by the hunters. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(37, 'media/Entrambosrios1.jpg', '', 'El viejo castaño', 'Encontraremos aquí, justo antes de llegar al pueblo de Entrambosríos, uno de los castaños más antiguos de Galicia. Este árbol milenario, con un tronco de 10 metros de circunferencia, se encuentra catalogado por la Xunta de Galicia como “Árbore Senlleira” y, a pesar de su edad, continúa cada otoño ofreciendo sus castañas.', 'Imagen', '', '', 1, 109, 'Old Chestnut tree', 'In the way to the village of Entrambosríos, there is one of the oldest chestnut trees in Galicia. This millennial tree has 10-metre circumference. It has been classified as “Árbore Senlleira” (Unique tree) by Xunta de Galicia and despite his age, it produces wonderful chestnuts.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(38, 'media/Entrambosrios2.jpg', '', 'Muiños de Entrambosríos', 'Si bajamos hacia el río desde el viejo castiñeiro, encontraremos un puente de madera que nos acercará a los molinos. Utilizados antiguamente para la molienda del maíz y del centeno, aprovechando la fuerza del agua, se encuentran parcialmente restaurados, conservando incluso parte de la maquinaria.', 'Imagen', '', '', 2, 109, 'Entrambosríos Mills', 'There is a wooden bridge which leads you to the mills going down from the chestnut tree to the river. In the past, they were used to grind corn and rye. They are partially restored, preserving even part of the machinery.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(39, 'media/Entrambosrios3.jpg', '', 'Ruta das Castañas', 'En el cercano núcleo de Fondodevila, la recolección de castañas alcanza la categoría de industria artesanal. En esta aldea comienza una sencilla ruta circular, en la que podemos disfrutar de los espectaculares bosques de castaños (Soutos), los viejos caminos de carros (Carriozas) y los tradicionales secaderos de castañas (Sequeiros).', 'Imagen', '', '', 3, 109, 'Chestnuts Route', 'In the village Fondodevila, the chestnuts harvest is contemplated as traditional industry. In this village there is an easy circular route where it can be appreciated the spectacular chestnut tree forests (“Soutos”), the old carriages paths (“Carriozas”) and the traditional chestnut drying places (“Sequeiros”).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(40, 'media/SanVitor2.jpg', '', 'La Necrópolis', 'Detalle de las tumbas en el yacimiento de San Vitor.', 'Imagen', '', '', 2, 110, 'The Necropolis', 'Tombs detail in the San Vitor site.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(41, 'media/SanVitor3.jpg', '', 'Dónde aparcar', 'Dejamos nuestro vehículo antes de entrar en el pueblo de San Lourenzo (existen dos accesos, en la carretera principal o desviándonos en un cruce a la izquierda justo antes de llegar). En esta localidad termina la carretera, no es posible aparcar y es complicado maniobrar. La distancia al yacimiento es de unos 1500 metros, con tramos de pendiente bastante acusada. Para mayor comodidad, recomendamos no llevar la tablet con nosotros para relizar esta visita.', 'Imagen', '', '', 3, 110, 'Parking', 'The vehicle must be left before entering San Lourenzo village (there are two entrances, one in the main road and another changing the course in the left crossing, just before arriving into the village). At this point, it is not possible to park neither manoeuvre.  The site is 1500 metres away and there are zones with steep slope. It is advisable not to carry the Tablet PC in order to do the sightseeing in greater comfort.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(42, 'media/SanVitor4.jpg', '', 'El Pueblo', 'Merece la pena dar una vuelta por los alrededores de la iglesia de San Lourenzo, donde se conservan edificaciones y callejuelas tradicionales que nos trasladan a otra época. Si observamos con calma, podremos descubrir, incrustados en las casas, algunos restos del antiguo monasterio que aquí se ubicaba.', 'Imagen', '', '', 4, 110, 'Village', 'It is well worth visiting the church in San Lourenzo and its surroundings. The constructions and the street design move the visitor to a remote time. If you look at the houses carefully, it can be found some pieces of the old monastery in their walls. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(43, 'media/RutaMao1.jpg', '', 'El cañón del río Mao', 'Sencillo paseo a orillas del río Mao, entre la zona de baño de Barxacova y la “Fábrica da Luz”. Se incluye un espectacular tramo de pasarelas de madera colgadas sobre el río. La distancia a recorrer, suma de ida y vuelta, es de 2 km, y el tiempo estimado de unos 50 minutos.', 'Imagen', '', '', 1, 111, 'River Mao Canyon', 'There is a clam path in the Mao Riverside, between Barxacova and the “Fábrica da Luz” establishment. There are some great footbridges over the river. It is a 50 minutes round trip, there are about 2 km.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(44, 'media/RutaMao2.jpg', '', 'Cómo llegar', 'Nos desviaremos en dirección al pueblo de Barxacova, para a continuación girar a la derecha, hacia la zona de baño. En este punto dejaremos el coche y comenzaremos el paseo. La distancia desde aquí es de 1 km hasta la “Fábrica da Luz”, desde donde volveremos por el mismo camino.', 'Imagen', '', '', 2, 111, 'How to get there', 'It is necessary to change the course and to go towards Barxacova. You should turn right on the way to the bathing area. The cars should be left here and you are ready for the walk. It is a 1 km walk to the “Fábrica da Luz”, where you will return in the same way.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(45, 'media/CastroCaldelas2.jpg', '', 'A Praza do Prado', 'Podemos aparcar en esta plaza triangular, centro de la vida cotidiana en Castro Caldelas. Se encuentra rodeada por edificios singulares, muchos con galería acristalada, y es la encargada de dar entrada al núcleo medieval. ', 'Imagen', '', '', 2, 113, 'A Praza do Prado (Prado Square)', 'Visitors can park the cars here, in this triangular square, centre of everyday life. It is surrounded by peculiar constructions with glass balconies and it is the entrance to the medieval heart.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(46, 'media/CastroCaldelas3.jpg', '', 'Cima de Vila', 'Nos podemos dirigir al castillo por la Rúa Grande o por la Rúa do Sol, estrechas callejuelas flanqueadas por construcciones de piedra. Algunas de las casas conservan su escudo en la entrada, ya que antiguamente pertenecían a las familias nobles de la villa.', 'Imagen', '', '', 3, 113, 'Top of the Vila', 'We can get to the castle through two different streets: Rúa Grande or Rúa do Sol. These two streets are narrow streets lined with old stone buildings. Some of the houses keep their old shield in the entrance because in the past these houses belonged to the Noble families.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(47, 'media/CastroCaldelas4.jpg', '', 'El castillo', 'Entramos por un portón junto a la reproducción de una catapulta medieval. Vemos los escudos de las antiguas familias propietarias. Los lobos (Osorio), el castillo y el león (Enriquez) o los seis roeles (Castro). Aparece también la Tau, símbolo de los Condes de Lemos. Hoy la fortaleza es propiedad de la Casa de Alba, que concedió usufructo al concello de Castro Caldelas.', 'Imagen', '', '', 4, 113, 'Castle', 'The entrance is decorated with an imitation of a medieval catapult. There can be seen the medieval shields of the owners. Every shield belongs to a family: The wolves (Osorio), the castle and the lion (Enriquez) or the six roundels (Castro). There is also a Tau which was the Lemos Counts symbol. Today the castle belongs to the House of Alba, which gave Castro Caldelas Council the right to use the property. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(48, 'media/MolinosCastroCaldelas1.jpg', '', 'La ruta', 'Sencillo paseo de 1,5 km, a orillas del río Edo (también río Caldelas), que nos llevará a través de un frondoso bosque de ribera hasta los molinos de de O Vao (primero, junto a un puente de piedra) y de A Ponte (segundo, junto a la carretera, donde daremos la vuelta). Para mayor comodidad, recomendamos no llevar la tablet para realizar la ruta.', 'Imagen', '', '', 1, 114, 'The Path', 'It is an easy way (1,5 km) by the Edo (or Caldelas) river bank. It goes by a dense riparian forest to the mills in O Vao (the first one next to a stoned bridge) and A Ponte (the second one next to the road where we will turn round). It is advisable not to carry the Tablet PC in order to do the sightseeing in greater comfort.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(49, 'media/MolinosCastroCaldelas2.jpg', '', 'Cómo llegar', 'Pasamos Castro Caldelas, y aproximadamente a 2 kilómetros encontraremos un desvío a la izquierda, que nos señaliza el Área Recreativa Ponte das Táboas (también hay un cartel naranja que indica área recreativa). Seguiremos esta indicación, y en unos 500 metros llegaremos al aparcamiento.', 'Imagen', '', '', 2, 114, 'How to get there', 'After crossing Castro Caldelas, 2 kilometer away, there is turnoff to the left which indicates Área Recreativa (recreation area) Ponte das Táboas (there is also an orange signpost which indicates the recreation area). We have to follow this signpost to arrive to the car park that is about 500 metres further.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(50, 'media/MolinosCastroCaldelas3.jpg', '', 'El Área Recreativa', 'En este amplio espacio contamos con un lugar de baño, mesas de picnic, columpios para los niños y un gran especio verde ideal para lrelajarnos. El paseo comienza al final del parque, junto a un cartel interpretativo de la ruta. Iremos desde aquí hasta que lleguemos al segundo molino, junto al puente de una carretera, donde daremos la vuelta.', 'Imagen', '', '', 3, 114, 'Recreation Area', 'There is a swimming area with picnic tables and swings for children as well as a long green space to relax and enjoy the visit. There is an interpretative sign at the end of the park which indicates the beginning of the walk. The route is from here to the second mill, next to the road bridge. At this point, we must go back. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(51, 'media/Montederramo3.jpg', '', 'La iglesia', 'De estilo Herreriano, fue reedificada en 1598 sobre el primitivo templo medieval. Se conserva un ventanal original del s.XII en el muro sur, descubierto durante la última restauración. Si nos fijamos, podemos observar como algunas de las columnas del templo encuentran inclinadas, como consecuencia del terremoto de Lisboa (1755).', 'Imagen', '', '', 3, 115, 'Church', 'The church has an Herreran style and it was rebuilt over the old medieval temple in 1598. In the southern wall, it was found an original large window belonging to the 12th century.  If you pay a look at them, it can be seen a slight inclination in the temple columns. This inclination was a result of the Lisbon earthquake in 1755.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(52, 'media/Montederramo4.jpg', '', 'Las rejas', 'Uno de los elementos que más nos llamará la atención en el interior de la iglesia, serán las rejas policromadas, dispuestas para separar a los aldeanos de los monjes durante la liturgia. Existe también una puerta con pinturas cerca del altar, en la que se encuentra el escudo de armas de Montederramo.', 'Imagen', '', '', 4, 115, 'The polychromatic bars', 'Inside the church there are the polychromatic bars, one of the most interesting elements inside because they were made to separate local residents from priests in the religious ceremony.  They are also noticeable the paintings on a door close to the altar, where the Montederramo shield can be seen.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(53, 'media/Montederramo5.jpg', '', 'El Retablo Mayor', 'Impresionante obra barroca del s.XVII, creada por el artista Mateo del Prado. En sus 16 metros de altura, se representan escenas de la vida de Jesús, tales como el nacimiento, la condena de Pilatos, la pasión o la resurrección. En la parte superior dos escudos, el de Castilla y León y el propio del monasterio, que incluye un roble junto a los símbolos propios de la Orden Cisterciense.', 'Imagen', '', '', 5, 115, 'Main Altarpiece', 'Impressive Baroque work belonging to the 17th century and created by Mateo del Prado.  It is 16 metres tall and there are Jesus life scenes in it, such as the birth, Pilate sentence or Jesus in his Passion or Resurrection.  On the top of the altarpiece, there are two shields, the one of the monastery and the Castilla y León shield. The monastery shield has an oak next to the Cistercian order symbols. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(54, 'media/Montederramo6.jpg', '', 'El Coro Alto', 'La magnífica sillería del coro, tallada en madera, es una obra del s.XVII, única en Galicia por sus características. Fue restaurada en los últimos años y conservado en su lugar de origen, salvando una propuesta de los años 80 de trasladarlo a la ciudad de Ourense. Su decoración recoge escenas del antiguo y del nuevo testamento.', 'Imagen', '', '', 6, 115, 'High Choir', 'The choir is decorated with 17th century wooden chairs. The magnificent set of chairs is exclusive in Galicia as a result of its wonderful features. It was restored and located in its original place in spite of the attempts to move it to Ourense in the 80’s.  It contains New and Old Testament scenes. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(55, 'media/Montederramo7.jpg', '', 'El Claustro Reglar o Procesional', 'El piso inferior es de estilo gótico, con influencia manuelina portuguesa, mientras que el superior es algo posterior, renacetista. En la actualidad el edificio alberga el Colegio Público de Montederramo, por lo que el claustro es utilizado como patio para los pequeños estudiantes.', 'Imagen', '', '', 7, 115, 'Processional Cloister', 'Its lower floor exhibits Gothic style as well as Manueline (Portugal) influence. However, the upper floor belongs to the Renaissance. Currently, the building has become Montederramo state school and so the cloister is used as playground.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(56, 'media/Montederramo8.jpg', '', 'Claustro de la Portería', 'Construcción renacentista del s.XVI. Se trata de la dependencia más exterior y pública del monasterio, utilizada por el abad para relacionarse con los feligreses.', 'Imagen', '', '', 8, 115, 'Entrance Cloister', 'Renaissance cloister from the 16th century. It is the outer part of the monastery where the abbot talked with the parishioners. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(57, 'media/Montederramo9.jpg', '', 'Los medallones de la Portería', 'Representaciones de Dios, Jesús, la Virgen, Santiago, San Pedro, San Pablo, San Juan, así como de monarcas como Carlos I y Felipe II, que aparecían como vigilantes y protectores de los alumnos que aquí se educaban.', 'Imagen', '', '', 9, 115, 'Entrance Cloister Medallions', 'They represent God, Jesus, Virgin Mary, Saint Peter, Saint Paul and Saint John as well as the images of kings Carlos I (Charles I) and Felipe II (Philip II), who acted as tutors of the monastery students. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(58, 'media/Montederramo10.jpg', '', 'Marrubio', 'En las proximidades de Montederramo encontramos el Conjunto Religioso de Marrubio, con la Capilla de San Antón y un curioso cruceiro de 1778.', 'Imagen', '', '', 10, 115, 'Marrubio', 'The Religious ensemble Marrubio is close to Montederramo, and there it can be seen the San Antón chapel and a singular cross dating back to 1778.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(59, 'media/Montederramo11.jpg', '', 'O Bidueiral', 'Montederramo cuenta con uno de los bosques de mayor valor ecológico de toda Galicia, con 250 hectáreas en las que predomina el abedul, acompañado de especies como el acebo o el roble. En esta fraga, de gran valor natural, Manuel Blanco Romasanta, asesino en serie de mujeres, juzgado en Allariz por hombre lobo (1853), asaltaba a sus víctimas y cometía sus crímenes.', 'Imagen', '', '', 11, 115, 'Bidueiral', 'There is one of the most valuable ecological forests in Galicia in Montederramo. It has about 250 hectares where different kinds of trees predominate as for example birch, holly or oak. The well-known serial killer, Manuel Blanco Romasanta, attacked his victims and committed his crimes here, in this forest.  He was tried in Allariz in 1853. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(60, 'media/Montederramo12.jpg', '', 'San Mamede', 'El monasterio es origen de muchas leyendas. Una de ellas nos cuenta que en la montaña más alta de la zona, tenía su refugio espiritual un monje llamado Mamede. Para construir la ermita, el religioso contaba con la ayuda de dos corzos, pero uno fue devorado por un lobo. Contrariado, Mamede impuso a la fiera el castigo de cargar con unas piedras, utilizadas para terminar la capilla.', 'Imagen', '', '', 12, 115, 'San Mamede', 'The monastery has been an inspiration for different stories and legends. One of the most listened stories is the one of Mamede. Mamede was a monk who lived on the top of the highest mountain in the area. It is said that in order to build the shrine, he counted on the help of two deer. According to the legend, when one of the deer was killed by a wolf, Mamede punished the beast with the imposition of carrying stones in order to build the shrine.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(61, 'media/XunqueiradeEspadanhedo2.jpg', '', 'La Portada de iglesia', 'Fachada neoclásica reconstruida en 1801, después de que la original románica fuese destruida (las teorías más aceptadas hablan de corrientes de agua subterránea o de un rayo como los causantes del destrozo). La imagen es de San Bernardo, gran impulsor de la Orden del Císter.', 'Imagen', '', '', 2, 116, 'Church Front', 'A Neoclassical front was built in 1801, when the original Romanesque one was destroyed (it is thought to be destroyed by a lightning or by streams of ground water). There appears the image of Saint Bernard who was a great supporter of the Cistercian Order. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(62, 'media/XunqueiradeEspadanhedo3.jpg', '', 'La Puerta románica', 'Puerta tapiada de origen románico. Se denomina como Porta dos Mortos, ya que por ella los monjes accedían al cementerio, localizado en el lugar hasta 1976. Podemos ver varios ejemplos de escultura románica, destacando una cabeza con la boca tapada, elemento frecuente en la simbología templaria.', 'Imagen', '', '', 3, 116, 'Romanesque Door', 'A Neoclassical front was built in 1801, when the original Romanesque one was destroyed (it is thought to be destroyed by a lightning or by streams of ground water). There appears the image of Saint Bernard who was a great supporter of the Cistercian Order. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(63, 'media/XunqueiradeEspadanhedo4.jpg', '', 'La cabecera', 'La iglesia cuenta con tres ábsides románicas, correspondientes a las capillas laterales y a la capilla mayor. En esta última podemos ver columnas cortadas y ventanas cegadas debido a la construcción en este lugar de una sacristía barroca. Se conserva de este habitáculo una especie de altar y parte del suelo, conformado por piezas similares a un tablero de ajedrez.', 'Imagen', '', '', 4, 116, 'Header', 'The church has three Romanesque apses that belong to the lateral and the main chapels. The latter has split columns and boarded-up windows because there was a Baroque sacristy in the past from which an altar and some pieces of flooring emulating chess pieces.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(64, 'media/XunqueiradeEspadanhedo5.jpg', '', 'El interior de la iglesia', 'Construcción de origen románico, con una decoración simple sin grandes motivos escultóricos, siguiendo los dictados de la Orden del Cister. No existen bóvedas de piedra, sino que las cubiertas son de madera. ', 'Imagen', '', '', 5, 116, 'Church', 'The church has a Romanesque style and simple ornaments in its interior, according to the Cistercian Order principles. There are no stone vaults but wooden covers.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(65, 'media/XunqueiradeEspadanhedo6.jpg', '', 'La verja', 'La reja que existe en la entrada de la iglesia para limitar el paso a la zona interior, cuenta en su parte superior con una decoración muy peculiar. Se trata de representaciones de caras y bocas, desfiguradas e incluso tenebrosas, que advertían y mantenían a raya a los que escuchaban misa desde esta zona.', 'Imagen', '', '', 6, 116, 'Fence', 'The fence in the church entrance holds a peculiar decoration. It has faces and mouths images. They are distorted and seem to be sinister. They were so in order to control those who listened to the prayers. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(66, 'media/XunqueiradeEspadanhedo7.jpg', '', 'El retablo', 'Magnífica obra de Juan de Angés de finales del s.XVI. Representa escenas de la asunción y la coronación de María, así como de la infancia de Jesús (nacimiento, adoración de los magos, presentación y circuncisión). En el ático, la crucifixión entre la Virgen y San Juan, y la lactación de San Bernardo (insólito y comprometido episodio en la tradición cristiana).', 'Imagen', '', '', 7, 116, 'Altarpiece', 'Great work of Juan de Angés dating to the end of 16th century. It deals with the Asuncion and Coronation of Mary, as well as Jesus childhood (birth, Adoration of the Magi, the appearance in the temple and the Circumcision).  The crucifixion between the Virgin Mary and Saint John and the lactation of Saint Bernard can be perceived in the attic.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(67, 'media/XunqueiradeEspadanhedo8.jpg', '', 'El altar', 'En la capilla mayor destaca el frente del altar, bonita pieza de arte románico decorada con arcos. Podemos ver también el monumento funerario de D. Paio Yáñez de Abelenda, Caballero de Cristo que, después de la muerte, continúa con su tarea de salvaguardar el altar. También se conservan una pila bautismal románica y un cristo articulado crucificado del s.XVII.', 'Imagen', '', '', 8, 116, 'Altar', 'The altar is one of the main pieces of the Romanesque that has arches as ornaments. Here is also the memorial of Mr. Paio Yáñez de Abelenda. He was a knight who continues taking care of the altar, even after his death.  There are still a Romanesque baptismal font and a crucified Christ. Both belong to 17th century.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(68, 'media/XunqueiradeEspadanhedo9.jpg', '', 'El Claustro da Hospedería o dos Cabaleiros', 'Sencillo y austero patio construido en el s.XVI. Se trata de un corredor cerrado, cuya función era la de comunicar las distintas estancias del monasterio. Era el único claustro de esta abadía, ya que se trataba de un modesto priorato de Montederramo. Se pueden encontrar restos románicos pertenecientes al templo original.', 'Imagen', '', '', 9, 116, 'Guests Quarters or Knights Cloister', 'It is a simple and austere 16th century yard. This is a closed corridor which connected all of the rooms. There was only this cloister because Montederramo was an austere priory.  There can be found the Romanesque remains of the original temple. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(69, 'media/XunqueiradeEspadanhedo10.jpg', '', 'Relojes de Sol', 'En las paredes del claustro se observan tres relojes de sol. Uno de ellos está orientado al este, y tiene forma de pentagrama, con numeración arábica. Los otros dos, de esfera oval, están juntos y orientados al sur, uno con numeración árabe y el otro con numeración romana y fecha de 1663. No se conoce la causa de la existencia de tres relojes en tan reducido espacio.', 'Imagen', '', '', 10, 116, 'Sundial', 'There are three sundials on the cloister’s wall. The pentagram sundial is oriented towards the East, and it has Arabic numbering. The remaining sundials are oval and they are oriented towards the South. One of them has Arabic numbering and the other has Roman numbering and it belongs to 1663.  There is no known reason for the close location of these three clocks. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(70, 'media/XunqueiradeEspadanhedo11.jpg', '', 'Dependencias municipales', 'En la actualidad parte del edificio del monasterio está dedicado a albergar algunas dependencias municipales, como oficinas, una biblioteca, la oficina de correos y un centro de salud.', 'Imagen', '', '', 11, 116, 'Municipal Units', 'Currently, part of the building is used as local government offices, state library, post office or health care centre. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(71, 'media/XunqueiradeEspadanhedo12.jpg', '', 'Niñodaguia', 'Cerca de Xunqueira, en dirección a Ourense, se encuentra Niñodaguia, único reducto de alfarería tradicional que se conserva en la provincia. En la localidad podemos encuentran unos hornos comunales restaurados, un museo taller, recientemente inaugurado, y varios artesanos alfareros.', 'Imagen', '', '', 12, 116, 'Niñodaguia', 'We can visit Niñodaguia, near Xunqueira, a village where we can find traditional pottery. There are some restored communal ovens, a museum-workshop and some traditional potters. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(72, 'media/PontedaBoga1.jpg', '', 'Ponte da Boga', 'Admite visitas para particulares y para grupos (en este caso mejor reservar con antelación). La visita es gratuíta. Horarios y contacto en +info.', 'Imagen', '<span style="font-weight: bold;">Verano:</span> Lunes y Martes grupos concertados previa cita. De miércoles a domingo horario de 11:00 a 14:30 y de 16:00 a 20:00 h. <br><br><span style="font-weight: bold;">Invierno:</span> De Lunes a Viernes de 9:00 a 13:30 y de 16:00 a 20:00 h. Sábados y Domingos grupos concertados previa cita. <br><br><span style="font-weight: bold;">Contacto:</span><span style="font-weight: normal;"> 672 070 721<br></span>', '', 1, 210, 'Boga Bridge', 'Free admission available for groups (it is advisable a previous reservation) and individuals. Schedule and information can be found in +info. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(73, 'media/MiradordeMatacas1.jpg', '', 'Primer mirador', 'Llegamos con coche al primer mirador, desde donde podemos ver una bonita panorámica del Sil. A la derecha los espectaculares viñedos de la zona de Doade (Lugo), y a la izquierda Parada de Sil, Castro Caldelas y A Teixeira.', 'Imagen', '', '', 1, 211, 'First Viewpoint', 'Accessible by car, this viewpoint offers wonderful views of the River Sil. On the right, there are some spectacular vineyards in Doade and on the left we can enjoy the villages of Parada de Sil, Castro Caldelas and A Teixeira.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(74, 'media/MiradordeMatacas2.jpg', '', 'Segundo mirador', 'Si caminamos unos 200 metros por la ruta señalizada, llegaremos a las Penas de Matacás, mirador natural sobre el río, desde el que disfrutaremos de una bonita vista aérea del Embarcadero de Doade, así como de su entorno repleto de viñas.', 'Imagen', '', '', 2, 211, 'Second viewpoint', '200 meters walking by the marked path we can get to the Penas de Matacás viewpoint. It is a natural viewpoint over the river, where some fantastic views of the Doade quay and its surroundings can be enjoyed. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(75, 'media/AdegaVella1.jpg', '', 'Adega Vella', 'Realiza visitas para particulares y grupos todo el año. La visita es gratuíta. Horarios y contacto en +info.', 'Imagen', '<span style="font-weight: bold;">Verano: </span>Abierta todos los días de 10.30 a 20.30 h. <br><br><span style="font-weight: bold;">Invierno:</span> Consultar<br><br><span style="font-weight: bold;">Contacto:</span><span style="font-weight: normal;"> 606 807 897 / 660 047 602&nbsp;<br></span>', '', 1, 212, 'Adega Vella', 'There are free planned visits the whole year, for groups or individuals. Schedule and contact in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(76, 'media/EmbarcaderodeDoade1.jpg', '', 'El Catamarán de Doade', 'Embarcación turística que recorre parte de los Cañones del Sil.', 'Imagen', '', '', 1, 213, 'Doade catamaran', 'Touristic boat that goes across River Sil Canyons. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(77, 'media/EmbarcaderodeDoade2.jpg', '', 'El Puente de Paradela', 'Desde época romana, Doade fue ya el punto elegido para cruzar el río, tanto a través de puentes como, cuando estos no existían, a través de barcas. El puente actual data de 1954, y está apoyado sobre otro más antiguo, de principios del s.XX, que fue necesario elevar para salvar la subida del nivel del agua por la construcción del embalse de Santo Estevo. ', 'Imagen', 'Cuando las aguas del embalse están muy bajas, se pueden ver todavía los restos de dos grandes pilares datados en época medieval y que soportaban un singular puente de madera. Esta estructura fue referida en el libro “Descripción del Reino de Galicia” del s.XVI por el Licenciado Molina, que hablaba de él como “cosa digna de admirar” y “cosa notable que, para ser de madera, no puede haber en España edificio igual”. Molina, en la descripción de este lugar, decía lo siguiente: “Está allí el río hundido entre dos sierras muy altas y pasa por medio de dos peñas, y encima de cada una está hecha una torre, y de torre a torre va en el aire un edificio de madera que tiene ciento y veinte pies de largo”.&nbsp;<div><br>Estas ruinas todavía no han sido estudiadas en profundidad, pero las hipótesis de algunos historiadores nos hablan de que posiblemente el puente fue construido por los monjes del Monasterio de Santo Estevo de Ribas de Sil. A través de esta estructura, la abadía obtendría buen número de rentas procedentes del cobro del derecho de paso, al tiempo que facilitaba el acceso hacia el monasterio de las gentes que acudían a pagar las rentas desde Tierras de Lemos.&nbsp;</div><div><br>Este antiguo puente fue destruido a finales del s.XVI por una crecida del río, y a pesar de los esfuerzos de los monjes de Santo Estevo por volver a levantarlo, incluso pidiéndole permiso de forma directa a Felipe IV, se encontraron con la oposición de los Condes de Lemos, que nunca permitieron volver a construir la infraestructura.<br></div>', '', 2, 213, 'Paradela Bridge', 'Since the Roman period, Doade was chosen as the best way to cross the river both, over bridges or by boats. The present-day bridge belongs to 1954 and it was built supported by another one which dates back to the beginnings of 20th century. The old one was raised in order not to be affected by the increase of the river because of the new construction of the Santo Estevo reservoir. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(78, 'media/EmbarcaderodeDoade3.jpg', '', 'La presa', 'La construcción del embalse de Santo Estevo, con sus 115 metros de altura en su presa, comenzó en 1945, prolongándose durante más de una década (fue inaugurada por Franco el 23 de Septiembre de 1956). Concebida en su momento como la mayor central hidroeléctrica de Europa, la obra no estuvo exenta de penalidades para los que en ella participaron, debido sobre todo a las dificultades del entorno.', 'Imagen', '', '', 3, 213, 'The Dam', 'The building of the Santo Estevo Reservoir (115 metres height) started in 1945 and it lasted more than a decade, opened by F.Franco in September 1956. Although it was thought to be the biggest hydroelectric station in Europe, it was full of risk for the workers because of the hardness of the surroundings. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(79, 'media/EmbarcaderodeDoade4.jpg', '', 'El embalse', 'La capacidad del embalse es de 213 Hectómetros cúbicos, suponiendo por volumen en la actualidad el cuarto de Galicia. La fotografía corresponde al vaciado en 2010 con motivo de unas obras de ampliación, que dejó al descubierto pueblos, viñas y puentes inundados y olvidados desde hace años.', 'Imagen', '', '', 4, 213, 'The Reservoir', 'The reservoir can contain 213 cubic hectometres, which is the fourth biggest one in Galicia.  The picture is about the emptying of the reservoir in 2010 because of the extension work. When it was emptied, there were discovered some villages, vineyards and bridges which had been forgotten for ages. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(80, 'media/MiradordeDoade1.jpg', '', 'Cómo llegar', 'A nuestra derecha si bajamos desde Doade y a nuestra izquierda si subimos desde el embarcadero, encontraremos junto a un viejo castaño un cartel turístico que anuncia: Spatium Interpretationis Rivoyra Sacrata. Nos desviaremos por el camino junto al cartel y dejaremos allí mismo el coche. Seguiremos la dirección que nos marca un pequeño cartel verde. Nos separan unos 100 metros del mirador.', 'Imagen', '', '', 1, 214, 'How to get there', 'On the right coming from Doade, on the left coming from the reservoir, there is a sign next to an old chestnut which says: Spatium Interpretationis Rivoyra Sacrata. We should take the way next to the sign post and park the car there. We have to follow the green sign in the way. There are 100 metres to the viewpoint.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(81, 'media/MiradordeDoade2.jpg', '', 'O Carreteiro', 'La escultura ensalza la figura de los carreteiros, personas que cargaban la uva en tiempo de vendimia por las escarpadas laderas de la Ribeira Sacra, realizando un esfuerzo casi sobrehumano. La obra fue realizada por el artista pontevedrés Manuel Rial y fue colocada en el año 2006.', 'Imagen', '', '', 2, 214, 'O Carreteiro', 'This sculpture praises the role of the carriers (“carreteiros”), people who carried the grapes in the steep slopes of Ribeira Sacra with so much effort when the wine harvest started. The sculpture was done by the artist Manuel Rial from Pontevedra and it was placed here in 2006. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(82, 'media/MiradordeDoade3.jpg', '', 'D.O Ribeira Sacra', 'La Denominación de Origen Ribeira Sacra, constituida en el año 1996, se encuentra dividida en cinco subzonas: Chantada, Quiroga-Bibei, Ribeiras do Miño, Ribeiras do Sil y Amandi, en cuyo territorio nos encontramos. Esta organización reúne unas 1.200 hectáreas de viñedo, 3.000 viticultores y más de 100 bodegas, algunas de las cuales cuentan con visitas guiadas para turistas. El mapa ha sido obtenido de la web de la Ruta del Vino de la Ribeira Sacra.', 'Imagen', 'Nos tenemos que remontar a la época romana, para encontrar el origen del cultivo de la vid en estas laderas. Los vinos que aquí se elaboraban, que en algunas crónicas es referido como el oro líquido del Sil, fueron muy apreciados en todo el imperio, llegando a formar parte de festejos de papas y emperadores.<br>En la edad media, el impulso de los monasterios y el esfuerzo de los habitantes de la comarca, consiguieron potenciar el cultivo de la vid, aumentando las tierras de labor y mejorando las técnicas de cultivo. En esta época la viticultura se convirtió en una de las bases de la economía, a pesar de los obstáculos que suponían las grandes pendientes y el aislamiento de la Ribeira Sacra.&nbsp;<div><br>Durante el s.XX, después de un periodo difícil por el abandono de tierras y la afección de plagas como la filoxera, la viticultura volvió a resurgir. Un hecho esencial para este renacimiento fue la creación de la D.O Ribeira Sacra, que trabajó en la rehabilitación de antiguos viñedos, la profesionalización el sector y dedicó numerosos esfuerzos para su promoción.</div><div><br>Los vinos se elaboran principalmente con variedades autóctonas. Destaca principalmente la variedad Mencía, que produce tintos de intenso color y gusto afrutado, aunque también podemos encontrar otras variedades como Brancellao, Merenzao, Sousón o Caíño. En cuanto a los blancos, con un producción bastante menor, los tipos más extendidos son Godello, Albariño, Treixadura y Dona Blanca.</div><div><br>Desde 1982 se celebra en Sober una importante feria del vino. Lo que empezó como una pequeña degustación organizada por los vecinos, se convirtió con los años en un evento de de gran entidad, reuniendo cada Domingo de Ramos a miles de personas.&nbsp;<br></div>', '', 3, 214, 'D.O Ribeira Sacra', 'The Designation of Origin Ribeira Sacra, founded in 1996, is divided into five sub-areas: Chantada, Quiroga-Bibei, Ribeiras do Miño, Ribeiras do Sil and Amandi, where we are at the moment. This organization has approximately 1.200 hectares of vineyard, 3.000 vine growers and more than 100 wineries, some of which can be visited by tourists. The map has been obtained from the site of the Wine Rout in the Ribeira Sacra. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(83, 'media/ReginaViarum1.jpg', '', 'Regina Viarum', 'Realiza visitas para particulares y grupos todo el año (para grupos mejor reservar con antelación) . La visita es gratuíta. Horarios y contacto en +info. (Foto propiedad de Bodega Regina Viarum).', 'Imagen', '<span style="font-weight: bold;">Del 1 de Junio al 31 de Octubre:</span> Todos los días (10h-14h y 16h-20h)<br><br><span style="font-weight: bold;">Del 1 de Noviembre al 30 de Marzo:</span> De Jueves a Domingo (10h-14h y 16h-19h)<br><br><span style="font-weight: bold;">Del 1 de Abril al 31 de Mayo:</span> De Miércoles a Domingo (10h-14h y 16h-20h)<br><br><span style="font-weight: bold;">Contacto:</span><span style="font-weight: normal;"> 619 009 777<br></span>', '', 1, 215, 'Regina Viarum', 'There are group and individual visits the whole year (For groups it is advisable to make a previous reservation).  It is free. Timetables and contact in +info. (Photo: property of  Regina Viarum Wine cellar). ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(84, 'media/CastelodeDoade1.jpg', '', 'Cómo llegar', 'En Doade encontraremos un desvío que nos indica O Castelo (un poco antes del pueblo si venimos de Monforte y después si subimos del Sil). Lo seguimos, y unos metros más adelante vemos una nueva indicación, a la derecha, que nos guía por un camino de tierra en buen estado. Continuamos y, un poco más adelante, llegamos un aparcamiento reconocible por la cubierta de madera con mesas.', 'Imagen', '', '', 1, 216, 'How to get there', 'There is a detour in Doade that says O Castelo (just before the village coming from Monforte or just after it if we come from the Sil). We take it and some meters after we can find a new signpost on the right which will lead us through a natural path and good for walking. We follow it and few meters away we can find a car park which we can recognise because of the wooden cover with tables.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(85, 'media/CastelodeDoade2.jpg', '', 'O Souto de Valguaire', 'Antes de llegar al aparcamiento se encuentra este bosque de castaños, algunos de ellos con varios siglos de vida. Se encuentra un poco deteriorado, ya que su espacio ha sido colonizado en algunos tramos por especies como el pino, pero conserva algunos ejemplares de porte imponente.', 'Imagen', '', '', 2, 216, 'O Souto de Valguaire (Chestnut forest)', 'Before arriving to the car park, there is a chestnut forest. Some of its trees are hundreds years old. It is a bit damaged because its land has been colonised by pine trees, however there are some marvellous samples of hundred-year-old chestnut trees. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(86, 'media/CastelodeDoade3.jpg', '', 'Aparcamiento', 'Desde el aparcamiento continuamos por el camino principal. Se trata de un agradable paseo, sin dificultad, con una longitud aproximada de 500 metros. Se trata de una de las mejores vistas de la Ribeira Sacra y que sin duda merece la pena visitar. No es necesario llevar la autoguía hasta el mirador.', 'Imagen', '', '', 3, 216, 'Car Park', 'From the car park we continue through the main way. It is an agreeable easy walk which is 500 meters long. It is one of the most exciting views of Ribeira Sacra and it is well worth visiting. We do not have to take the auto-guide to the viewpoint.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(87, 'media/CastelodeDoade4.jpg', '', 'Capilla de San Amaro', 'Según cuenta la tradición, existió desde muy antiguo una ermita en este lugar, desaparecida a mediados del s.XIX por causas desconocidas. En 2004 un grupo de vecinos de Doade decidió llevar a cabo su reconstrucción, alojando en su interior una imagen de piedra dedicada al santo. ', 'Imagen', '', '', 4, 216, 'San Amaro Chapel', 'According to the legend, an ancient chapel existed in this place since a remote time and it disappeared in the 19th century and the disappearance causes are unknown.  A group of residents decided to rebuild it in 2004 and they placed a statue dedicated to the saint inside the new chapel. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(88, 'media/AdegaAlgueira1.jpg', '', 'Adega Algueira', 'Realiza visitas a la bodega. Contacto: 982 410 299 / 629 208 917. Foto propiedad de Adega Algueira.', 'Imagen', '<span style="font-weight: bold;">Horarios:&nbsp;</span><div><div style="font-weight: normal;"><br></div><div><div style="font-weight: normal;">11:30 a 14:30 y de 16:30 a 19:00<div><br><span style="font-weight: bold;">Contacto:</span><div style="font-weight: normal;">&nbsp;</div><div><div style="font-weight: normal;"><br></div><div><div style="font-weight: normal;">698 171 168 (recomendable llamar antes)</div><div><br><span style="font-weight: bold;">Restaurante</span></div><div style="font-weight: normal;"><br>La bodega cuenta con restaurante. Las visitas a la bodega para sus clientes son gratuitas. Ofrece también paseos en barco por el Sil.&nbsp;<br></div></div></div></div></div></div></div>', '', 1, 217, 'Adega Algueira', 'To visit the wine cellar, contact to: 982 410 299 / 629 208 917. Photo: Property of Adega Algueira.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(89, 'media/SanXiaodeLobios1.jpg', '', 'Origen', 'En el lugar existió en origen una pequeña abadía femenina, perteneciente a la Órden Benedictina, y de la que únicamente se conserva su iglesia, construída en el s.XIII. El primer documento en el que se nombra este pequeño monasterio, data del año 1.185.', 'Imagen', 'La última abadesa fue Doña Constanza Vázquez de Somoza (1487-1509), que durante la reforma de los Reyes Católicos, en la que se quiso anexionar Lobios como posesión del monasterio de San Paio de Antealtares (Santiago de Compostela), fue acusada de numerosos delitos, no necesariamente ciertos, para conseguir su encierro y poder arrebatarle el lugar.&nbsp;<div><br>El fiscal Fernando de Estrada presentó el 4 de Diciembre de 1498 una querella contra la abadesa Doña Constanza, acusándola de no guardar las reglas de la Órden Benedictina, y solicitando su cese inmediato. Algunos de los cargos presentados hablaban de ser amante pública de clérigos y otros miembros de la comunidad religiosa, de tener hijos conocidos, de consentir que las monjas del monasterio viviesen deshonestamente, de no vivir en comunidad, de no reparar el monasterio y de malversar los fondos en casos prohibidos por la ley.</div><div><br>Doña Constanza, ante semejantes acusaciones, fue recluida en Santiago de Compostela,  mientras el proceso de anexión a San Paio de Antealtares continuaba, sin que ella en su encierro pudiese realizar ninguna acción para evitarlo. Pero con la ayuda de algunos amigos poderosos, la abadesa logra escapar de Compostela, recurriendo ante Roma y firmando un pacto con el Obispo de Mondoñedo, en el que este se comprometía a defender las posesiones de Lobios, a cambio de las rentas derivadas del monasterio.</div><div><br>Ante estos hechos, San Paio de Antealtares tuvo que negociar para que retirasen su reclamación ante el Papa, viéndose obligado a pagarles una pensión vitalicia a la abadesa y a sus monjas, aunque trasladándolas al cercano Monasterio de Chouzán. Finalmente en 1516, fallecida ya Doña Constanza, San Paio toma posesión del Monasterio de Lobios.<br></div>', '', 1, 311, 'Origins', 'There existed a female abbey that belonged to the Benedictine Order, from which is preserved only the church and it was built in the 13th century. The first document with a reference to the monastery dates back to 1.185. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(90, 'media/SanXiaodeLobios2.jpg', '', 'La iglesia', 'Como hemos dicho, San Xiao de Lobios (San Julian), fue construída en el siglo XIII, aunque cuenta con elementos añadidos más modernos como la sacrístía, del s.XVIII, o la espadaña (campanario), seguramente del XIX. Es muy probable que la Señora Concha, responsable de las llaves, nos salga al paso y se ofrezca a enseñarnos el interior de la iglesia (por la voluntad).', 'Imagen', '', '', 2, 311, 'Church', 'As mentioned previously, San Xiao de Lobios (Saint John), was built in the 13th century although it has modern elements like the sacristy, which belongs to the 18th century, or the “espadaña”  (bell tower), supposedly to the 19th century. Concha, the woman in charge of the keys, may receive and offer us to visit the inside of the Church (we can pay a symbolic amount of money if we want).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(91, 'media/SanXiaodeLobios3.jpg', '', 'La entrada', 'Podemos ver aquí varios capiteles historiados. Un personaje con bastón que parece venciendo a la serpiente, símbolo del mal. Varias aves picoteando frutos, símbolo de las almas aceptando la palabra sagrada y esperando , o dos centauros, mitad hombre mitad bestia, normalmente símbolo de brutalidad, tentación, lujuria y  vicio. Muchas veces la intepretación de los centauros varía si su arco apunta para disparar a algún elemento maligno.', 'Imagen', '', '', 3, 311, 'The entrance', 'There can be seen some decorated capitals. There is a person holding cane, who seems to be beating a snake, symbol of the evil. There are several birds pecking at some fruits which are considered a symbol of the souls accepting the sacred word.  There are also two centaurs, half man half beast, which normally represent luxury and vices. Sometimes the interpretation of the symbolism of centaurs can change; it depends on the use of their bows, if the bow points to a devilish element or not. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(92, 'media/SanXiaodeLobios4.jpg', '', 'Las pinturas', 'Durante unas obras de restauración, se descubrieron debajo de la cal de las paredes restos de pinturas de los siglos XV y XVI. A la izquierda, frente a la entrada, una escena con un gran grupo de almas arremolinadas alrededor de San Pedro en el Juicio Final. A la derecha, se distingue una cabeza de caballo de grandes dimensiones, con tipología románica y excepcional factura.', 'Imagen', '', '', 4, 311, 'Paintings', 'While the restoration, there were discovered several remains of 15th and 16th century paintings under the layer of the limestone wall. On the left, opposite the entrance, there is an image with a group of souls together around Saint Peter in the Final Judgement. On the right there is an enormous Romanesque horse head.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(93, 'media/SanXiaodeLobios5.jpg', '', 'El altar', 'En los capiteles junto al altar podemos ver la representación de los cuatro evangelistas (tetramorfos). Destaca una divertida imagen del toro (representa a San Lucas), además del hombre (San Mateo), el águila (San Juan) y el león (San Marcos). El retablo mayor es rococó, de 1792.', 'Imagen', '', '', 5, 311, 'The altarpiece', 'In the capitals next to the altarpieces there is a scene of the four evangelists (tetramorphs). It is highlighted a humorous image of a bull representing St. Luke, the man (St. Matthew), the eagle (St. John) and the lion that represents St. Mark. The main altarpiece is rococo and belongs to 1792. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(94, 'media/SanXiaodeLobios6.jpg', '', 'La sacristía', 'Vemos claramente que la sacristía es posterior al resto de la iglesia, ya que en su interior se conservan una ventana con capiteles y una serie de canecillos que en origen daban directamente al exterior. Podemos destacar en la ventana lo que parece una fiera devorando a un animal, y en uno de los capiteles, un gato enseñando amenazante su dentadura, vigilándonos y advirtiéndonos del riego del pecado. El confesionario, con una tétrica calavera, es del siglo XVIII.', 'Imagen', '', '', 6, 311, 'The Sacristy', 'It can be appreciated that the sacristy is later than the rest of the church, so inside we can see a window with some capitals and a series of “canecillos” to the exterior. In the window we can see something that seems to be a beast devouring an animal, and in one of the capitals there is a cat bearing its threatening teeth, as if it were warning the visitor about the risks of sin. The confessional belongs to the 18th century and it has a skull inside.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(95, 'media/SanXiaodeLobios7.jpg', '', 'La Capilla', 'Los orígenes de la capilla de la Reina de los Ángeles, podrían estar en el siglo X, aunque en la actualidad casi no conserva restos de su primera época. Fue recientemente restaurada por los vecinos y en el interior conserva una imagen de la Virgen, sacada en procesión cada 15 de agosto sobre una carroza, tirada por los propios habitantes de lobios.', 'Imagen', '', '', 7, 311, 'The Chapel', 'Although there aren’t almost any remains of the original Reina de Los Angeles Chapel, we can set its origins in the 10th century.  It has recently been re-built by the locals, there is a beautiful statue of the Virgin inside, which the residents use in processions on a carriage drawn by the Lobios inhabitants every 15th May.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(96, 'media/SanXiaodeLobios8.jpg', '', 'La leyenda', 'Una mujer de Lobios le había robado una cadena de oro a una “Moura” (habitantes mitológicos de los montes en toda Galicia), que al instante se transformó en serpiente y comenzó a perseguirla. La mujer al pasar por delante de la capilla gritó “que la Reina de los Ángeles me valga”. Al momento la serpiente contestó “a buen santo pediste encomienda”, desapareciendo a continuación.', 'Imagen', '', '', 8, 311, 'The legend', 'A woman of Lobios stole a gold chain to a “Moura” (mythological inhabitants of Galician mountains), which became a snake and began to follow her. The woman when she passes by the chapel cried out: “Help me Reina de Los Angeles” and the snake answered: “You have asked for help to the right person” and it disappeared immediately.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(97, 'media/SanXiaodeLobios9.jpg', '', 'El Cruceiro', 'En las proximidades de la capilla podemos encontrar un interesante cruceiro del S.XVIII. Destaca el Cristo, muy rústico, representado en la cruz, así como otras figuras en la columna como la serpiente que la rodea. En la parte posterior, representación de la Dolorosa.', 'Imagen', '', '', 9, 311, 'The Cross', 'Next to the chapel we can find a fascinating cross dating to the 18th century. It has a rustic image of Christ sacrifice, as well as other figures like the snake around the column. At the back, there is an image of the Sorrowful Virgin.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(98, 'media/SanXiaodeLobios10.jpg', '', 'El Souto', 'Junto a la capilla encontramos un camino que sube, y que da acceso a un bonito souto (bosque de castaños), compuesto por impresionantes árboles centenarios, y salpicado por construcciones tradicionales. Seguiremos en dirección a la bodega, hasta el primer desvío, donde continuaremos por la derecha. Nos separan unos 300 metros. Merece la pena darse un paseo para admirar estas auténticas esculturas naturales, y que todavía producen sus castañas cada otoño.', 'Imagen', '', '', 10, 311, 'Souto (chestnut forest)', 'Next to the chapel there is a path which goes up to the chestnut trees forest, and it is made up of dramatic hundred-year-old trees and it is peppered with traditional buildings. In order to reach the wine cellar, we have to continue walking until the first detour on the right. In fact there are only 300 meters. It is well worth visiting the natural scenes and it is necessary to mention that the chestnut trees even produce thousands of chestnuts every autumn.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(99, 'media/MiradoresCividadeBoqueirinho0.jpg', '', 'Cómo llegar', 'Llegaremos hasta Bolmente, en donde nos encontraremos con un cruce en el que están señalizados ambos miradores. En primer lugar tomaremos rumbo al Mirador de Cividade, hacia la derecha.', 'Imagen', '', '', 1, 313, 'How to get there', 'Arriving in Belmonte, we will find a crossroads where both viewpoints are signposted. First, we will visit the Cividade Viewpoint, which is on the right. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(100, 'media/MiradoresCividadeBoqueirinho1.jpg', '', 'Cómo llegar', 'Seguiremos por el mismo camino, sin desviarnos, durante unos  5  kilómetros. El acceso deja de estar asfaltado en el tramo final, pero se encuentra en buen estado y se puede llegar en coche sin problema. Al llegar a una curva en herradura, veremos un cartel señalando hacia la derecha e indicándonos como llegar al mirador (nos faltan unos 100 metros). ', 'Imagen', '', '', 2, 313, 'How to get Cividade Viewpoint', 'we have to continue on the same way 5 kilometres longer. The access is unpaved but it is in good conditions and we can get there by car without any problem. Coming to a hairpin bend, there is a signpost pointing to the right and telling us how to get the viewpoint. (It is 100 meters longer)', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(101, 'media/MiradoresCividadeBoqueirinho4.jpg', '', 'Cómo llegar a Boqueiriño', 'Volvemos a la curva de herradura donde nos desviamos antes (a unos 100 metros). Aquí podemos ver un cartel que nos indica el mirador de Boqueiriño hacia la derecha. Se trata de un camino de tierra, con una longitud de unos 2,5 kilómetros, pero sin dificultad para acceder con el coche.', 'Imagen', '', '', 4, 313, 'How to get to Boqueiriño', 'We have to come back to the hairpin bend where we had detoured before (100 metres away). Then we see the signpost indicating Boqueiriño viewpoint on the right. It is an unpaved path that is 2’5 kilometres long, but we can go by car. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(102, 'media/MiradoresCividadeBoqueirinho5.jpg', '', 'Mirador de Boqueiriño', 'Enfrente, al otro lado del Sil, tenemos el concello de Parada de Sil. Destaca la vista sobre el monasterio de Santa Cristina de Ribas de Sil, magnífico exponente del románico gallego, localizado en el interior de un magnífico bosque de robles y castaños. Este punto se encuentra incluído en las rutas 1 y 2.', 'Imagen', '', '', 5, 313, 'Boqueiriño Viewpoint', 'Opposite the river, there is the village Parada de Sil. It is important to mention the Santa Cristina monastery, which is one of the prime examples of Romanesque in Galicia. It is located in a wonderful oak and chestnut forest. This monument is included in route 1 and 2. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(103, 'media/PetroglifosdeNogueira1.jpg', '', 'Cómo llegar', 'Pasamos Anllo y nos dirigimos a la localidad de Nogueira. Desde el cruce de la foto veremos ya carteles que nos indican la presencia de los petroglifos. Después de ver estas inscripciones, tenemos también la posibilidad de bajar hasta A Barca. Este pueblo está ubicado en un bonito entorno, conformado por la desembocadura del Cabe en el Sil. Allí encontramos una pequeña capilla y una pasarela peatonal que nos permitirá cruzar el Cabe.', 'Imagen', '', '', 1, 314, 'How to get there', 'Overtaking Anllo, we have to go to Nogueira’s village. From the photo crossroads we will see signposts indicating the petroglyphs. After seeing these paintings we can go to A Barca. This village is located in a striking place, made up of the mouth of Cabe in the River Sil.  There is a small chapel and a pedestrian walkway which allow us to cross the river Cabe. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(104, 'media/PetroglifosdeNogueira2.jpg', '', 'Los petroglifos', 'También denominados como A Tapada do Monte, se trata de un conjunto de grabados prehistóricos sobre roca granítica. Representan diversos motivos, típicos del arte rupestre gallego de la Edad de Bronce, como figuras circulares, surcos o cazoletas. También podemos ver algunas cruces sobre la roca, quizás intento medieval de cristianizar estos símbolos paganos ancestrales.', 'Imagen', '', '', 2, 314, 'The petroglyphs', 'Also called A Tapada do Monte, this prehistoric etching ensemble is on granite stone. They represent typical Galician cave art motifs belonging to the Bronze Age.   There are some circle figures, furrows and vessels.. We can also see several crosses on the stone, it may have been an attempt to Christianise this ancestral pagan symbols.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(105, 'media/PetroglifosdeNogueira3.jpg', '', 'La leyenda', 'La tradición local nos cuenta que los dibujos de círculos concéntricos que podemos ver repetidos en numerosas ocasiones en los petroglifos de Nogueira, se corresponderían con las huellas del famoso caballo de Santiago, y que las dejó en el lugar durante una batalla contra los enemigos infieles.', 'Imagen', '', '', 3, 314, 'The Legend', 'The locals tell that the concentric circle drawings that appear constantly in the petroglyphs might be the tracks of the famous Santiago’s horse, it is said that the tracks were done during a battle against the disloyal enemies.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(106, 'media/MuinhosdoXabrega1.jpg', '', 'La ruta', 'Agradable paseo de aproximadamente un kilómetro, en el que podemos ver restaurados o semirestaurados algunos de los 28 molinos que movía el río Xábrega en su último tramo antes de desembocar en el Sil.', 'Imagen', '', '', 1, 315, 'The route', 'Nice one kilometre walk where we can see some mills that were moved by the Xabrega River in the last stretch. It was before its discharging into the Sil River. Some of them have been restored and others are the semi-restored mills.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(107, 'media/MuinhosdoXabrega2.jpg', '', 'Acceso', 'El camino comienza junto a un cartel de madera, en la misma carretera, que nos señala Muiños do Xábrega. Bajaremos por el camino, girando bruscamente hacia la derecha después de 50 metros y encontrando ya los primeros molinos, además de una moderna construcción, en mal estado de conservación, que pretendía ser un espacio interpretativo. La ruta continúa a las puertas del primero de los molinos, subiendo unas pequeñas escaleras de piedra.', 'Imagen', '', '', 2, 315, 'Access', 'The way starts next to a wooden signpost, in the road, that points to Muiños do Xabrega. We have to go down the path and turn sharply on the right after 50 meters, there can be seen the first mills as well as a modern construction, supposedly an interpretation centre, however it is poorly preserved. The route continues since the entrance of the first mill, we have to go up the stairs. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(108, 'media/MuinhosdoXabrega3.jpg', '', 'La vegetación', 'Si nos fijamos un poco veremos que todo el espacio se encuentra salpicado por una serie de carteles, que nos informan sobre la vegetación y otros aspectos de la naturaleza de este espacio.', 'Imagen', '', '', 3, 315, 'The vegetation', 'The entire site has a lot of signpost telling us about the vegetation and other aspects of the natural environment of the area. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(109, 'media/MuinhosdoXabrega4.jpg', '', 'Los molinos', 'Muy extendidos por Galicia, es dificil encontrar un curso de agua, por muy pequeño que sea, sin alguna construcción de este tipo. Eran utilizados para moler utilizando la energía del agua. Para ello contaban con una sencilla maquinaria que giraba al golpear la corriente contra ella, haciendo girar al mismo tiempo una piedra de moler, situada sobre otra piedra fija. Entre ambas piedras se colocaba el grano, que con el roce de las dos piedras se machacaba, obteniéndose la harina.', 'Imagen', '', '', 4, 315, 'The mills', 'This type of mill spreads over Galicia. There is one similar construction next to almost every watercourse, no matter how big or small it is.  They grind using the water energy, and for this purpose, there was huge machinery that moved when the water hit it and at the same time it made a millstone spin, which was placed on other fixed stone. The grain was placed between the two stones and it was mashed with the stone friction, getting thus the flour. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(110, 'media/MuinhosdoXabrega5.jpg', '', 'Los canales', 'Para conseguir que el agua tuviese la fuerza suficiente para mover la maquinaria y la piedra de moler, era llevada hasta la construcción, desde río arriba, por un canal (gavada, gavia o loada). Al llegar al molino, a mayor altura que este, era soltada con fuerza a modo de cascada por un agujero en el edificio (billote o bofetón), consiguiéndose así una mayor energía.', 'Imagen', '', '', 5, 315, 'The canals', 'The water was taken to the construction with the aim of getting enough strength so that the water could move the machinery and the grain could be mashed. In order to carry the water up the river, some canals were designed. When the water reached the mill, it was dropped by a hole in the mill abruptly, as if it were a waterfall, so that it created much more energy.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(111, 'media/MuinhosdoXabrega6.jpg', '', 'El azud', 'Para lograr que una parte del agua del río corra por el canal, conduciéndola hasta el molino, se construían aguas arriba unas pequeñas presas (guiares o ceñas), normalmente hechas con piedras. Se abrían y cerraban con unas  pequeñas compuertas. ', 'Imagen', '', '', 6, 315, 'The mill dam', 'These are some small dams built of stone. They were used to lead some water of the river into the canal so as to reach the mill. They were opened and closed with the help of a small lock-gate. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(112, 'media/MuinhosdoXabrega7.jpg', '', 'Final', 'La ruta continúa, alternando una y otra orilla del río, hasta la construcción de la fotografía. Desde aquí podremos subir a la carretera para volver hasta el coche  (que está aparcado a unos 5 minutos), viendo desde arriba los molinos que hemos ido visitando en nuestro recorrido. También podemos optar por volver por el mismo camino por el que hemos llegado hasta aquí.', 'Imagen', '', '', 7, 315, 'End', 'The route goes on by both river banks until the construction we can see in the photography. From this place, we can go the road up so as to get to the car (five minutes away). While walking to the car, we can enjoy the views of the mills from the top. We can also go back to the car by the same path we came from. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(113, 'media/EmbarcaderodosChancis1.jpg', '', 'Aparcamiento', 'Debido a las dificultades que existen para aparcar en el embarcadero, deberemos de dejar el coche en una zona habilitada y señalizada,  que se encuentra a unos 300 metros del pantalán.', 'Imagen', '', '', 1, 316, 'Car park', 'As it is difficult to park in the quay, we should park in a special area that is about 300 metres away.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(114, 'media/EmbarcaderodosChancis2.jpg', '', 'Ruta de los Viñedos y Mirador', 'Justo antes de llegar al aparcamiento, encontraremos un cartel que nos señaliza el inicio de la Ruta de los Viñedos (PR-G86), y que nos lleva hasta el pueblo de Rosende, a unos 14 km de distancia. Si vamos caminando desde el aparcamiento y seguimos esta ruta unos 150 metros, llegaremos hasta un pequeño mirador sobre el Sil.', 'Imagen', '', '', 2, 316, 'Viewpoints and wine cellars Route', 'Before reaching the car park, there is a signpost of Ruta de los Viñedos (PR-G86), that takes us to the Rosende village, which is about 14 km away. Walking 150 metres way from the car park, we will get a small viewpoint over the Sil River. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(115, 'media/EmbarcaderodosChancis3.jpg', '', 'Embarcadero de Os Chancís', 'Además de disfrutar del hermoso lugar en el que se encuentra el embarcadero, tendremos la posibilidad aquí de realizar multitud de actividades relacionadas con el río y con la naturaleza. El lugar cuenta con un agradable bar, abierto todo el verano, con una terraza desde la que podremos relajarnos con las mejores vistas de la Ribeira Sacra. Son famosas sus paellas (por encargo). Foto: www.turismoenlaribeirasacra.com', 'Imagen', '', '', 3, 316, 'Os Chancis quay', 'Apart from the enjoyment of the place, we can also do a lot of leisure activities connected with nature. There is a nice café that opens during the summer where we can relax, and from its terrace we can appreciate the best views of Ribeira sacra. In the restaurant we can eat the well-known paella (previous reservation). Photo: www.turismoenlaribeirasacra.com', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(116, 'media/EmbarcaderodosChancis4.jpg', '', 'Actividades', 'Desde Semana Santa y hasta finalizar la temporada de verano, la empresa Turismo en la Ribeira Sacra realiza aquí un buen número actividades de ocio, aptas para todos los públicos. Información sobre tipos de actividades y contacto en +info.', 'Imagen', '<span style="font-weight: bold;">Teléfono:</span> 982 460 714/627 777 540<div style="font-weight: normal;"><br><span style="font-weight: bold;">Correo electrónico:</span> info@turismoenlaribeirasacra.com<br></div><div style="font-weight: normal;"><br></div><div><span style="font-weight: bold;">Paseos en barco:</span> Viaje de aproximadamente hora y media de duración por el espacio catalogado como Cañón del Sil. La ruta se realiza en una moderna embarcación a motor con capacidad para 12 personas, con todas las comodidades, en la que se nos ofrecerá una degustación de vino de la zona y tendremos la oportunidad, si nos apetece, de darnos un baño en alguno de los rincones del río. El precio de esta actividad es de 15 euros por persona, es necesario reservar por teléfono.</div><div><br><span style="font-weight: bold;">Alquiler de Kayaks:</span><span style="font-weight: normal;"> Por 15 euros para dos personas (la primera hora, el resto 10 euros).&nbsp;</span></div><div><br><span style="font-weight: bold;">Otras actividades:</span><span style="font-weight: normal;"> Esqui Acuático, Tiro con Arco, Enoturismo, Visitas por el Románico, Senderismo, Aulas de Naturaleza, Pesca y Enduro.&nbsp;<br></span></div>', '', 4, 316, 'Activities', 'From Easter to the end of summer, the company Turismo en la Ribeira Sacra offers a lot of leisure activities, adapted for everybody. There is more information in +info. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(117, 'media/MonforteCiudadMedieval0.jpg', '', 'Aparcamiento y oficina de turismo', 'Existe un aparcamiento gratuito junto a los Escolapios. Nuestro primer destino es la Oficina de Turismo, que encontraremos a unos 500 metros, siguiendo la calle Cardenal Rodrigo de Castro (el segundo tramo es una calle peatonal) hasta la Plaza de España (que reconoceremos por las escaleras que suben hacia el castillo). Giramos aquí a la izquierda por la estrecha Rúa do Comercio. Información sobre horarios de la oficina en +info.', 'Imagen', '<div style="font-weight: normal;">El horario de la Oficina de Turismo es:<div><br><span style="font-weight: bold;">Verano:</span> De 11:00 a 15:00 y de 17:00 a 21:00 h. Los domingos solo por la mañana y los lunes cerrado.</div><div><br><span style="font-weight: bold;">Invierno:</span> De 10:00 a 14:00 y de 16:00 a 20:00 h. Los domingos solo por la mañana y los lunes y martes cerrado.&nbsp;<br></div></div>', '', 0, 400, 'Car park and tourism office', 'There is a free car park next to the Escolapios. The first destination is the Tourism Office that is 500 meters away in Cardenal Rodrigo de Castro Street (the second section is a pedestrian street) until arriving at the España Square (we go through it by the stairs that take us to the castle).  We have to turn on the right, taking the Comercio Street. There is information about timetables in the office +info. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(118, 'media/MonforteCiudadMedieval2.jpg', '', 'Plaza de España', 'Antes de llegar a la oficina de turismo podemos hacer una parada en la Plaza de España, antiguo centro de la judería y del burgo medieval. Si nos decidimos a subir las escaleras, encontraremos la vieja cárcel del s.XIX. Aquí comienzan, hacia la izquierda, las estrechas calles de Zapaterías y Pescaderías, que nos llevan hasta una de las puertas que todavía se conservan de la antigua muralla (Porta de Pescaderías).', 'Imagen', 'En las calles y plazas de Monforte de Lemos, vivieron en la Edad Media un buen número de familias judías, cuyo legado se conserva en forma de documentos, leyendas y elementos arquitectónicos. La primera noticia que se tiene de la presencia judía en Monforte, será un documento datado en el año 915, y en el que se nombra a un tal Ismael, judío y habitante de la ciudad.&nbsp;<div><br>Al contrario de lo que pasaba en el resto de la península, la nobleza local no vió una amenaza en esta comunidad, algo determinante para que se incrementase su población con familias procedentes de otros lugares, de donde eran expulsados. Por contra, los distintos Señores de Lemos colocaron en puestos de confianza y cargos importantes, sobre todo relacionados con tesorería y hacienda, a distintos personajes de la comunidad judía.</div><div>&nbsp; <br>El entorno de la Plaza de España, de la cárcel vieja y la calle de la Zapatería, serán los lugares dónde se concentraba una mayor población judía, y donde todavía hoy podemos encontrar vestigios de su presencia. Se trataba de una población en su mayor parte rica, dedicada sobre todo al comercio de la seda, los paños y la plata, así como al ejercicio del derecho y la medicina. Sin embargo, en 1492 los Reyes Católicos ordenaron la expulsión de todas aquellas personas de esta comunidad que no se convirtiesen al cristianismo, huyendo muchos de ellos hacia el vecino Portugal, y siendo perseguidas por la inquisición la mayoría de las familias que decidieron quedarse.&nbsp;<br></div>', '', 2, 400, 'España Square', 'Before we arrive at the tourism office, we can stop and see the España Square, the town centre in the medieval time and also a Jewish main square. If we dare to go up the stairs we can find an old prison that belongs to the 19th century. On the left we can find the narrow streets Zapaterías y Pescaderías that take us to one of the remaining doors of the old wall (Porta de Pescaderías).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(119, 'media/MonforteCiudadMedieval3.jpg', '', 'Centro do Viño da Ribeira Sacra', 'Junto a la Oficina de Turismo encontraremos el Centro do Viño de la Ribeira Sacra. Se trata de un edificio construido en 1.583, y que cumplió funciones de hospital, casino y oficina municipal. Desde 2009 es centro de exposiciones, tienda y tapería. Su museo nos adentra en la cultura del vino a través de fotografías, esculturas, vídeos, etc.  Información sobre horarios y precios pulsando en +info.', 'Imagen', '<span style="font-weight: normal;">El </span><span style="font-weight: bold;">horario </span>del Centro del Vino es:<div><br>De 10:00 a 14:00 y de 17:00 a 20:00 h. Domingos de 9:00 a 16:00 h, y los lunes cerrado. Visitas guiadas todos los días a las 12:00 y a las 18:00 (excepto domingos, que se realiza unicamente a las 12:00 y los lunes que el centro permanece cerrado).</div><div><br>El <span style="font-weight: bold;">precio </span>para la exposición es de 3 euros por persona (Mayores de 65 y menores de 18 años, 2 euros).&nbsp;<br></div>', '', 3, 400, 'Ribeira Sacra Wine Centre', 'Next to the tourism office there is the Ribeira Sacra Wine Center, a building that belongs to 1.583 and it was used as hospital, casino and Municipal Office. It has become an exhibition centre, with shop and tapas bar since 2009. The museum takes us into the wine culture with a lot of pictures, sculptures and videos.  There is more information about the timetable and prices clicking in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(120, 'media/MonforteCiudadMedieval4.jpg', '', 'Ponte Vella', 'Atravesaremos el río Cabe por su Ponte Vella. Según la tradición local es de origen romano, pero los primeros testimonios escritos lo datan en la Edad Media, época en la que era vital para el abastecimiento de la ciudad. Además, por él circulaba una variante del Camino de Santiago. No se sabe si por una riada o por resultar demasiado pequeño para el volumen de tránsito que soportaba, el puente fue reconstruído en el s.XVI, momento del que procede su aspecto actual.', 'Imagen', '', '', 4, 400, 'Ponte Vella', 'We are going to cross the Cabe River through the Ponte Vella (“Ancient Bridge”). According to the locals, it has Roman origins although there are some documents that connect the bridge with the Middle Ages because in that period it was necessary for the village supply. In addition, there was a detour of the Camino de Santiago (the route of Santiago). The reason why the bridge was rebuilt is uncertain. It is said that it was because of a flood or because it was small, taking the daily move into account. It was rebuilt in the 16th century and it is preserved until the present days.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(121, 'media/MonforteCiudadMedieval5.jpg', '', 'Museo de Arte Sacro de Las Clarisas', 'El Monasterio de las Clarisas, del s.XVII y con una arquitectura sencilla y austera, alberga una comunidad de monjas de clausura de la Orden de las Franciscanas Descalzas. En su interior se encuentra una de las colecciones de arte sacro más importantes de España, gran parte donada por los VIII Condes de Lemos, Virreyes de Nápoles, y compuesta por piezas españolas e italianas. Información sobre horarios y precios en +info.', 'Imagen', 'Turnos de visita de Lunes a Sábado a las 11h, 12h, 16h, 17h y 18h. Los domingos y festivos no se lleva a cabo la de las 11h. Las visitas son guiadas y el precio por persona es de 3,5 euros.', '', 5, 400, 'Religious art  Clarisas Musem', 'The Clarisas Monastery (17th century), whose design is plain and austere, is the home to a community of enclosed nuns who belong to the Franciscan sisters. The monastery has one of the most important religious art collections in Spain, most of which was donated by the VIII Lemos Counts, viceroy of Naples. It is made up of Spanish and Italian works. Information about the timetable and prices in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(122, 'media/MonforteCiudadMedieval6.jpg', '', 'El paseo del Cabe', 'Volveremos al puente para tomar el paseo del río Cabe, que nos  llevará de nuevo al aparcamiento a través del Parque dos Condes. Junto al coche encontraremos el último punto de nuestra visita en el casco urbano. Se trata de Nosa Señora da Antiga (Colegio de los Escolapios), conocido popularmente como El Escorial de Galicia.', 'Imagen', '', '', 6, 400, 'Cabe Walk', 'We should go back to the bridge in order to take the Cabe River way which will lead us to the car park through the Parque dos Condes. There is the last landmark to see in our visit inside the village. It is the Nosa Señora da Antiga (Escolapios School), known as El Escorial of Galicia.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(123, 'media/MonforteCiudadMedieval7.jpg', '', 'Nosa Señora da Antiga', 'Fundado por el Cardenal Rodrigo de Castro, Arzobispo de Sevilla y gran benefactor de Monforte,  su construcción se prolongó entre 1591 y 1622. Es uno de los pocos ejemplos que hay en Galicia de edificio de estilo Herreriano (por eso es conocido como el Escorial de Galicia), y en la actualidad cumple funciones de Colegio Escolapio. Alberga una valiosa colección de cuadros, entre los que destacan algunas obras de El Greco. Información sobre horarios y precios en +info.', 'Imagen', 'Visitas guiadas al claustro, la iglesia y el museo, de aproximadamente 1 hora de duración, de martes a sábado a las 12:00 y a las 16:30 h. Los domingos unicamente a las 12:00 y los lunes permanecerá cerrado. El precio de la visita de es de 3 euros por persona.&nbsp;', '', 7, 400, 'Nosa Señora da Antiga', 'It was founded by the Cardinal Rodrigo de Castro, Sevilla Archbishop, who was an important benefactor of Monforte de Lemos. The construction was done between 1591 and 1622. It is one of the few examples of buildings in Herrera style, because of that it is known as the Escorial of Galicia. Today it is a school of Escolapios. There is an important picture collection, containing some works by El Greco. Information about the timetables and prices can be found in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(124, 'media/FortalezadeMonforte1.jpg', '', 'El Monte de San Vicente', 'Los orígenes de Monforte se encuentran en el Monte de San Vicente, elevada colina que destaca sobre el resto de la villa. Aquí se edificó en época medieval la antigua ciudad-fortaleza, construida alrededor del Monasterio y del Castillo, y rodeada por una muralla, de la que hoy se conservan amplios tramos y algunas de sus puertas. ', 'Imagen', '', '', 1, 401, 'San Vicente’s Hill', 'Monforte origins start in San Vicente’s Hill, which is a high hill that stands out the rest of the village. It was erected here a medieval fortress city that surrounded the monastery and castle. It was enclosed by a wall, whose doors and some pieces of its pieces are preserved today. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(125, 'media/FortalezadeMonforte2.jpg', '', 'El Palacio Condal', 'Residencia señorial de los Condes de Lemos desde el s.XVI, tuvo que ser reconstruido como consecuencia de un devastador incendio. La de Lemos fue una de las familias más importantes de la nobleza española, sobre todo D. Pedro Fernández de Castro, que llegó a ser Virrey de Nápoles (1610) y mecenas de escritores como Cervantes, Góngora, Lope de Vega y Quevedo. Actualmente alberga algunas de las dependencias del Parador de Turismo.', 'Imagen', '', '', 2, 401, 'County Palace', 'It was the Lemos Counts mansion since the 16th century. There was a fire and it had to be rebuilt. The Lemos family was one of the most important families of the Spanish aristocracy, one of its members, Sir Pedro Fernandez de Castro was proclaimed viceroy of Naples in 1610 as well as patron of a great number of writers such as Cervantes, Gongora, Lope de Vega and Quevedo. Nowadays it has become the National Parador.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(126, 'media/FortalezadeMonforte3.jpg', '', 'La iglesia', 'De estilo gótico con fachada renacentista, alberga en su interior algunas piezas artísticas de interés. En el altar mayor (barroco) podemos ver una pintura del martirio de San Vicente y una composición escultórica de Santa Ana, con la Virgen y el niño. El viejo órgano, un dintel románico empotrado en el coro y un antiguo sepulcro, serán otros elementos a destacar en este templo.', 'Imagen', 'En el imaginario popular de Monforte de Lemos, se dice que existe un pasadizo subterráneo que comunica el Palacio Condal con la iglesia de San Vicente de Pino. En la existencia de este corredor se basa una de las leyendas más recitadas en estas tierras, la de la Corona de Fuego.&nbsp;<div><br>Cuentan que el Conde de Lemos tuvo que marchar a tierras lejanas para cumplir mandatos reales, dejando a su hija al cuidado del abad del monasterio de San Vicente do Pino. Era tanta la belleza de la joven noble, que acabó por enloquecer de amor al religioso, por lo que ideó un plan para alcanzarla. El monje aprovechó la existencia del túnel para colarse en el palacio y, con la ayuda de una doncella, llegar hasta los aposentos para satisfacer sus deseos. Pero una vez cumplido su objetivo, decide matar a la mujer para asegurarse que su secreto quedase bien guardado.&nbsp;</div><div><br>La doncella que ayudó al abad a llegar hasta los aposentos, asustada y arrepentida, se lo cuenta todo al Señor a su llegada, y este, enfurecido, se dispone enseguida a ejecutar su venganza. Invitó al asesino a su casa, ofreciéndole una magnífica comida. Al llegar a los postres, ordenó a uno de sus sirvientes que trajese una corona de hierro, la cual tenía preparada al fuego y se encontraba al rojo vivo. Acto seguido obligó al abad a colocársela en la cabeza, provocándole, ante los ojos de los demás invitados, una muerte terrible.<br></div>', '', 3, 401, 'The Church', 'it is a gothic church although its front belongs to the renaissance. There are some valuable art works in it.  In the main baroque altarpiece there is a painting about San Vicente martyrdom and a sculpture depicting Santa Ana, Madonna and the Child.  There are other main elements as for instance the antique organ, the lintel set into the choir and an old sepulchre. +info', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(127, 'media/FortalezadeMonforte4.jpg', '', 'Monasterio Benedictino de San Vicente do Pino', 'En el s.XIX, un devastador incendio destruyó gran parte del Palacio Condal y de las dependencias del monasterio. En este suceso desapareció la mayor parte de la documentación sobre el origen de la abadía, a pesar de lo cual sabemos que existía ya en el s.X. No será hasta el s.XVI cuando se construya el edificio actual, que en la actualidad alberga el Parador Nacional de Turismo.', 'Imagen', '', '', 4, 401, 'San Vicente do Pino Benedictine Monastery', 'There was a fire in the 19th century that destroyed most of the County Palace and the monastery chambers. Most of the documents about the abbey also disappeared with the fire. Even though, it is known that the abbey existed in the 10th century, and it is in the 11th century when the current structure is built, now is the National Parador', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(128, 'media/FortalezadeMonforte5.jpg', '', 'El Claustro', 'Alrededor de este claustro neoclásico, de cantería simple y bien labrada, se distribuyen la mayor parte de las habitaciones del Parador. En su parte central podemos ver una especie de templete circular, réplica de una de las torres del monasterio, y que corona y sirve como acceso al pozo (algunos historiadores plantean la posibilidad de que fuese la entrada del pasadizo que unía con el Palacio Condal).', 'Imagen', '', '', 5, 401, 'The Cloister', 'Most of the rooms in the Parador are settled around this neoclassic cloister made with plain but crafted masonry. There is a circular shrine, which is a monastery tower replica. It gives access to the well. Some historians claim that it could be the gate to the passage that joined the monastery with the palace.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(129, 'media/FortalezadeMonforte6.jpg', '', 'La Torre del Homenaje', 'Construida entre los siglos XIII y XV, y de estructura marcadamente defensiva, tiene 30 metros de altura y muros de 3 metros de grosor. Se trataba del lugar más seguro del castillo, en el que se refugiaban el Señor y su familia en caso de asedio. En su parte superior podremos disfrutar de buenas vistas de la comarca de Lemos.', 'Imagen', 'La historia del Castillo de Monforte va unida a la familia Castro, que lo habitó durante siglos, desde que en 1332 Alfonso XI lo cediera a Pedro Fernández de Castro. El hecho de que fuese lugar de residencia de la nobleza local, hizo que el edificio se conservase en muy buen estado, pero la revuelta de los Irmandiños contra los abusos de los señores, y el incendio de 1672, provocaron grandes destrozos en sus estructuras.<div>&nbsp; <br>La parte que mejor se conserva es la Torre del Homenaje, llamada así porque en ella se celebraban las ceremonias de investidura, en las que los señores del lugar entregaban tierras a los vasallos, bajo contrato feudal y juramento de obediencia, fidelidad y servicio. En lo más alto de la torre estaban las habitaciones, debajo los salones y en los pisos inferiores el almacén para las provisiones y un espacio para los soldados. En la actualidad alberga un museo en el que se exponen muebles y otros enseres de la época.<br></div>', '', 6, 401, 'Homage Tower', 'It was built between the 13th and 15th centuries and it has a self-protective structure because it is 30 metres high and the walls are 3 metres thick. It was considered the safest place in the castle and in the case of a siege, the Lord and his family were sheltered in. From the top we can enjoy great views of the Lemos region. +info', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(130, 'media/AlfareriadeGundivos1.jpg', '', 'La Alfarería', 'La zona de Gundivós es conocida popularmente como “Terra de Cacharreiros”. El lugar fue centro importante de industria alfarera hasta la primera mitad del s.XX, pero en los 60 sufrió un importante declive, que casi termina con su desaparición. Consiguió mantenerse viva gracias al esfuerzo de los pocos artesanos que todavía conocen el oficio, y que trabajan intensamente para su recuperación.', 'Imagen', '', '', 1, 402, 'Pottery', 'Gundivos surroundings are known as “Terra de Cacharreiros” (“land of potters”). There was an important pottery industry until the first half of the 20th century. In the 60’s it began to go down, to the extent that it has almost disappeared. This manual activity survived thanks to the few potters that struggled to keep with the practice.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(131, 'media/AlfareriadeGundivos2.jpg', '', 'La Rectoral de Gundivós', 'En la antigua casa rectoral de Gundivós, construcción del s.XVI hoy rehabilitada, se encuentra uno de los talleres en los que se trabaja la alfarería de Gundivós. Este espacio cuenta además con museo, tienda y espacio para realizar demostraciones del oficio (normalmente para grupos y con cita previa). Foto: Chela', 'Imagen', '', '', 2, 402, 'Gundivos Rectory', 'There is one of the pottery workshops in the old rectory in Guindivos. This workshop has a museum, a shop and a room for exhibitions (groups with previous booking).  Photo: Chela', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(132, 'media/AlfareriadeGundivos3.jpg', '', 'La cerámica', 'Las piezas son hechas en torno bajo, movido a mano, con el barro local y cocidas en horno de leña. Las formas tradicionales son sobrias, únicamente con alguna decoración sencilla. En las piezas destinadas a guardar el vino, se recubre el interior con resina de pino, calentando después la pieza en una hoguera  para fundirlo. Esto le da un tono negro característico. Se dice que además de conservar el vino fresco, le daba mejor sabor. Foto: Nuria', 'Imagen', '', '', 3, 402, 'Ceramic', 'The pieces are made with a low potter’s wheel moved by hand, they are made with local clay and they are fired in a kiln. The clay pieces are simple with few decorative elements. The vessels used for keeping the wine are covered with a pine gum and later they are heated in a bonfire so as to smelt them. Thanks to this process the vessels get their famous dark colour. It is said that apart from keeping the wine fresh, it also enhances the wine’s favour. Photo: Nuria', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(133, 'media/AlfareriadeGundivos1.mp3', '', 'El oficio', 'En sus mejores tiempos, llegaron a trabajar al mismo tiempo unos 30 oleiros en Gundivós, pero en la década de los 50 del s.XX, desplazados por el plástico y el cristal, los aperos domésticos de arcilla de Gundivós perdieron demanda, por lo que los oleiros tuvieron que emigrar, quedando únicamente uno en la aldea. A finales de los 70 comenzaron a retornar aquellos antiguos oleiros que habían emigrado, sentándose de nuevo delante del torno. Recuperaron la tradición tal cual la habían dejado, sin caer en la tentación de adulterarla con tornos altos, eléctricos, manteniendo el sistema primigenio de rueda baja, casi a ras de suelo, que había que mantener girando con impulsos de la mano. También continuaron trabajando en un silla de patas recortadas y con los codos clavados en las ingles, el mejor punto de apoyo para mantener el pulso sin cansarse. \r\n\r\nProsiguieron también utilizando arcilla autóctona, cociendo en horno de leña, ahumando las piezas para ennegrecerlas, impermeabilizando los jarros con resina de pino y derritiéndola con fuego de carqueixa antes de verterla dentro de los cacharros. Ni siquiera innovaron en los adornos, únicamente bincos y bigotes, sin más historias. \r\n\r\nEn los últimos años, algunos de estos artesanos fallecieron y otros se retiraron del oficio. Pero un joven del lugar, Elías González, decidió proseguir la tradición, aprendiendo el oficio para preservarlo. Hoy podemos encontrar su taller en la Casa Rectoral de Gundivós, en la que además cuenta con sala de exposición, museo con piezas históricas y elementos etnográficos de la zona, aula para seminarios y zona de demostraciones, donde reciben grupos de visitantes para conocer el proceso de elaboración de la alfarería más ancestral de Galicia, y quizás también de toda la península. ', 'Texto', '', '', 4, 402, 'The job', 'There were almost 30 potters working together in Gundivos in the best of times but in the 50’s the importance of the job was falling due to the appearance of other material such as the plastic or glass. All the potters emigrated but one, who kept working. At the end of the 70’s, all those potters who had emigrated, came back to the village and restarted the work. They kept the traditional way of working, without changing their low wheel for a high one, or without using an electric wheel. They continued working with a low potter’s wheel, which they had to keep spinning with their hands. They also kept working to the floor level in a short and small chair, supported in their elbows not to lose the equilibrium. These workers continued using local clay as well as smoking the pieces so as to give them their black colour, and they also made waterproof vessels with pine gum, as in the past time. They not even changed the ornaments. \r\nMost of these potters have died or retired. Elias González, a young man decided to continue the practice.  Currently, his pottery workshop can be found in the Rectory in Guindivos, where there is also a room for exhibitions, museum with some historical pieces, ethnographic local element and a classroom so as to celebrate lectures and demonstrations for the visitors. In these special lessons we can learn about the ancient pottery of Galicia, and maybe in Spain.  \r\n', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(134, 'media/Pombeiro1.jpg', '', 'El nombre', 'El conjunto toma su nombre del lugar donde antiguos documentos localizan, a orillas del Sil, el Portus Palumbaron (Puerto de las Palomas). La paloma estaba muy vinculada a los navegantes, ya que de ella admiraban su capacidad de orientación. Además, Pombeiro se encuentra frente a Pena Pombiera, lugar en el que según la tradición quedaron grabadas las huellas de la Virgen.', 'Imagen', '', '', 1, 500, 'Name', 'This village was named after the place where it was located the Portus Palumbaron (Dove’s port), in the Sil bank. Doves were related to sailors since they praised the dove’s ability to get orientated. In addition, Pombeiro is located opposite Pena Pombeira, where the Virgin’s footprints are thought to be noticeable.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(135, 'media/Pombeiro2.jpg', '', 'El origen', 'En el año 935, los Condes Don Suario y Doña Guntroda donaron al abad Sabarico tierras para financiar la construcción de la iglesia, la ropa y la comida de los religiosos, así como las obras de caridad. Unos años más tarde, Doña Goto, mujer de Sancho I, cedió también, al abad Asterigo, los terrenos para la casa rectoral. En 1109 el monasterio fue cedido a la Órden de Cluny, a la que que seguiría inscrito hasta 1526, que fue anexionado a Santo Estevo de Ribas de Sil.', 'Imagen', '', '', 2, 500, 'Origins', 'In 935, the Counts Don Suario y Doña Guntroda conferred lands to the abbot in order to build the church, and money for the clergy’s clothes, the food as well as money for the charity.  Some years later, Doña Goto, the wife of Sancho I, gave the abbot the lands of the rectory.  The monastery was assigned to the Cluny Order in 1109, and it was registered there until 1526, when it was annexed to Santo Estevo de Ribas de Sil.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(136, 'media/Pombeiro3.jpg', '', 'La Casa Rectoral', 'El edificio monasterial propiamente dicho se encuentra ya desaparecido, y en su lugar se construyó lo que hoy es la Casa Rectoral. El edificio fue construido en el s.XVIII, originalmente como casa-palacio. En la desamortización del s.XIX, cuando la iglesia pasó a ser parroquial, se convirtió en Casa Rectoral al servicio del párroco local. En su fachada vemos el escudo de Santo Estevo de Ribas de Sil, con sus 9 obispos.', 'Imagen', '', '', 3, 500, 'The Rectory', 'The original building was destroyed and today there is the Rectory. The original structure was built in 18th century as a mansion (house-palace). In 19th century, when the church became parish and when everything was under the confiscation, it turned into a Rectory for the local priest. There is the Santo Estevo de Ribas de Sil shield with 9 bishops in the front. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(137, 'media/Pombeiro4.jpg', '', 'La iglesia', 'La iglesia, románica de principios del S.XIII, tiene influencia compostelana, posiblemente debido a su construcción por artistas del taller del maestro Mateo. Se trata de un templo esbelto y de gran sobriedad en su decoración, siguiendo los dictados de  San Bernardo, que recomendaba austeridad en las construcciones benedictinas.', 'Imagen', 'En el capítulo 29 de su apología, San Bernardo criticaba las representaciones de personas y animales en las esculturas de los templos, y  recomendaba los elementos escultóricos fitomórficos (representado plantas): “ En los capiteles de los claustros, donde nuestros hermanos hacen su lectura, ¿qué razón de ser tienen tantos monstruos ridículos, tanta belleza deforme y tanta deformidad artística?. Esos macacos inmundos, esos fieros leones, esos horribles centauros, esas  representaciones y carátulas con cuerpo de animal y caras de hombre, esos tigres con pintas, esos soldados peleando, esos cazadores con cuernos. Podrás encontrar muchos cuerpos humanos colgados en una sola cabeza, o un solo cuerpo para varias cabezas. Aquí un cuadrúpedo con rabo de serpiente, allí un pez con cabeza de cuadrúpedo, o una fiera con delantera de caballo o el cuerpo trasero de cabra montaraz. Por todas partes aparece tan grande y prodigiosa variedad de los más diversos caprichos, que los monjes más les gusta leer en los mármoles que en los códices, pasando todo el día admirando tanto detalle sin meditar en la ley de Dios”.&nbsp;', '', 4, 500, 'The Church', 'The Romanesque church, built in the 18th century, has Compostela influence, perhaps because it was built by artists belonging to the master Mateo’s school. It is an elegant and sober temple. As St. Bernard claimed, there was a need for austerity in relation to the Benedictine buildings. +info', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(138, 'media/Pombeiro5.jpg', '', 'El zorro', 'La primera puerta que encontraremos es la puerta lateral sur. En ella destaca un zorro con la boca semiabierta, enseñándonos los dientes. Este animal es símbolo de alevosía, engaño, avaricia, lujuria y mentira, siendo habitualmente relacionado con las artes del demonio para seducir a las personas de bien y llevarlas a cometer pecado. ', 'Imagen', '', '', 5, 500, 'The Fox', 'The first gate is the southern lateral door. It is noticeable the fox showing its teeth, as if it were a warning. This animal is a symbol of treachery, trick, avarice, lust and falsehood, so it was related to the devil and its attempts to seduce people so as to fall into sin. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(139, 'media/Pombeiro6.jpg', '', 'Cabeza', 'Opuesta al zorro se encuentra una cabeza en actitud reflexiva y humilde. Frente a la amenaza del mal, para prevenir el castigo por los pecados, nos muestra el camino de la oración y la obediencia como la forma en que los fieles obtendrían la salvación. Destaca el tamaño de las orejas, grandes para recordar a los fieles que deben entrar en la iglesia y escuchar con atención la palabra de Dios.', 'Imagen', '', '', 6, 500, 'The Head', 'There is a thoughtful and humble head opposite the fox. Contrary to the devilish fox, it shows us the way to salvation through obedience and prayers, and prevents us from sin. It should be paid attention to the size of its ears, they are extremely big to remember the faithful they should enter the church and listen to the word of God carefully.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(140, 'media/Pombeiro7.jpg', '', 'Portada principal', 'Destaca la sobriedad con la que está construida, y únicamente encontraremos algunas esculturas de la vegetación de la zona. Llaman la atención los discos solares que hay sobre el arco, representando la inmortalidad y la supremacía divina. La espiral hacia la derecha simboliza el sol de invierno, que se encoje (se acortan los días), y a la izquierda, el sol de primavera. Estos símbolos eran utilizados ya por los primitivos pueblos celtas en referencia a la vida eterna.', 'Imagen', '', '', 7, 500, 'Main gate', 'It is very sober; there are only some sculptures of the local vegetation. Two solar disks over the arch are particularly eye-catching. They depict the immortality and the divine supremacy. The spiral on the right is the winter sun, which shrinks and so the days are shorter and on the left, there is the spring sun. These symbols were used by the Celts, and they were connected with the eternal life. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(141, 'media/Pombeiro8.jpg', '', 'Torre', 'Llama la atención la original torre de sección circular, muy poco repetida en el románico rural gallego, rematada con un campanario, ya cuadrado. El interior está recorrido por una estrecha escalera de caracol.', 'Imagen', '', '', 8, 500, 'Tower', 'Although its bell tower is squared, the peculiar circular tower is very interesting because it is not very common in the rural Galician Romanesque.   There is a narrow spiral staircase. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(142, 'media/Pombeiro9.jpg', '', 'Ajedrezado', 'Tanto en Pombeiro como en otras muchas de las construcciones románicas, podemos ver un tipo de decoración que se denomina ajedrezado. Tiene el significado de alternancia entre lo positivo y lo negativo, y el esfuerzo humano para vencer los obstáculos, racionales y sobre todo irracionales (tentaciones, etc), con la fuerza de su mente.', 'Imagen', '', '', 9, 500, 'Chequered', 'In Pombeiro, as well as in other Romanesque structures of other locations, there can be found a kind of ornamentation called chequered. This ornament symbolises the alternation of negative and positive things and the human effort to overcome the rational and irrational difficulties.  ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(143, 'media/Pombeiro10.jpg', '', 'Aves', 'En una de las ventanas del ábside vemos dos aves con el cuello entrelazado, picando un fruto. El cuello entrelazado representa el amor, mientras que el fruto que comen es la palabra de Dios.', 'Imagen', '', '', 10, 500, 'Birds', 'In one of the window’s apse, there are two birds with their necks interweaved and pecking at the fruit. The joined necks depict love and the fruit depict the word of God. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(144, 'media/Pombeiro11.jpg', '', 'Pinturas', 'Con las restauraciones de 1996 aparecieron pinturas en las paredes del interior de la iglesia. Las más antiguas datan de 1460 y son de estilo Hispano-Flamenco, aunque con una composición y factura un tanto primitiva. Desde un cristal localizado en la puerta norte, podemos ver en una columna la imagen de la Virgen con el niño en brazos, y un Cristo crucificado.', 'Imagen', '', '', 11, 500, 'Paintings', 'There appeared some paintings on the wall when the church was being restored. The ancient paintings date back to 1460. Although their composition has primitive structures, they are in a Spanish-Flemish style.  Through a window glass in the northern door we can see the figure of the Virgin Mary with the Child and also a Crucified Christ. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(145, 'media/Pombeiro12.jpg', '', 'Ruta de San Cosmede', 'En el pueblo de Cibrisqueiros, unos 50 metros carretera arriba desde el desvío a Pombeiro, comienza la ruta de San Cosmede. Son aproximadamente 3 km (solo de ida), atravesando por paisajes de gran belleza. En el punto intermedio de este paseo, encontramos el lugar de O Preguntoiro, con inscripciones medievales y tumbas con forma humana talladas en las rocas, relacionadas con la antigua ermita de San Xoan Degolado, hoy desaparecida.', 'Imagen', 'Se trata del camino tradicional entre Pombeiro, capital parroquial, y la adea de San Cosmede. Lo primero que encontraremos serán unos sequeiros, antiguas construcciones de piedra utilizadas para hacer fuego y secar las castañas, mejorando su conservación. Unos metros más adelante llegamos a O Preguntoiro, lugar rodeado de misterio y leyendas, en el que hubo en otra época una ermita, San Juan Degolado, hoy desaparecida. En este lugar, mirador natural sobre el Sil, se encuentran las antiguas tumbas con forma humana talladas en la roca, así como inscripciones en la piedra: “Ora frater aster alfape”, hace referencia a Asterigo, segundo abad de Pombeiro. Llegando ya a San Cosmede veremos la pequeña capilla y una antigua bodega excavada en la roca y con diferentes símbolos gravados, como cruces (para algunos estas inscripciones se utilizarían para intentar proteger las cosechas, para otros el edificio podría ser una vieja iglesia). Por último, en San Cosmede encontraremos un bello conjunto de casas, compuesto por varios ejemplos de arquitectura tradicional. Como elemento etnográfico singular destacan las pozas talladas en las rocas, que estaban llenas de agua y eran utilizadas desde época medieval para conservar viva la pesca. Desde el s.XIII se conoce la existencia de normas que regulaban la pesca en este tramo del río. Una parte de las capturas, que eran sobre todo lampreas, anguilas y truchas, debía ser entregada a los frailes de Santo Estevo, que eran los dueños de las zonas de pesca. En estas piscinas se guardaba el pescado hasta el momento de ser entregado a los monjes.', '', 12, 500, 'San Cosmede Route', 'In Cibrisqueiros, a village about 50 metres away up to the detour to Pombeiro, stars the San Cosmede Route. There about 3 km (one way only) through beautiful landscapes. We will find O Preguntorio in the middle of this route. There are medieval writings and anthropomorphic tombs in the rocks, these engravings are related to the San Xoan Degolado shrine, which does not exist today.+info\r\n', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(146, 'media/SantoEstevodeAtan1.jpg', '', 'Historia', 'Como es habitual en la mayor parte de las iglesias románicas de la Ribeira Sacra, Santo Estevo de Atán formó parte de un antiguo monasterio, hoy desaparecido. La tradición dice que su fundador fue el Obispo Odoario, personaje que en el siglo VIII se encargó de repoblar la zona tras la conquista musulmana. Existen serias dudas sobre la certeza de este hecho.', 'Imagen', 'Un documento del año 747, considerado en un principio como el testamento del Obispo Odoario de Lugo, pone el Monasterio de Atán bajo jurisdicción de Santa María de Lugo. En este manuscrito se puede leer lo siguiente: “Lo fundé con mi familia edificándolo desde los cimientos. Lo doté de ingresos procedentes de otras iglesias que por mi y mi familia fueron tomadas y debidamente organizadas”. Sin embargo, existen serias dudas sobre la autenticidad de este documento, ya que era habitual en la Edad Media falsificar herencias para la obtención de derechos de propiedad.', '', 1, 501, 'History', 'Santo Estevo de Atan was part of the old monastery which has already disappeared; it is common to find only parts of the Romanesque structures in the Ribeira Sacra. The locals said that its founder was the Bishop Odoario, who supposedly ordered to repopulate the region after the Muslims Conquer. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(147, 'media/SantoEstevodeAtan2.jpg', '', 'La iglesia', 'Único resto que se mantiene del antiguo monasterio, fue construido en el siglo XII y declarado Monumento Nacional en 1975. Conserva numerosos ejemplos de arte románico, aunque el conjunto se encuentra muy modificado, dando la sensación algunas veces de que todo está cambiado de sitio y de que nada está dónde debería estar. Algunas partes de la iglesia están levantadas con piedras reutilizadas, posiblemente de la antigua abadía existente en el lugar.', 'Imagen', '', '', 2, 501, 'The Church', 'The single remain of the ancient monastery which was built in the 12th century and it was considered National Monument in 1975. Although it contains a lot of Romanesque samples, it has been modified so many times that the visitor feels that everything has been moved and taken out and that nothing is in the right place.  Some parts of the church have been built with the old stones belonging to the ancient abbey that was in this place.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(148, 'media/SantoEstevodeAtan4.jpg', '', 'Las marcas de los canteros', 'En los muros de la iglesia, sobre todo en los laterales de la portada principal, podemos ver unas inscripciones en la piedra, que se corresponden con las marcas de los canteros. Se trata de la firma de los antiguos maestros que trabajaron en la construcción de la iglesia.', 'Imagen', '', '', 4, 501, 'The masons mark', 'On the walls of the church, especially in the laterals of the main door, we can see a few inscriptions in the stone, which resemble to the marks of the masons. It is the signature of the ancient masters who also worked in the construction of the church.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(149, 'media/SantoEstevodeAtan5.jpg', '', 'Puerta', 'Según estudios arqueológicos realizados en Atán, esta puerta no corresponde originalmente al lugar en el que se encuentra. Existen dos teorías al respecto, que fue traída de otra iglesia más antigua existente en las proximidades, o que fuese cambiada de lugar en alguna de las restauraciones de la iglesia. La decoración de este elemento es sencilla, llamando la atención una pequeña calavera.', 'Imagen', '', '', 5, 501, 'Door', 'According to archaeological studies carried out in Atan, this door does belong to the place where it is. Two theories exist on this matter, one is that it belonged to another ancient church in the proximities, or that it was changed of place when some of the restorations of the church. Its decoration simple and a small skull is the most eye-catching element.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(150, 'media/SantoEstevodeAtan6.jpg', '', 'Cabeza', 'Vemos un hombre en actitud reflexiva, con los ojos cerrados y la cabeza agachada hacia el suelo (símbolo de humildad y deseo de huir de pensamientos impuros). Por el corte de su pelo, redondeado y con un entrante en la parte frontal, se dice que es un monje benedictino.', 'Imagen', '', '', 6, 501, 'Head', 'There is a man in philosophical attitude, with the eyes closed and the head bent towards the floor (symbol of humbleness and desire to avoid impure thoughts). Taking into account his haircut, rounded and with some hair in the frontal part, it is said that he is a Benedictine monk.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(151, 'media/SantoEstevodeAtan7.jpg', '', 'Lobo', 'Representación de la cabeza de un lobo, con un carnero en la boca. Simboliza la fiereza del animal, el mal e incluso a los malos pastores de la iglesia. El carnero, símbolo del bien, estaría siendo devorado por el demonio.', 'Imagen', '', '', 7, 501, 'Wolf', 'This depicts the head of a wolf, with a sheep in its mouth. It symbolizes the ferocity of the wolf, the evil and even the bad shepherds that could be found in the church. The sheep, symbol of the good, appear thus being devoured by the demon.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(152, 'media/SantoEstevodeAtan8.jpg', '', 'El hombre bebiendo', 'Giramos ya hacia el lado opuesto de la iglesia. Allí vemos una escultura muy repetida en las iglesias de la Ribeira Sacra. Para algunos nos encontramos ante la figura de un hombre bebiendo vino directamente de la cuba, no se sabe si advirtiendo del peligro de los excesos o predicando las bondades del vino. Para otros, se trataría de un músico tocando un antiguo instrumento musical, el Dolio, hoy casi desconocido.', 'Imagen', '', '', 8, 501, 'Man drinking', 'We turn towards the opposite side of the church. There is a sculpture highly repeated in the churches of Ribeira Sacra. Some people say it is the figure of a man drinking wine directly from the vat, although it is uncertain if it is warning of the danger of the excesses or preaching the benefits of wine. Others assert that it could be a musician playing an ancient musical instrument, the Dolio, today almost unknown.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(153, 'media/SantoEstevodeAtan9.jpg', '', 'Torre', 'La torre es la parte más antigua del conjunto, resto posiblemente del monasterio primitivo, aunque muy modificada en la actualidad. Sirvió como campanario hasta el s.XVIII, momento en el que fue destruida, posiblemente para aprovechar la piedra en otras construcciones. En su restauración más reciente, en 2002, se volvió a levantar y se adosaron algunas ventanas y cruces encontradas en el lugar. Se dice que estas piezas provienen de época prerrománica.', 'Imagen', '', '', 9, 501, 'Tower', 'The tower is the most ancient part of the site; they are possibly the remains of the primitive monastery, although it has been modified at present. It served as bell tower until the 18th century, when it was destroyed to make use of the stone in other constructions. In was rebuilt in 2002 and there were leaned some windows and crosses against the wall, these objects were found in the place. It is said that these pieces belong to the Pre-Romanesque time.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(154, 'media/SantoEstevodeAtan10.jpg', '', 'Restos prerrománicos', 'Podemos apreciar dispersos por los muros de la iglesia y de la torre, cuatro piezas de piedra utilizadas como ventanas y que conservan trazas de arte prerrománico. Para algunos es la evidencia de la antigüedad de Atán, mientras que otros afirman que no se puede asegurar que sean originales de este templo y que podrían proceder de otro antiguo monasterio ya desaparecido y localizado en algún paraje del entorno.', 'Imagen', '', '', 10, 501, 'Pre-Romanesque Remains', 'There can be appreciated four stones dispersed pieces on the walls of the church and the tower. They were used as windows and preserve Pre-Romanesque design. Some claim it is the evidence of the antiquity of Atan, whereas others sustain that it is not possible to make sure that they are originated in this temple and that they might come from another ancient monastery which has already disappeared and located in some other place of the surroundings.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(155, 'media/SantoEstevodeAtan11.jpg', '', 'La leyenda', 'Santo Estevo de Atán se encuentra ubicado en un paraje recóndito cerca de la ribera del Miño. Según cuenta la tradición popular de la zona, los monjes que aquí vivían atravesaban con cierta frecuencia el río para visitar a las monjas residentes de los monasterios femeninos de Chouzán y A Cova, situados justo enfrente, en la otra orilla. Se dice que la relación entre estos tres centros religiosos era bastante cercana en la época.', 'Imagen', '', '', 11, 501, 'Legend', 'Santo Estevo de Atan is located in a hidden place next to the bank of the Miño River. According to the popular tradition, the monks who were living here crossed the river so as to visit the nuns of the female monasteries of Chouzan and A Cova, which were placed just facing the male monastery, in the other river’s bank. It is said that the relationship between these three religious centres was quite good in that time.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(156, 'media/SanFizdeCangas1.jpg', '', 'El calvario', 'Lo primero que veremos al llegar al aparcamiento, será un calvario, copia de una rara joya tallada en madera en el s.XIV, y cuyo original se encuentra a buen recaudo en el Museo de la Clarisas, en Monforte de Lemos. Ante este tipo de imágenes la gente se acercaba para arrodillarse y orar, por lo que también tenían el nombre de humilladeros.', 'Imagen', '', '', 1, 502, 'The Calvary', 'There is a scene on the Calvary, copy of a rare jewel carved in wood in the 14th century, whose original version is in the Museum of the Clarisas, in Monforte de Lemos. In the presence of this type of images, people approached to them in order to kneel down and pray, from what also they received the name of “humilladeros” (shrines).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(157, 'media/SanFizdeCangas2.jpg', '', 'Historia', 'Como ocurre en casi todas las iglesias románicas de la Ribeira Sacra, en el lugar en el que se encuentra San Fiz de Cangas existió un antiguo monasterio, en este caso de monjas benedictinas. En la actualidad únicamente se conserva la iglesia románica del s.XIII, aunque con detalles escultóricos que por su rusticidad nos sugieren la presencia en el lugar de construcciones más antiguas. La primera referencia escrita de la abadía de San Fiz data del año 1108.', 'Imagen', '', '', 2, 502, 'History', 'As it happens in almost all the Romanesque churches in the Ribeira Sacra, there existed an ancient monastery in the place where San Fiz of Cangasis located, in this case of Benedictine nuns. At present only the Romanesque church of the 13th century survives, although its wild sculptural details suggest the presence of ancient constructions in this area. The first written reference of San Fiz abbey dates back to the year 1108.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(158, 'media/SanFizdeCangas3.jpg', '', 'Los Leones', 'Podemos ver dos cuadrúpedos, uno a cada lado de la entrada, que avanzan hacia la puerta. Son dos leones, animales sagrados que representan al propio Cristo, y que ejercen como vigilantes y protectores de la iglesia. En esta época representar a este animal en las entradas de las iglesias era algo habitual, ya que fue promovido directamente por los altos estamentos católicos, pensando que así harían sentir a los fieles protegidos en su interior.', 'Imagen', '', '', 3, 502, 'The lions', 'There are two quadrupeds, one at every side of the entry, which advance towards the door. They are two lions, sacred animals that symbolise Christ, and they are like vigilant and protectors of the church. The representation of this animal in the doors of the churches was something habitual, since it was promoted by the catholic classes, as they thought that they would make the faithful feel protected.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(159, 'media/SanFizdeCangas4.jpg', '', 'El tímpano', 'Justo encima de la puerta podemos ver dos símbolos de gran singularidad, posiblemente procedentes de una antigua sepultura prerrománica. Son muy sencillos y están esculpidos de forma bastante rudimentaria, pero podrían tener un significado de protección espiritual y de vida eterna. En la parte superior del tímpano hay una cruz griega custodiada por el sol y la luna.', 'Imagen', '', '', 4, 502, 'The tympanum', 'Over the door we can see two symbols that have important a  great distinctiveness, they may come from an ancient Pre-Romanesque tomb. They are very simple and they are carved very simply, but they might be a symbol of spiritual protection and of eternal life. In the top of the tympanum there is a Greek cross guarded by the sun and the moon.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(160, 'media/SanFizdeCangas5.jpg', '', 'Los macacos', 'En los capiteles de esta ventana están presentados sendos grupos de simios o macacos. En la época eran esculpidos de forma siniestra y deformada, como símbolo del mal y del hombre degradado por sus vicios y por todo aquel comportamiento contrario al que la iglesia exigía.', 'Imagen', '', '', 5, 502, 'The monkeys', 'In the capitals of this window there are groups of monkeys. They were sculpted in a sinister and distorted way, as a symbol of the evil and of the man degraded by his vices and by all that behaviour contrary to the one the church was demanding.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(161, 'media/SanFizdeCangas6.jpg', '', 'El ternero', 'Se trataría del becerro de Bel, que aparece en la Bíblia como ídolo pagano, y puesto en relación con el mismo Satán. Carece tanto de ojos como de orejas, elementos que simbolizan la luz y la sabiduría. La falta de estos elementos confirma que se trata de un símbolo negativo, una advertencia de las consecuencias de adorar a falsos ídolos. ', 'Imagen', '', '', 6, 502, 'The calf', 'It could be the Bel''s calf, which appears in the Bible as a pagan idol, and related to Satan. It does not have eyes or ears, elements that symbolize the light and the knowledge. The lack of these elements confirms that this calf is considered a negative symbol, a warning of the consequences of admiring false idols.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(162, 'media/SanFizdeCangas7.jpg', '', 'El perro', 'A este animal, que se nos presenta en San Fiz con la cabeza baja, en posición de respeto o humillación, se le atribuyó antes del cristianismo el significado de guía del viaje a ultratumba, el acompañante fiel que que introducía al espíritu de los fallecidos en el hábitat de los dioses. Sin embargo, en el cristianismo se modificó este significado, otorgándole un poder maléfico.', 'Imagen', '', '', 7, 502, 'The dog', 'This animal, which is depicted in San Fiz with the low head, in position of respect or humiliation, was assumed to be the guide in the trip after death, the faithful companion that took the spirit of the deceased to the habitat of the gods. Nevertheless, in the Christianity this assumption was modified, and the dog became a harmful being.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(163, 'media/SanFizdeCangas8.jpg', '', 'La cuba', 'Este elemento se repite en varias ocasiones en las iglesias de la Ribeira Sacra. Se trataría, según intepretaciones, de un hombre bebiendo de una cuba o tocando un instrumento de viento (el Dolio). Para algunos advertencia ante el pecado y los excesos, para otros culto al vino, tan extendido desde tiempo inmemorial en estas tierras.', 'Imagen', '', '', 8, 502, 'The Vat', 'This element is repeated itself in several occasions in the churches of Ribeira Sacra. According to some interpretations, it could be a man drinking from a vat or touching a wind instrument (the Dolio). Some said it was a warning about the sin and the vices, others asserted it was a moment of wine devotion, very extended in this area since remote times.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(164, 'media/SanFizdeCangas9.jpg', '', 'El hombre', 'En la figura continua se nos representa una imagen humana con los brazos transformados en patas de animal, lo que viene a incidir en la idea de la bestialidad del hombre. Se nos advierte en este icono contra el hombre bestial, sin alma, perdido en las tinieblas. Al pié de esta de esta figura, en uno de los capiteles de la ventana, dos animales unidos por una sola cabeza, lo que representa de nuevo el pecado.', 'Imagen', '', '', 9, 502, 'The man', 'In the adjoining figure there is a human image with the arms transformed into animal paws, which emphasises the idea of the bestiality of men. We are warned against the beastly man, without soul, lost in the darkness. At the foot of this figure, in one of the capitals of the window, there are two animals joined by a single head, which symbolises the sin once more.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(165, 'media/SanFizdeCangas10.jpg', '', 'La puerta norte', 'Contiene formas arquitectónicas muy simples, con una cruz rodeada por un círculo, posiblemente representando la la unificación entre el reino de los cielos y la tierra. Por su parte, en el lateral derecho de esta puerta, una figura de gran rusticidad y que podría evocar la fertilidad y la fecundación, fuente de vida.', 'Imagen', '', '', 10, 502, 'The northern gate', 'It has simple architectural forms, containing a cross surrounded by a circle, which might represent the union between the kingdom of heaven and the earth. Likewise, on the right side of this door, there is a rustic figure which might evoke the fertility and the fertilisation.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(166, 'media/FerreiradePanton1.jpg', '', 'Entrada', 'Entramos en el recinto del monasterio a través de una gran puerta de piedra, escoltada por dos columnas. La de la izquierda, con un acabado más trabajado, se encuentra rematada por una imagen del Salvador, titular del monasterio. En medio del conjunto, en la parte superior, el escudo de la Congregación Cisterciense de Castilla y una cruz con dos brazos (arzobispal).', 'Imagen', '', '', 1, 503, 'Entrance', 'We go into the monastery through a big door of stone, supported by two columns. The one on left, which is more elaborated, is finished off by an image of Salvador, owner of the monastery. In the top part of the middle of the set, we can see the arm of the Cistercian Congregation of Castile and a cross with two arms (archiepiscopal).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(167, 'media/FerreiradePanton2.jpg', '', 'Historia', 'Se desconoce la fecha exacta de la fundación del monasterio. Los primeros documentos escritos que se conocen datan del año 924, momento en el que funcionaba como abadía dúplice (habitada tanto por monjes como por monjas, situación que no gustaba a muchos de los estamentos católicos). En 1175 se anexiona a la Órden Cisterciense, ya como convento únicamente femenino (manteniéndose así hasta la actualidad).', 'Imagen', '', '', 2, 503, 'History', 'It is unknown the precise date of the foundation of the monastery. The first written documents that have survived date it in 924, when it was working as dual abbey (inhabited both by monks and by nuns, many of the catholic classes did not like this situation). In 1175 it is annexed to the Cistercian Order, but as female convent (up to the present).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(168, 'media/FerreiradePanton3.jpg', '', 'La torre', 'En el extremo norte de la fachada se encuentra la esbelta torre cuadrada. En ella se albergaban las dependencias para huéspedes y una pequeña vivienda para el capellán. Desde hace unos años se ha recuperado la función de hospedería.', 'Imagen', '', '', 3, 503, 'The tower', 'There is a slender square tower in the north end of the front. There can be found the hosts chambers and a small apartment for the priest. It has been using as guests quarters for a few years.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(169, 'media/FerreiradePanton4.jpg', '', 'La fachada', 'En el s.XVIII, en época barroca, se realizan la mayor parte de las ampliaciones de la abadía que se conservan en la actualidad, como la monumental fachada del monasterio. Entre las ventanas y balcones típicos de la época podemos leer la siguiente inscripción: ESTA FACHADA SE HIZO AÑO 1728. LAS ZELOSIAS SE PINTARON AÑO 1791.', 'Imagen', '', '', 4, 503, 'The front', 'In the 18th century, the Baroque Era, the abbey is enlarged and one of the enlargements was the monumental front of the monastery. Between the windows and typical balconies of the epoch we can read the following inscription: ESTA FACHADA SE HIZO AÑO 1728. LAS ZELOSIAS SE PINTARON AÑO 1791. (THIS FRONT WAS BUILT IN 1728. THE LATTICEWORK WAS PAINTED IN 1791.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(170, 'media/FerreiradePanton5.jpg', '', 'La entrada', 'En el centro de la fachada se encuentra la entrada. De construcción simple, en origen albergaba, además del acceso al monasterio y la sala de recepción, una péqueña vivienda llamada la Casa da Mandareira, donde vivía la criada o chica de los recados al servicio del convento.', 'Imagen', '', '', 5, 503, 'Entrance', 'In the middle of the front there can be seen the entry. It has a simple construction, at the beginning it had, in addition to the access to the monastery and the reception hall, there was a small apartment called the Casa da Mandareira, where the housemaid lived.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(171, 'media/FerreiradePanton6.jpg', '', 'Casa das Donas', 'La Casa de las Señoras, antiguamente lugar en el que se alojaban las mujeres nobles, es hoy la edificación destinada a las celdas de las monjas. Cada habitáculo tenía cocina individual y espacio para el servicio, estructura reconocible por la gran cantidad de chimeneas que sobresalen del tejado.', 'Imagen', '', '', 6, 503, 'Casa das Donas', 'The Casa das Donas (House of the Ladies), formerly it was a place where the noble women stayed, and today the building is used as cells of the nuns. Every room had an individual kitchen and space for the servants, which is recognizable because of the amount of chimneys that stand above of the roof.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(172, 'media/FerreiradePanton7.jpg', '', 'El ábside', 'El arte románico tiene una función principalmente didáctica, por lo que cada elemento tiene un significado. Las esculturas de las iglesias son en su conjunto pequeñas biblias en piedra, adoctrinando a las gentes que se acercaban al templo y que no sabían leer, pero si mirar y observar conocedores del mundo simbólico y mítico que representaban.', 'Imagen', '', '', 7, 503, 'The Apse', 'The Romanesque Art has mainly an educational function. As a result every element has a different meaning. The sculptures of the churches are like short Bibles carved in stone, indoctrinating people who approached the temple and who could not read, but they could look and understand the symbolic and mythical world that the images and sculptures depicted.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(173, 'media/FerreiradePanton8.jpg', '', 'Las Figuras', 'Las figuras con forma humana y animal, de gran riqueza escultórica, no encajan con el estilo austero de la Órden Cisterciense, por lo que esta parte de la iglesia será anterior al año 1175, fecha en la que la abadía pasó a depender de esta congregación. Veremos a continuación algunos ejemplos de estas esculturas y de la simbología que representan.', 'Imagen', '', '', 8, 503, 'The figures', 'The figures with human and animal shape, which were very important from a sculptural point of view, do not fit with the austere style of the Cistercian Order, so this part of the church could be prior to 1175, date in which the abbey would be dependent on this congregation. We will see some examples of these sculptures and of the symbols that they represent.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(174, 'media/FerreiradePanton10.jpg', '', 'La iglesia', 'El elemento más antiguo que se conserva es la iglesia, que constituye un bonito ejemplo del románico gallego del siglo XII. Por lo general la iglesia está abierta y se puede visitar. De no ser así, tendremos que llamar al timbre, junto al torno del convento, y nos abrirán tanto el templo, como al claustro y la tienda de dulces.', 'Imagen', '', '', 10, 503, 'The Church', 'The most ancient element that survives is the church, which is a nice example of the Galician Romanesque in the 12th century. In general the church is opened and everybody can visit it. Otherwise, we should ring the bell, next to the along with the winch of the convent, and they will let us visit the temple as well as the cloister and the sweets shop.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(175, 'media/FerreiradePanton12.jpg', '', 'Los sepulcros', 'Detrás del altar se encuentran los sepulcros de dos caballeros medievales. Se trata de Diego de Lemos, uno de los principales líderes en las Revueltas Irmandiñas (en el siglo XV, en toda Galicia, el pueblo se levantó contra los abusos de la nobleza), y su hijo primogénito, López Sánchez de Lemos. Ambos yacen con sus armaduras y diferentes escudos representativos de sus familias.', 'Imagen', '', '', 12, 503, 'The tombs', 'There are the tombs of two medieval gentlemen behind the altar. One of them belongs to Diego de Lemos, one of the leaders in the Irmandiñas Riots (people in Galicia were against the nobility in the 15th century). The other is his first-born son, Lopez Sanchez of Lemos. Both lie with his armours and different arms of their family.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(176, 'media/FerreiradePanton13.jpg', '', 'La Virgen', 'En el año 1975 se celebró el octavo centenario de la llegada de la Órden Cisterciense al monasterio, comenzándose las tareas de limpieza y restauración del monumento. Durante las obras se encontró tapiada en un muro una curiosa imagen de madera policromada, en representación de la Virgen con el niño en brazos, y con apariencia románica (para algunos expertos podría datar incluso del s.XII).', 'Imagen', '', '', 13, 503, 'The Virgin', 'In the year 1975 there was the eighth centenary of the arrival of the Cistercian Order to the monastery. At that moment, the cleaning and restoration of the monument started. While the restoration was taking place, someone found a rare polychrome wooden sculpture in a wall. It was an image of the Madonna with the Child and it seemed to be a Romanesque image (for some experts it could belong 12th century).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(177, 'media/FerreiradePanton14.jpg', '', 'El claustro', 'En época renacentista se acomete una segunda ampliación del monasterio, llevándose a cabo en el s.XVI las obras del sencillo Claustro Reglar. Se encuentra en la zona de clausura, por lo que únicamente podremos verlo a través de un cristal, localizado en las proximidades de la tienda (llamar al timbre en el torno). En el claustro se encuentra el sepulcro de Doña Fronilde, primera abadesa Cisterciense del monasterio.', 'Imagen', '', '', 14, 503, 'The Cloister', 'There was a second enlargement in the Renaissance in the 16th century, and the Cloister was rebuilt too. It is located near the enclosed zone in the monastery so we will only be able to see it through a glass which is next to the shop (ring the bell in the winch). There can be seen the tomb of Mrs Fronilde, the first abbess of the Cistercian monastery.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(178, 'media/SantoEstevoMinho1.jpg', '', 'El Anfiteatro', 'Lo primero que veremos al llegar a Santo Estevo, una vez aparcado el coche, será una especie de pequeño anfiteatro. Resulta curioso situarse en el centro de este semicírculo para hablar o silbar.', 'Imagen', '', '', 1, 506, 'The Amphitheatre', 'The first thing that we will see when we arrive to Santo Estevo will be a small amphitheatre. It is interesting to be located in the centre of this semicircle so as to speak or to whistle.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(179, 'media/SantoEstevoMinho2.jpg', '', 'La ubicación', 'La localización de Santo Estevo es magnífica, dominando el valle del Miño. Sin embargo, esta posición dificultó su construcción debido a las elevadas pendientes, sobre todo si tenemos en cuenta que los templos de la época debían guardar orientación Este-Oeste (recorrido del sol). Esto hizo que fuese necesario excavar la montaña para colocar el ábside, y construir una pequeña cripta debajo de la fachada para ganar espacio y soportar la parte frontal de la iglesia.', 'Imagen', '', '', 2, 506, 'The location', 'Santo Estevo has a great location, where the valley of the Miño is very important. Nevertheless, its location made his construction difficult due to the high slopes, especially because the temples had to be oriented to the East-West (course of the sun). Owing to this reason, it was necessary to excavate the mountain in order to place the apse, and to build a small crypt under the front to get space and to support the frontal part of the church.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(180, 'media/SantoEstevoMinho3.jpg', '', 'Historia', 'El origen y la historia del antiguo monasterio que aquí se localizaba son prácticamente desconocidos, ya que no existen documentos sobre él. Es posible que se tratase del antiguo Santo Estevo in villa Mazara, dependiente de Samos, pero no se puede afirmar con total seguridad. En la actualidad se conserva únicamente su preciosa iglesia románica, declarada Monumento Nacional ya en 1939.', 'Imagen', '', '', 3, 506, 'History', 'It is uncertain the origin of the ancient monastery that was here in the past, since documents do not exist on him. It might be the ancient Santo Estevo in villa Mazara, which was dependent on Samos, but it is not possible to make it sure. At present it is preserved only its beautiful Romanesque church, declared National Monument in 1939.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(181, 'media/SantoEstevoMinho4.jpg', '', 'La iglesia', 'La primera mención documental conservada en la que se cita Ribas de Miño, es una donación del año 976. La iglesia actual, uno de los mejores exponentes del románico rural gallego, fue construída a principios del s.XIII, con claras similitudes con la cercana Portomarín, e incluso con algunos elementos de la Catedral de Santiago de Compotela. El elemento más destacado del conjunto es su fachada, con su imponente portada y el magnífico rosetón.', 'Imagen', '', '', 4, 506, 'The Church ', 'Ribas de Miño was mentioned for the first time in 976 in a document that was a donation. The current church, one of the best exponents of the rural Galician Romanesque, was built at the beginning of the 13th century. It bears similarities with the church in Portomarin, and even with some elements of the Cathedral of Santiago of Compostela. The most prominent element is his front, containing a magnificent rose window.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(182, 'media/SantoEstevoMinho5.jpg', '', 'El ábside', 'En la parte trasera de la iglesia destaca su ábside semicircular, de gran sobriedad. En él podemos ver algunas ventanas, decoradas con ajedrezado y motivos vegetales, y el campanario, que se encuentra separado de la iglesia, sobre un muro de piedra.', 'Imagen', '', '', 5, 506, 'The apse', 'In the back of the church it is emphasized its semi-circular and sober apse. There are some windows, decorated with chequered and plants motifs. The bell tower, which is separated from the church, is located on a stone wall', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(183, 'media/SantoEstevoMinho6.jpg', '', 'Puerta sur', 'Este elemento cuenta, al igual que las ventanas que lo rodean y con las que guarda gran parecido, con una decoración mínima, nada que ver con la fachada occidental. Es muy probable que esta zona del edificio estuviese antiguamente cubierta por un pórtico de madera, algo habitual en muchas iglesias de Galicia, ya que a lo largo del muro se conservan los soportes sobre los que se dispondrían las vigas para sostener la techumbre de la constucción. ', 'Imagen', '', '', 6, 506, 'Southern Gate', 'This element has a minimal decoration, as the windows that surround it, very different from the western front. This part of the building might have been covered by a wooden entry, something very common in many churches of Galicia, since the supports survived and on those would be the beams in order to support the roof.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(184, 'media/SantoEstevoMinho7.jpg', '', 'La fachada', 'La parte más espectacular de la iglesia es su fachada, una de las más bellas y elegantes del románico gallego. En esta parte se nota claramente la influencia del taller del Maestro Mateo, por lo que posiblemente algunos de los magníficos artistas que estuvieron a sus órdenes en Santiago de Compostela trabajaron posteriormente en esta iglesia (al igual que en la Catedral de Ourense o en Portomarín).', 'Imagen', '', '', 7, 506, 'The front', 'The most spectacular part of the church is his front, one of the most beautiful and elegant of the Galician Romanesque. It is noticeable the influence of the workshop of Master Mateo, and some of the brilliant artists who worked for him in Santiago de Compostela might have worked later in this church (as in the Cathedral of Ourense or in Portomarin).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(185, 'media/SantoEstevoMinho8.jpg', '', 'Rosetón', 'Magnifico elemento de 4 metros de diámetro y con tendencia gótica.  Realizado completamente en granito, su diseño está formado a partir del círculo central, alrededor del cual se disponen doce pétalos. Se trata de uno de los rosetones de mayor tamaño de toda Galicia, y su función es la de iluminar el templo, consiguiendo un ambiente místico con la luz de colores procedente de las cristaleras, alumbrando la zona del altar. ', 'Imagen', '', '', 8, 506, 'The Rose Window', 'It is a magnificent element of 4 meters of diameter and it seems to be Gothic. It was made in granite and his design is formed from circle in the centre, about which twelve petals are arranged. It is one of the biggest rose windows in Galicia, and his function is that of illuminating the temple. It is obtained a mystical atmosphere thanks to the colour lights coming from the dresser which provide the altar with light.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(186, 'media/SantoEstevoMinho9.jpg', '', 'Ancianos', 'Representación a modo resumido del Pórtico de la Gloria de la Catedral de Santiago de Compostela, seguramente realizado por algún artista procedente del taller del Maestro Mateo. En vez de los 24 ancianos del Apocalipsis, aquí solo podemos ver siete, sentados y mirándose de dos en dos, menos el del centro. Cinco tocan instrumentos, uno sostiene un cuadrante lunar (artilugio de medición) y otro, el central, un disco solar (el sol se identificaba con Cristo).', 'Imagen', '', '', 9, 506, 'Elderly', 'It is a copy of the “Portico de la Gloria” in the Cathedral of Santiago de Compostela, which might have been made by someone who worked for the Master Mateo. Instead of the 24 elderly men of the Apocalypse, here we can see only seven. Most of them are sat and looking at each other in couples, except for the one in the centre. Five of them are playing instruments, one is holding a lunar quadrant (gadget of measurement) and other, the one in the middle, is holding a solar disc because the sun was thought to symbolise Christ.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(187, 'media/SantoEstevoMinho10.jpg', '', 'Ángel y Demonio', 'La entrada de la iglesia se encuentra flanqueada por un ángel y un demonio, ambos en actitud de señalar unos escritos que apoyan en sus rodillas, y que se corresponderían con el Libro de la Vida, en el que está apuntadas las acciones de cada persona a lo largo de su biografía. Es una clara alusión al Juicio Final, en la que contrapone el bien frente al mal.', 'Imagen', '', '', 10, 506, 'Angel and Demon', 'The entry of the church is flanked by an angel and a demon, both pointing at a few writings that they have in their knees. These documents would be related to the Book of the Life, whose function is to keep a collection of the actions of everybody along his life. It is an allusion to the Last Judgement, in which the good is faced to the evil.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(188, 'media/SantoEstevoMinho11.jpg', '', 'Capiteles', 'Motivos vegetales, evocando la naturaleza, en todos menos en el más cercano a la puerta, en el que podemos ver dos dragones alados mordiendo cada uno la cola del otro. Podría ser una variante del Ouroboros, presente desde hace 3000 años en diferentes culturas europeas, y que representa la vida después de la muerte terrenal  y la naturaleza cíclica del universo. ', 'Imagen', '', '', 11, 506, 'The Capitals', 'There are plant motifs that evoke the nature in all the capitals except in the one next to the door, in which we can see two winged dragons biting each other their tails. It might be something similar to the Ouroboros, known for 3000 years in different European cultures, which represents life after death and the cyclical nature of the universe.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(189, 'media/SantoEstevoMinho12.jpg', '', 'Capiteles', 'De nuevo motivos vegetales en todos menos en el más cercano a la puerta, en el que podemos ver dos extrañas aves con cara felina. Se trataría de arpías, que representaban las bajas pasiones y los remordimientos generados por el pecado y la satisfacción de los vicios. ', 'Imagen', '', '', 12, 506, 'The Capitals', 'We can see plant motifs again in all the capitals but in the one next to the door, in which there are two strange birds with feline face. It would be harpies that symbolise the lower passions, the remorse provoked by the sin and the satisfaction of the vices.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(190, 'media/SanPaiodeDiomondi1.jpg', '', 'Historia', 'Poco se sabe del monasterio que aquí había en origen, y unicamente algunas referencias en documentos del s.X, nos dan una idea sobre la antigüedad del conjunto. Como muchos de los conventos de la zona, se trataría de una abadía dúplice, en la que convivían hombres y mujeres, pero la llegada de la Órden Benedictina obligó a modificar esta situación. En la actualidad se conserva su iglesia, románica, y la casa rectoral, de la misma época.', 'Imagen', '', '', 1, 507, 'History', ': Little is known about the first monastery that was here, there are only some references in documents of the 10th century, which give us an idea on the antiquity of the structure. As many of the convents in the area, it is supposed to be a double abbey, in which men and women lived together, but with the arrival of the Benedictine Order this situation was forced to change. At present only the Romanesque church and the rectory are preserved.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(191, 'media/SanPaiodeDiomondi2.jpg', '', 'El nombre', 'El nombre Paio (Pelagio o Pelayo) tuvo gran tradición en la iglesia gallega del s.X por la impresión que tuvo entre los fieles el martirio en Córdoba del joven Pelayo, sobrino del Obispo de Tuy. Según la historia que por aquí se contaba, prefirió morir decapitado antes de sucumbir a las tentaciones que le proponía el Califa. Por su parte, Diomondi (y sus variantes), proceden de nombres visigodos, y nos plantean la cuestión de si pudo existir en el lugar un templo ya anterior.', 'Imagen', '', '', 2, 507, 'The Name', 'The name Paio (Pelagio or Pelayo) was very common among the  Galician clergy in the 10th century because of the impact of the young man Pelayo’s martyrdom in Cordoba, who was the nephew of the Bishop of Tuy. According to the folklore, he preferred being decapitated in spite of succumbing to the temptations that the Caliph offered to him. Otherwise, Diomondi (and his variants), comes from Visigoth names, and it raises the issue of a possible prior temple existing in the place.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(192, 'media/SanPaiodeDiomondi3.jpg', '', 'La Casa Obispal', 'En uno de los laterales de la iglesia, se encuentra la Casa Episcopal. Se trata del edificio civil más antiguo de la zona, construído en el S.XIII por el Obispado de Lugo, para convertirla en residencia de verano de los Obispos de ls diócesis. Unos siglos más adelante se convertiría en casa rectorial de uso parroquial, llegando con el tiempo a estado ruinoso e incluso derrumbándose una parte en el año 2010.', 'Imagen', '', '', 3, 507, 'The Bishop House', 'There can be seen the Episcopal House in one of the wings of the church. It is the most ancient civil building in the area, built in the 13th century by the Diocese of Lugo, so as to turn it into the summer residence of the Bishops. A few centuries later, it would become rectory, with the time passage it became damaged and even part of it was demolished in 2010.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(193, 'media/SanPaiodeDiomondi4.jpg', '', 'La iglesia', 'La construcción actual data de finales del S.XII, hecho que parece confirmar la inscripción de la puerta, en la que se puede leer la fecha de 1170 (ERA (M):CC:VIII). Se detecta en ella, por su esbeltez, el intento de construir un templo mayor, hecho que queda frustado sin que todavía hoy conozcamos los motivos. Considerada como una “hermana pequeña” de Santo Estevo de Ribas de Miño, destaca por sus entradas, en las que podemos ver esculturas con gran simbolismo.', 'Imagen', '', '', 4, 507, 'The Church', 'The current structure dates back to ends of the 12th century, what seems to be proved by the inscription of the door, where we can read the date 1170 (ERA (M):CC:VIII). There was an attempt to build a bigger temple. However it was never done and the reasons are unknown. It is considered as a “small copy” of Santo Estevo de Ribas de Miño, and it has beautiful entrances, where we can see sculptures with an important symbolism.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(194, 'media/SanPaiodeDiomondi6.jpg', '', 'Puerta Sur', 'Mucho más sencilla que la anterior, en este caso serán dos bueyes los que guarden la puerta de la iglesia. En el románico, el buey es un animal símbolo de bondad, de calma, de fuerza apacible y de trabajo, valores predicados por la iglesia, asociados directamente con la figura de Cristo. Además, destaca una representación muy marcada de las orejas, lo que simboliza el hecho de escuchar con atención la palabra de Dios en el interior del templo, durante el sermón.', 'Imagen', '', '', 6, 507, 'Southern Gate', 'It is simpler than the previous one. There are two oxen that guard the door of the church. In the Romanesque, the ox symbolizes kindness, calmness and hard work, values that were preached by the church and associated with Christ''s figure. Also, it is stressed the way in which the ears are depicted, which denotes the importance of listening carefully to the word of God during the religious ceremony.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(195, 'media/SanPaiodeDiomondi7.jpg', '', 'Senda medieval a Belesar', 'Desde Diomondi discurre una ruta que nos llevará hasta el pueblo de Belesar, a orillas del río Miño. El camino comienza a unos 100 metros de la iglesia, junto al cementerio, donde encontraremos un cartel que nos indica su inicio. Son aproximadamente 3 Km hasta Belesar, en los que podremos disfrutar de bonitas vistas del valle del Miño, paseando por una antigua vía romana entre bosques de robles y viñedos. La pendiente es acusada en algunos tramos.', 'Imagen', '', '', 7, 507, 'Medieval path to Belesar', 'We will get to the Belesar village from a path that goes along the Miño’s bank. The way starts about 100 meters away from the church, next to the cemetery, where we will find a signpost that indicates us the beginning. There are approximately 3 Km up to Belesar, where we will be able to enjoy nice sights of the Miño’s valley. We will walk along an ancient Roman path full of oak forests and vineyards. There is a steep slope in some stretches.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(196, 'media/CabodoMundo1.jpg', '', 'O Cabo do Mundo', 'Espectacular meandro que conforma el río Miño a su paso por el concello de O Saviñao. Proponemos una parada en las proximidades de la iglesia de Fión, donde casi desde la misma carretera podremos ver una de las mejores vistas que hay sobre este entorno. Existe también un mirador habilitado en las proximidades, pero la gran cantidad de vegetación limita un poco la visión. También hay buenas vistas desde las proximidades de la bodega Abadía da Cova.', 'Imagen', '', '', 1, 508, 'O Cabo do Mundo (End of the World) ', 'We can see a spectacular meander that shapes the river Miño on its passage to the O Saviñao County. We propose to make a stop in the church of Fión, where we can enjoy one the best sights that exists on this environment. There is a viewpoint in the proximities, but view is not very good because of the great deal of vegetation. We can also enjoy the views from the Abadia da Cova winery. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(197, 'media/CabodoMundo2.jpg', '', 'Como llegar', 'Al llegar al punto que vemos en la fotografía, en O Pousadoiro, aparcaremos el coche a la entrada del camino. Recorremos unos 25 metros por el sendero, y sobre una antigua bodega casi derruída pararemos para disfrutar con las maravillosas vistas del Cabo do Mundo. ', 'Imagen', '', '', 2, 508, 'How to get there', 'When we arrive to the place in the picture, O Pousadoiro, we will park the car at the entrance. There is a 25 metros walk by the path and we will stop next to an ancient winery, which is almost destroyed, so as to enjoy the awesome views of Cabo do Mundo. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(198, 'media/AbadiadaCova1.jpg', '', 'La bodega', 'Admite visitas para particulares y grupos (en este caso mejor reservar con antelación). Cuenta además con una de las mejores vistas do Cabo do Mundo. Más información sobre horarios en contacto en +info.', 'Imagen', '<p class="MsoNormal" style="text-align: justify;"><span style="font-weight: bold;">Horario:</span><br><br>De Miércoles a Domingo, de 10:00 a 14:00 h y de 16:30 a 20:00 h.<br><br><span style="font-weight: bold;">Contacto:</span><br><br>982 453 062</p>', '', 1, 509, 'The winery', 'We can visit the winery in groups or individually (it is advisable to make a previous booking). It has one of the most breath-taking views of Cabo do Mundo. Information about timetables and contact can be found in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(199, 'media/SanMartinhodaCova1.jpg', '', 'La historia', 'Exitió aquí un antiguo monasterio dúplice, habitado por hombres y mujeres. Obligados por los altos estamentos de la iglesia a sustituír esta situación, por considerarla opuesta a los principios de la iglesia, acabó convirtiéndose en abadía de Canónigos de San Agustín. Esta situación duraría hasta el año 1824, cuando reformas y desamortizaciones eliminaron aquí la actividad monacal. La mayoría de las piedras del monasterio fueron reutilizadas en las casas del lugar.', 'Imagen', '', '', 1, 510, 'History', 'An ancient monastery inhabited by men and women was erected here. This situation was forced to change by the clergy, as they considered it to be against the church principles. Finally, the monastery became abbey, the abbey Canonigos de San Agustin. This situation would last until 1824, when the monastic activity was eradicated. Most of the stones of the monastery were used to build the local houses.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(200, 'media/SanMartinhodaCova2.jpg', '', 'La iglesia', 'Único elemento que se conserva del antiguo monasterio, la iglesia fue construída a finales del s.XII, cumpliendo el patrón de las iglesias románicas del rural gallego, aunque un poco más esbelta de lo habitual. El exterior es muy austero, pero conserva en su interior interesantes pinturas románicas de la Santísima Trinidad, así como retablos de los siglos XVII y XVIII. Si preguntamos en las casas cercanas posiblemente podamos visitarla por dentro.', 'Imagen', '', '', 2, 510, 'Church', 'The church is the only remaining element of the ancient monastery, which belongs to the 12th century. It is more slender than the rest of rural Galician Romanesque churches. It has an austere exterior, but it has several interesting Romanesque paintings of the Holy Trinity inside, as well as reredoses of the 12th and 13th centuries. If we ask in the nearby houses, we may be be able to visit it.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(201, 'media/SanMartinhodaCova3.jpg', '', 'A Praia de Cova', 'Podemos visitar también, si queremos hacer una pequeña parada en el camino, la playa fluvial de A Cova, que encontraremos señalizada en varias ocasiones bajando por la carretera. Se trata de un pequeño arenal artificial, muy concurrido en época estival, ubicado en un precioso rincón de las “ribeiras” del Miño. Cuenta con bar y otros servicios de ocio.', 'Imagen', '', '', 3, 510, 'Cova’s beach', 'We can also visit, if we want to stop briefly, the river beach A Cova. It is signposted along the road. It is a small artificial river beach, which is highly frequented in summer, set in a great site of the banks of the Miño. It has a bar and other leisure services. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(202, 'media/SanMiguelEire1.jpg', '', 'Historia', 'Como es habitual en las iglesias románicas de la Ribeira Sacra, Eiré fue en origen un antiguo monasterio, del que hoy unicamente se conserva el templo. Fundado en el año 962, siendo primero una pequeña abadía privada, de carácter dúplice (hombres y mujeres), pasa posteriormente a ser un convento femenino de la Órden Benedictina. Su decadencia llegó con las reformas de los Reyes Católicos, que lo anexionaron a San Paio de Antealtares (Compostela).', 'Imagen', 'Como decíamos, San Miguel de Eiré fue fundado y dotado como Monasterio privado en el año 962, bajo las órdenes de la abadesa Dona Seriberta. Todavía se conservan en el interior del monumento lo que según algunos historiadores serían testimonios de esta época, tales como una pila bautismal, una ventana y una tumba prerrománica. En un principio habitaban en la abadía tanto hombres como mujeres, algo muy habitual en la época, pero con la llegada de la estricta Órden Benedictina,  se empezó a considerar incompatible con la vida austera y dedicada a la oración que promulgaba, por lo que pasó a ser convento unicamente femenino. La decadencia llegó con la reforma eclesiástica de los Reyes Católicos, que comenzó en Galicia justamente por este lugar, y por la cual Eiré pasó a depender de San Paio de Antealtares, en Santiago de Compostela. Como consecuenca, todas las monjas fueron trasladadas y las rentas obtenidas de las tierras de Eiré, utilizadas para construir el Hospital Real de la Plaza del Obradoiro.', '', 1, 511, 'History', 'As something common in the Romanesque churches of Ribeira Sacra, Eiré was an ancient monastery originally, but today only the temple survives. Founded in 962, it was a small private abbey for men and women. Later it became female convent of the Benedictine Order. Its decline came with the Catholic Kings, who annexed it to San Paio of Antealtares (Compostela).', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(203, 'media/SanMiguelEire2.jpg', '', 'La Iglesia', 'San Miguel de Eiré está considerado como uno de los mejores conjuntos románicos del s.XII de los que se conservan en Galicia. Fue declarada Monumento Nacional en 1964, y sirvió en el s.XIII como modelo para la construcción de otras iglesias de la zona, como San Fiz de Cangas. Destacan la portada, la torre y las numerosas esculturas historiadas que salpican el monumento.', 'Imagen', '', '', 2, 511, 'The Church', 'San Miguel de Eire is considered to be one of the best Romanesque surviving sites of the 12th century in Galicia. It was declared National Monument in 1964. It was a model for the construction of other churches in the 13th century, as San Fiz de Cangas. If is famous for its front, the tower and the numerous sculptures that belong to the monument.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(204, 'media/SanMiguelEire4.jpg', '', 'La virilidad', 'Comenzamos en la fachada principal, con un motivo de caráter erótico, muy repetidos en la iglesia de Eiré. Se trata de un hombre con su miembro en erección, sostenido con la mano derecha, mientras la mano izquierda reposa sobre su cadera. En este caso representaría la virilidad y la fecundidad, generadora de vida. Si saltamos tres figuras a nuestra derecha, tenemos de nuevo el tema sexual, con un totem de fecundidad, representado como si de un menhir se tratase.', 'Imagen', '', '', 4, 511, 'The virility', 'Our visit begins in the major front, which has an erotic motif, repeated in the church of Eire. It is a man holding his erected virile member with his right hand, while the left hand rests on his hip. It might represent the virility and the fecundity, which creates life. Three figures away on the right, there is another sexual figure, a totem of fecundity, as if it were a menhir.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(205, 'media/SanMiguelEire5.jpg', '', 'El macaco', 'Representación de un hombre o de un macaco que con su mano derecha se agarra el miembro viril (mutilado por censura), y con la izquierda se tapa los ojos. Estamos ante un símbolo claro del pecado y una advertencia ante la degradación humana. La intención de figuras como esta sería la de corregir ciertos actos que la iglesia clasificaba como vicios propios de los animales.', 'Imagen', '', '', 5, 511, 'The monkey', 'It depicts a man or monkey that is clutching the virile member (cripple because of the censorship), and with the left hand covers the eyes. We are facing a clear symbol of sin and a warning against human degradation. The aim of these figures is the one of modifying certain habits that the church considered proper of animals rather than humans.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(206, 'media/SanMiguelEire6.jpg', '', 'El antiguo monasterio', 'Destaca en una de las paredes una puerta colgada en el aire. En el interior corresponde con el coro alto, y seguramente serviría para comunicar directamente esta dependencia del templo con las antiguas dependencias monacales, hoy desaparecidas. ', 'Imagen', '', '', 6, 511, 'The Old Monastery', 'The first thing we see is a door suspended on one of the walls. Inside the church, we can see the door is in the high choir, it must have connected the choir with the ancient monastic dependencies, today disappeared.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(207, 'media/SanMiguelEire7.jpg', '', 'La torre', 'Elemento singular, de gran originalidad dentro del románico gallego, al que unicamente se puede acceder desde el exterior, por una frágil escalera. Hace la función de campanario, con amplios decorados y ventanales.', 'Imagen', '', '', 7, 511, 'The tower', 'Curious element of the Galician Romanesque, we can only get into from the exterior, from a fragile stair. It functions as a bell tower, with great ornaments and large windows.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(208, 'media/SanMiguelEire8.jpg', '', 'El apocalipsis', 'Nos encontramos ante una pieza de gran interés, muy valorada por los amantes del románico. Se trata de una representación, única por su antigüedad, del apocalipsis, en la que aparecen los cinco caballos de la destrucción, representados aquí con rasgos de león y echando azufre por la boca. Este tipo de escenas apocalípticas fueron muy utilizada en la Edad Media para amedentrar a los fieles, tanto en forma de esculturas como en los sermones de los párrocos.', 'Imagen', '', '', 8, 511, 'The Apocalypse', 'We are looking at an interesting piece, worthy by the lovers of Romanesque. It is a picture of the apocalypse, and it is unique due to its antiquity. We can see the five horses of destruction, with features of lion and spiting sulphur. This type of apocalyptic scenes was abundant in the Middle Age, used to frighten the faithful, both by means of sculptures and also in the sermons.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(209, 'media/SanMiguelEire9.jpg', '', 'El cerdo', 'Junto a esta pieza encontramos la representación de un cerdo, animal relacionado en la época con la degradación de las virtudes, debido a su afición a alimentarse de inmundicias. Por esto era elegido para representar los pecados relacionados con la lujuria y con la gula. A su lado, a nuestra derecha y muy desgastado, un acto erótico entre dos personas, rendidas en abrazos y cruzadas de piés.', 'Imagen', '', '', 9, 511, 'The Pig', 'Next to this figure we find the image of a pig, related to the deprivation of virtues because this animal used to be fed of filth. It signified the sins related to lust or greed. Next to the pig, on the right, there is a worn erotic image depicting two people who are hugging and with their feet crossed. ', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(210, 'media/FervenzadeAugacaida1.jpg', '', 'Aparcar', 'Antes de llegar al pueblo de Marce, encontraremos un cartel que nos indicará el inicio de la ruta. Se trata de un paseo sencillo, excepto en el tramo final justo antes de llegar a la cascada, donde tendremos que bajar por un pedregal con pendiente pronunciada. La distancia total, ida y vuelta, es de unos 2.800 metros (1.200 metros más si optamos por visitar también el Castro). Si deciden llevar la tablet, por favor, tengan cuidado durante el camino.', 'Imagen', '', '', 1, 601, 'Parking', 'Before reaching the village of Marce, we will find a signpost that indicates the beginning of the route. It''s an easy walk, except in the last stretch of the route, just before reaching the cascade, where we will have to go down by a rocky ground with a steep slope. The entire distance, back and forth, is about 2,800 meters (there are 1,200 meters more if we also decide to visit the Castro). If you decide to take the tablet, please be careful in this walk.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(211, 'media/FervenzadeAugacaida2.jpg', '', 'O Castro', 'A unos 1.100 metros de comenzar el paseo, junto a una antigua bodega de piedra, encontramos un cruce que nos señaliza el castro y la cascada. Si decidimos continuar hasta el castro nos faltarán unos 600 metros. Prácticamente no quedan restos de las antiguas construcciones, pero si se pueden disfrutar buenas vistas sobre el Miño.', 'Imagen', '', '', 2, 601, 'O Castro', 'We find a crossroad, next to an old stone cellar, that indicates the castro and the cascade about 1,100 meters before starting the walk. If we decided to go to the castro, the walk will be 600 meters longer. Practically there are no remains of the ancient buildings, but we can enjoy the views of the Miño River.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(212, 'media/FervenzadeAugacaida3.jpg', '', 'El Castillo', 'La entrada al castro se hace por su acceso primitivo, un estrecho sendero labrado en la roca y flanqueado por viejos muros de piedra. Este fue el lugar elegido en la edad media para vigilar el Miño y el puerto de Chouzán (antiguo Porto Monsulio), que podemos ver en la orilla opuesta. Para ello se construyó una fortaleza, sobre el antiguo asentamiento prehistórico, que hoy está totalmente destruida. Podremos ver algunos restos de muros diseminados por el lugar.', 'Imagen', '', '', 3, 601, 'The Castle', 'We go into the castro by its old access, a narrow path carved in the rock and flanked by old stone walls. This place was chosen in the middle ages to guard the Miño and the port of Chouzan (former Porto Monsulio) that can be seen on the opposite bank. Therefore a fortress, now completely destroyed, was built where the ancient prehistoric settlement was located. We can see some wall remains scattered through the place.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(213, 'media/FervenzadeAugacaida4.jpg', '', 'Os Xacios', 'En esta zona vivían, según las leyendas locales, los hombres acuáticos (Os Xacios). Se trataba de unos seres mitológicos, con parte superior humana e inferior de pez, y que tenían su guarida en las pozas más profundas del río. Existen numerosos cuentos en la tradición popular de la zona, que nos hablan sobre encuentros e incluso matrimonios entre hombres y hermosas mujeres Xacias. ', 'Imagen', '', '', 4, 601, 'Os Xacios', 'In this area, according to local legends, the aquatic men (Os Xacios) lived. They were mythological creatures, with its upper part of the body in the shape of human and lower one of fish, and they lived in the deepest pools of the river. There are numerous local stories that tell us about the meetings and the marriages between men and beautiful Xacias women.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(214, 'media/FervenzadeAugacaida5.jpg', '', 'La cascada', 'Si hemos decidido visitar el castro, volveremos por nuestros pasos hasta el cruce junto a la antigua bodega de piedra. Desde aquí bajaremos a la cascada, pudiendo visitar su parte alta o bajar hasta su base. Esta última opción es la que nos ofrece mejores imágenes, pero para llegar tendremos que descender por un pedregal con la ayuda de unas cuerdas allí colocadas. El río Aguianza cae en este lugar desde un desnivel de 40 metros.', 'Imagen', '', '', 5, 601, 'The cascade', 'If we decided to visit the castro, we will return to the intersection where the old stone cellar is. From here we will go down to the waterfall, we may just see the top or we can go down to its base. This last option is which offers us better landscapes and images, but to get there we have to walk down by a rocky place with the help of ropes placed there. The river Aguianza drops here from a hollow of 40 meters.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(215, 'media/FervenzadeAugacaida6.jpg', '', 'Como llegar', 'Volvemos sobre nuestros pasos hasta el cruce del cartel (fervenza-castro). Bajamos hacia Augacaída y unos 150 metros más adelante, encontramos un nuevo letrero, que nos indica de nuevo la fervenza. Seguimos de frente unos 50 metros (no bajar junto al cartel), y antes del árbol caído, giramos a la derecha, comenzando la bajada. La pendiente es pronunciada y existen cuerdas para facilitar el paso. Con cuidado no debe de haber problema para descender. ', 'Imagen', '', '', 6, 601, 'How to get there', 'We need to be back on our steps to the intersection (fervenza-castro). We got to Augacaida 150 metres down. There is a new signpost, which points out the fervenza again. We go straight 50 metres (we should not go down next to the poster), and before reaching the fallen tree, we need to turn right, and there we begin to go down. There is a steep slope so it would be helpful to use the ropes to facilitate the descent. If we are careful, there is no problem to descend.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(216, 'media/FervenzadeAugacaida7.jpg', '', 'Marce', 'En el pueblo de Marce podemos ver algunos ejemplos de arquitectura tradicional. También una torre del reloj, construida hacia 1950 en honor a los emigrados a Argentina (banderas en la foto), y los restos de una antigua torre medieval de defensa (en la foto, en primer plano) que, según la tradición, tenía tales dimensiones que su sombra llegaba hasta la otra orilla del río. Hoy solo se conservan dos pisos, muy modificados, y reconvertidos en bodega o cuadra. ', 'Imagen', '', '', 7, 601, 'Marce', 'We can see some examples of traditional architecture in the village of Marce. There is a clock tower, built around 1950 in honour of the emigrants to Argentina (the flags in the picture). We can also see the remains of an old medieval Defence Tower (foreground of the picture) which, according to the locals, had such a big structure that the shadow reached the other bank of the river. Today there are only two modified floors and renewed into wine cellar or stable.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(217, 'media/PazodeArxeriz1.jpg', '', 'El Pazo', 'Conjunto arquitectónico declarado de Singular Interés Arquitectónico por la Xunta de Galicia. Se trata de un conjunto de edificios dispuestos alrededor de un patio central, algunos de ellos procedentes del s.XVII y muy representativos de la arquitectura gallega. Entraremos por el portón de la fotografía hasta el aparcamiento.', 'Imagen', '', '', 1, 605, 'The Pazo', 'Declared as Architectural Settlement of Singular Interest by the Xunta de Galicia. It is a set of buildings arranged around a central courtyard. Some of them date back to the 17th century and they characterise the Galician architecture. We will enter to the car park by the gate we can see in the picture.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(218, 'media/PazodeArxeriz2.jpg', '', 'Ecomuseo', 'Este antiguo pazo, gestionado por la Fundación Soto de Fión, alberga una exposición permanente sobre etnografía, cultura e historia de la Ribeira Sacra. Cuenta con tres salas, dedicadas al románico, a la viticultura y al río, además de barcas y numerosas herramientas tradicionales diseminadas por las instalaciones. Dentro de la finca, de 25 Ha, existe una ruta de 2,5 Km, en la que se incluye la visita al castro de Arxeriz y un mirador sobre el Miño. Ver horarios en +info.', 'Imagen', '<span style="font-weight: bold;">Junio-Septiembre</span><div><span style="font-weight: bold;"><br></span>Miércoles y Domingos: 11:00 a 14:00 h.</div><div><br>Jueves, Viernes y Sábados: 11:00 a 14.00 h y de 17:30 a 20:00 h.&nbsp;</div><div><br><span style="font-weight: bold;">Octubre-Mayo</span></div><div><span style="font-weight: bold;"><br></span>De Miércoles a Domingo: 11:00 a 14:00 h.</div><div><br><span style="font-weight: bold;">Visitas concertadas:</span> 692 941 667 (Llamar de 11:00 a 13:00 h, de Miércoles a Domingos)<br></div>', '', 2, 605, 'Eco-museum', 'This ancient pazo, managed by the Foundation Soto de Fion, has a permanent exhibition on ethnography, culture and history of the Ribeira Sacra. It has three rooms dedicated to Romanesque, viticulture and to the river, as well as some boats and numerous traditional tools scattered around the museum. Within the ranch that has25 Ha, there is a 2.5 Km route, which includes the visit to the castro of Arxeriz and a viewpoint over the Miño. Timetables in +info.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(219, 'media/Belesar1.jpg', '', 'El pueblo', 'Belesar se encuentra a orillas del Miño, entre los concellos de Chantada y O Saviñao. El lugar cobró importancia en la antigüedad como punto de paso del río, gracias a la existencia de un antiguo puente, hoy ya sustituído. Por aquí pasaban una vía romana y un Camino Real, utilizados para transportar el vino hasta otros lugares de Galicia. Existe aguas arriba un embalse con el mismo nombre, aunque las aguas aquí están contenidas por la presa de Peares.', 'Imagen', '', '', 1, 609, 'The Village', 'Belesar is located on the banks of the Miño River, between the Chantada and O Saviñao Counties. The place gained in importance in the past as a crossing point over the river, due to the existence of an ancient bridge, today replaced. There were a Roman road and a Royal Path, used to carry the wine to other places of Galicia. There is a reservoir upstream from the river with the same name, although the water is controlled by the dam of Peares.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(220, 'media/Belesar2.jpg', '', 'El embarcadero', 'Uno de los elementos que más nos llama la atención en este lugar, es la existencia de un embarcadero. Se trata del Náutico de Belesar, desde el que existe una variada oferta de paseos por el río. El espacio cuenta también con un restaurante y una agradable terraza.', 'Imagen', '', '', 2, 609, 'The Quay', 'One element that catches our attention is the quay. It is the Nautico de Belesar, from which we can walk through a wide range of paths along the river. It also has a restaurant and a nice terrace.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(221, 'media/Belesar3.jpg', '', 'De paseo', 'Si nos apetece, una buena idea puede ser dar un paseo por el lugar. Proponemos un tramo, en sentido inverso, de la senda de los Codos de Belesar, que llega hasta aquí procedente de Diomondi. En la orilla opuesta a la iglesia, enfrente de este edificio, asciende una calle entre las casas, que nos dirige a la parte alta del pueblo y a las viñas. Podemos subir por este camino para disfrutar del entorno y de las vistas. Cuanto más arriba lleguemos, mejores serán. ', 'Imagen', '', '', 3, 609, 'Walking', 'If we like walking, it may be a good idea to take a stroll through the place. We propose a walk in the path of Codos de Belesar, which comes to here from Diomondi. In the other river bank, opposite the church, there is a street that leads us to the top of the village and vineyards. We can go up this street to enjoy the surroundings and the views of the village. The longer we go up, the better will be the views.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(222, 'media/BodegaViaRomana.jpg', '', 'Bodega Vía Romana', 'Admite visitas para particulares y grupos (en este caso mejor reservar con antelación). La bodega cuenta con buenas vistas del entorno de Belesar. Información sobre horarios y contacto en +info. Foto de Vía Romana.', 'Imagen', '<span style="font-weight: bold;">Horario</span><div><span style="font-weight: bold;"><br></span>De Lunes a Viernes de 10:00 a 13:00 y de 16:00 a 19:00 h.&nbsp;</div><div><br>Sábados de 11:00 a 14:00 y de 17:00 a 20:00 h.</div><div><br><span style="font-weight: bold;">Contacto</span></div><div><span style="font-weight: bold;"><br></span>982 462 069<br></div>', '', 1, 610, 'Via Romana Wine Cellar', 'Visits for individuals and groups (In the case of groups, it is advisable to make a previous booking). The winery has spectacular views of the surroundings. More information can be found at +info. Photo: Vía Romana.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(223, 'media/TorredeCandaira1.jpg', '', 'Historia', 'Bonito ejemplo de arquitectura militar medieval. La construcción de la torre de Candaria se remonta a mediados del s.XIII, por lo que está considerada como uno de los edificios más antiguos de la comarca. Construída en granito y restaurada hace unos años, hoy es propiedad de la familia Taboada de Zuñiga y Caballo.', 'Imagen', '', '', 1, 611, 'History', 'It is a nice example of medieval military architecture. The Tower of Candaria dates back to the middle of the 13th century, so it is considered to be one of the most ancient buildings in the region. Built of granite and restored a few years ago, today it is owned by the family Taboada de Zúñiga y Caballo.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(224, 'media/TorredeCandaira2.jpg', '', 'La torre', 'El edificio, declarado Bien de Interés Cultural (B.I.C.) desde 1994, tiene planta cuadrada de casi 10 metros por lado, y unos 14 metros de altura. Conta de tres plantas, coronadas por almenas (no son las originales). Además de las “seteiras”, pequeñas ventanas por las que los defensores vigilaban y lanzaban sus flechas contra los atacantes, en la fachada podemos ver varios escudos, pertenecientes a las sucesivas familias propietarias.', 'Imagen', '', '', 2, 611, 'The tower', 'The building was listed for its cultural interest in 1994. It has a square floor, which is nearly 10 meters each side, and it is about 14 meters high. It has three floors, crowned by battlements although they are not the original ones. In addition to the "seteiras", small windows through which defenders watched and threw their arrows against the attackers, we can also see several shields on the front that belong to the different owners.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(225, 'media/TorredeCandaira3.jpg', '', 'El pazo', 'Los estudios sobre su diseño y sobre su localización, en un terreno de pronunciada pendiente, hacen presumible que no se tratase de una construcción aislada, ni de la torre del homenaje de una fortificación más grande, que pudiera haber sido derribada por los irmandiños (revuelta del s.XV contra la nobleza, en la que se destruyeron numerosas fortalezas en Galicia). La teoría más factible sería la de formar parte de de una antigua casa señorial, anexa, hoy ya desaparecida.', 'Imagen', '', '', 3, 611, 'Pazo', 'Studies about its design and its location (in an area of steep slope) show that they were not probably isolated constructions. It was not the remains of the homage tower of a larger fort, which could have been demolished by the “irmandiños” (a revolt in the 15th century against the nobility where a lot of fortresses in Galicia were destroyed). The most accepted idea would be the one about taking part in an old annexed manor house, which today has disappeared.', '');
INSERT INTO `Multimedia` (`id`, `uri`, `thumbUri`, `nombre`, `descripcion`, `tipo`, `mas_info`, `media_masinfo`, `orden`, `poiId`, `nombre_en`, `desc_en`, `mas_info_en`) VALUES(226, 'media/PraiadaCova1.jpg', '', 'La playa', 'Bonito rincón en pleno meandro del Cabo do Mundo. Se trata de una playa fluvial de arena, localizada a orillas del río Miño, y rodeada por un bonito entorno natural. Cuenta con área de recreo, cafetería y restaurante, abierto en verano.', 'Imagen', '', '', 1, 608, 'The beach', 'A lovely spot in the middle of the river bend, this is a riverside sand beach, located by the edge of the Miño river, and sourrounded by a beautiful natural environment. It has a recreation area, cafeteria and restaurant, which remains open in the summer.', '');
