INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(19, 'SantaCristina3', 'D', 'El lateral de la iglesia está decorado con esculturas románicas. Muy representativos los ventanales y las figuras de sus capiteles.  ', 'SantaCristina3D.jpg', 'The side of the church is decorated with Romanic sculptures. The large windows and the figures in their capitals are highly representative. ');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(20, 'PenedosdoCastro2', 'A', 'El origen de este asentamiento se encuentra en la Edad de Hierro (s.VI a.C). Abandonado en época romana, fue recuperado en época visigoda. Durante la Edad Media tuvo función de vigilancia de los accesos a Santo Estevo y de lugar de refugio para los monjes en caso de peligro.', '-', 'The Neolithic fortified settlement: The origin of this settlement dated from the iron age. (6th Century B.C.) Desolated in the roman period, it was recovered with the Visigoths. During the Middle age, it was used as vigilance post in order to have access to Santo Estevo and as a shelter for the monks in case of danger.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(21, 'PenedosdoCastro2', 'B', 'La muralla fue construida en el s.VI a.C con grandes piedras y sin argamasa. ', 'PenedosdoCastro2B1.jpg', 'It was built in the 6th Century B.C. by using rocks without mortar.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(22, 'PenedosdoCastro2', 'B', 'La muralla se reforzó en época medieval, llegando a un grosor considerable.', 'PenedosdoCastro2B2.jpg', 'It was reinforced in the medieval period reaching a great thickness.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(23, 'PenedosdoCastro2', 'C', 'Inscripción del s.VI en referencia al rey visigodo Leovigildo', 'PenedosdoCastro2C.jpg', 'Leovigil dux: Inscription from 6th Century referred  to Visigoth King Leovigildo.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(24, 'PenedosdoCastro2', 'D', 'Disfrutaremos de una de las mejores vistas de la Ribeira Sacra.', 'PenedosdoCastro2D.jpg', 'It can be appreciated one of the best views in Ribeira Sacra.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(25, 'PenedosdoCastro3', 'A', 'Buena ejemplo de arquitectura tradicional. Algunas casas conservan pozas, excavadas en la roca, para conservar la pesca.', 'PenedosdoCastro3A.jpg', 'San Cosmede: Good example of traditional architecture . Some houses preserve pools excavated in rocks to keep the fish.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(26, 'PenedosdoCastro3', 'B', 'El desnivel con el río es de 550 metros', 'PenedosdoCastro3B.jpg', 'Cross of Pena Pombeira (701m): There is a slope of 550 meters to  the river.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(27, 'PenedosdoCastro3', 'C', 'Hoy abandonado, albergó a los trabajadores que levantaron la presa de Santo Estevo.', 'PenedosdoCastro3C1.jpg', 'Workers who constructed the reservoir of Santo Estevo were hosted here. Nowadays it is abandoned.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(28, 'PenedosdoCastro3', 'C', 'Cuenta incluso con iglesia (s.XX), también en estado de ruina. ', 'PenedosdoCastro3C2.jpg', 'In addition, it also has a church (20th Century) which is in ruins too.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(29, 'PenedosdoCastro3', 'D', 'Fachadas de la iglesia y del monasterio', 'PenedosdoCastro3D1.jpg', 'Church and monastery´s front ');
