INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(31, 'PenedosdoCastro3', 'E', 'Casa de la Audiencia o de la Cárcel, con escudo del monasterio.', 'PenedosdoCastro3E1.jpg', 'House of court or prison, containing a shield that belonged to the monastery.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(32, 'PenedosdoCastro3', 'E', 'Antigua Casa del Médico, con escudo del monasterio', 'PenedosdoCastro3E2.jpg', 'Old doctor´s house, containing a shield that belonged to the monastery.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(33, 'PenedosdoCastro3', 'E', 'Casa de los Condes de Gustavalle. Hoy totalmente en ruina', 'PenedosdoCastro3E3.jpg', 'Counts Gustavalle´s house. Nowadays it is totally ruined. ');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(34, 'PenedosdoCastro3', 'F', 'Puente de Sobredo. Aquí el Sil recibe al río Cabe.', 'PenedosdoCastro3F1.jpg', 'Sobredo Bridge. The river Sil and the river Cabe are joined here.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(35, 'PenedosdoCastro3', 'F', 'Pueblo de A Barca y pasarela peatonal sobre el Cabe.', 'PenedosdoCastro3F2.jpg', 'A Barca village and footbridge to cross the river Cabe.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(36, 'PenedosdoCastro3', 'F', 'Cerca de la desembocadura del Cabe, había una cueva abierta hacia el río. Los que pasaban por allí al atardecer, aseguraban ver a una hermosa mujer de cabellos dorados sentada sobre una roca, peinándose con un peine de oro y mirándose a un espejo, también de oro. Pero cuando las gentes del lugar intentaban acercarse, la hermosa mujer se convertía en una serpiente, desapareciendo rápidamente en el interior de la cueva.', '-', 'Near the mouth of the river Cabe, there was an open cave facing the river. People who walked there in the evening claimed that they could see a beautiful golden-hair woman sitting on a stone, brushing  herself with a golden comb and looking in golden mirror too. Nevertheless, when people tried to talk to her, the beautiful women became a snake and disappeared into the cave.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(37, 'PenedosdoCastro3', 'G', 'Colegio de los Escolapios (Antiguo monasterio - s.XVI)', 'PenedosdoCastro3G.jpg', 'Monforte de Lemos: Escolapios School (Old monastery, 16th Century)');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(38, 'PenedosdoCastro3', 'H', 'Plaza Campo da Feira y Casa do Concello', 'PenedosdoCastro3H.jpg', 'Sober: Campo da Feira Square (market) and Council');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(39, 'SantoEstevoRibasSil2', 'B', 'Escudo del Reino de Castilla. Destacan el Águila Bicéfala, símbolo de la Casa de Austria, el león y el castillo, símbolos de las coronas de Castilla y de León. ', 'SantoEstevoRibasSilB.jpg', 'Castile Kingdom Shield. Standing out the two-headed eagle, symbol of Austria´s House, the lion and the castle, symbols the crown of Castile and Lion.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(40, 'SantoEstevoRibasSil2', 'D', 'Escudo de la Congregación Cisterciense de Castilla. Castillo, León y báculo benedictino. ', 'SantoEstevoRibasSilD.jpg', 'The Baroque front of the monastery, finished in 1734, was erected to replace and older one. It is almost a stony altarpiece, with sculptures and shields of different periods. When entering the monastery there was found “El torno de las limosnas” (alms room). In this room harvested food was stored. Twice a week the cloisters door was closed and the monastery door was opened to give food to the homeless.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(41, 'SantoEstevoRibasSil2', 'E', 'En la parte izquierda, figura de San Benito de Nursia, fundador de la orden benedictina. Sujeta el libro en el que figuran las rígidas reglas que debían de cumplir las abadías de esta orden. ', 'SantoEstevoRibasSilE.jpg', 'On the left side, figure of San Benito de Nursia, founder of Benedictine order. He stands the book where are the hard laws that all abbeys had to obey.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(42, 'SantoEstevoRibasSil2', 'F', 'San Vicente de León, personaje considerado mártir por su defensa del cristianismo. ', 'SantoEstevoRibasSilF.jpg', 'San Vicente de León is considered to be a martyr by his support to Christianity. ');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(43, 'SantoEstevoRibasSil2', 'A', 'La fachada barroca del monasterio, rematada en 1734, fue levantada para sustituir a otra más antigua, presumiblemente románica. Se trata prácticamente de un retablo pétreo, con esculturas y escudos de diferentes épocas. Nada más atravesar la puerta que da acceso al monasterio, se encontraba el torno de las limosnas. En este habitáculo se acumulaban alimentos producidos en las tierras del monasterio. Dos días a la semana se cerraba la del claustro y se abría la exterior para ofrecer estos productos a los mendigos que hasta allí se acercaban.', '-', 'The Baroque front of the monastery, finished in 1734, was erected to replace and older one. It is almost a stony altarpiece, with sculptures and shields of different periods. When entering the monastery there was found “El torno de las limosnas” (alms room). In this room harvested food was stored. Twice a week the cloisters door was closed and the monastery door was opened to give food to the homeless.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(44, 'SantoEstevoRibasSil2', 'C', 'Según cuenta la tradición, nueve obispos santos, que tuvieron que huir ante la invasión sarracena, eligieron Santo Estevo como lugar de retiro en los siglos X y XI. En los años posteriores a la muerte de estos obispos, sus anillos fueron conservados en una urna, ante la que numerosos fieles y peregrinos les pedían favores. Este hecho dio gran renombre al monasterio, incluso lejos de la Ribeira Sacra, y como conmemoración les fue dedicado el escudo del monasterio, con nueve mitras (gorros), uno por cada obispo.', '-', 'According to the tradition, nine saints bishops, who had to run away in the Saracen invasion, chose Santo Estevo like venue to retreat from  10th  to  11th Century. When the bishops died their rings were kept in a vase and a large group of faithful and migrants demanded favours to it. This fact gave importance to the monastery, even so far from Ribeira Sacra, and in the bishop’s memorial the shield of the monastery was dedicated to them with nine mitres, one for each bishop.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(45, 'SantoEstevoRibasSil2', 'G', 'La iglesia es románica del s.XIII, pero ha sufrido numerosas modificaciones posteriores. La fachada actual es del s.XVIII, y conserva dos grandes contrafuertes originales. Proponemos iniciar la visita a Santo Estevo en el interior de la iglesia, donde podremos ver algunos elementos artísticos destacados. En cuanto al exterior, la espesa vegetación y lo peligroso de la orografía desaconsejan intentar rodear el templo.', '-', 'The Romanesque church date from 18th Century but it was a lot of modifications throw the years. Actually façade is from 18th Century and it keeps two big original buttresses. We suggest you start the visit to Santo Estevo inside the church, where there are some noted elements of architecture. Outside the church, there is thickness vegetation and a danger orography that advise against about circle the temple.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(46, 'MiradordaColumna2', 'A', 'En esta pequeña localidad existió un antiguo albergue de peregrinos, propiedad de los monjes de Santo Estevo. Un documento de restauración del rey Ordoño II, datado en el año 921, da cuenta de esta construcción, que contó con su mayor actividad durante los siglos XVI y XVII.', '-', 'In this small village there was an old pilgrim hostel that belonged to the monks of Santo Estevo. It first appeared in a restoration document of King Ordoño II, which dates back to 921. It claims that the hostel was considered important in 16th-17th Centuries.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(47, 'MiradordaColumna2', 'A', 'Bonito Cruceiro en Alberguería, levantado en el s.XIX.', 'MiradordaColumna2A2.jpg', 'Beautiful cross in Alberguería erected in 19th Century. ');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(48, 'MiradordaColumna2', 'B', 'Antigua Casa Rectoral, construida en la primera mitad del s.XX. ', 'MiradordaColumna2B1.jpg', 'Ancient Rectory House, constructed in the first half of the XXth century.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(49, 'MiradordaColumna2', 'B', 'Casas de piedra en el pueblo de Cerreda', 'MiradordaColumna2B2.jpg', 'Stones houses in the village of Cerreda');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(50, 'MiradordaColumna2', 'B', 'Existen restos en la sierra de antiguos pozos subterráneos, revestidos de piedra, denominados “Casas da Neve”. En ellos se conservaba nieve hasta el verano, transportada entonces, de noche y a lomos de mulas, hasta Ourense. Allí se vendía a familias adineradas, que la utilizaban para fabricar productos refrescantes. Estas estructuras eran propiedad del Monasterio de Santo Estevo.', '-', 'There are still preserved the remains of ancient underground wells in the mountain chain, covered in stone which are called “Snow Houses”. There was snow kept in them until the summer when it was carried by asses to Ourense at night. Once it arrived there, it was sold to wealthy families that used it in order to keep products as fresh as possible. These structures were a property of the Santo Estevo Monastery.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(51, 'MiradordaColumna2', 'C', 'Capilla de San Xoan de Vilouxe', 'MiradordaColumna2C1.jpg', 'Chapel of San Xoan of Vilouxe');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(52, 'MiradordaColumna2', 'C', 'Bonito pueblo con numerosos ejemplos de arquitectura tradicional', 'MiradordaColumna2C2.jpg', 'Nice village full of examples of traditional architecture');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(53, 'MiradordaColumna2', 'C', 'Los Petos de Ánimas se asocian a la idea del culto a los muertos. Su fin es el de poder orar por las almas del purgatorio, para que asciendan a los cielos.', 'MiradordaColumna2C3.jpg', 'The Souls Guards are related to the idea of the worship to the dead. Their aim is that of being able to pray for the souls of the purgatory, so that they could ascend to heaven.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(54, 'MiradordaColumna2', 'D', 'En este punto salen los catamaranes de Santo Estevo (Ourense) y de Os Chancís (Lugo), que recorren el Sil, justo a nuestros pies.', 'MiradordaColumna2D.jpg', 'The quay: Catamarans from Santo Estevo (Ourense) and from Os Chancis (Lugo) can be seen at this point. They go across the Sil, just under our feet.');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(55, 'MiradordaColumna2', 'E', 'El desnivel del cañón alcanza aquí los 525 metros', '-', 'The slope of the cannon reaches here 525 meters');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(56, 'MiradordaColumna2', 'F', 'Moderna plataforma de acero, construida en 2012 y diseñada por la arquitecta Isabel Aguirre. Bonitas vistas del Cañón. ', 'MiradordaColumna2F.jpg', 'Cividade Viewpoint: Modern platform of steel constructed in 2012 and designed by the architect Isabel Aguirre. There are nice sights of the Cannon. ');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`, `desc_en`) VALUES(57, 'MiradordaColumna2', 'G', 'En este lugar se encuentra un antiguo asentamiento castreño, ubicado sobre un espolón granítico inexpugnable. Se conservan restos de muros defensivos, un foso y algunos restos cerámicos.  ', '-', 'Castro da Cividade: Here can be seen an ancient fort. There are remains of the defensive walls, a pit and some ceramic vestiges.');
