INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(2, 'BalconesdeMadrid1', 'B', 'Castelo de Castro Caldelas', 'BalconesdeMadrid1B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(3, 'BalconesdeMadrid1', 'C', 'Calles en San Lourenzo', 'BalconesdeMadrid1C1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(4, 'BalconesdeMadrid1', 'C', 'Necrópolis de San Vitor, cerca de San Lourenzo', 'BalconesdeMadrid1C2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(5, 'BalconesdeMadrid1', 'D', 'San Adrián: Lugar en el que existió un antiguo monasterio.', 'BalconesdeMadrid1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(6, 'BalconesdeMadrid1', 'E', 'El nombre podría venir de Sakarboudio, la victoria del guerrero Sakar. ', 'BalconesdeMadrid1E1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(7, 'BalconesdeMadrid1', 'E', 'Magosto: En noviembre, con la castaña como protagonista. ', 'BalconesdeMadrid1E2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(8, 'BalconesdeMadrid1', 'F', 'Según la tradición, descendientes de Noé, llegados hasta Porta Brosmos remontando el río desde el mar, plantaron aquí las primeras viñas de la Ribeira.', 'BalconesdeMadrid1F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(9, 'BalconesdeMadrid1', 'G', 'La tradición atribuye numerosos milagros a la Virgen de Cadeiras, que cuenta con gran devoción en la comarca.', 'BalconesdeMadrid1G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(10, 'BalconesdeMadrid1', 'H', 'Monforte de Lemos', 'BalconesdeMadrid1H.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(11, 'BalconesdeMadrid1', 'I', 'A Arriba, Parroquia de Barantes (Sober)', 'BalconesdeMadrid1I.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(12, 'BalconesdeMadrid1', 'J', 'El desnivel llega en este punto hasta los 450 metros.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(13, 'SantaCristina3', 'A', 'Arco románico que da acceso al monasterio.', 'SantaCristina3A1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(14, 'SantaCristina3', 'A', 'Evangelistas: Marcos (León) y Mateo (Hombre)', 'SantaCristina3A2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(15, 'SantaCristina3', 'A', 'Evangelistas: Juan (Águila) y Lucas (Toro)', 'SantaCristina3A3.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(16, 'SantaCristina3', 'B', 'En el acceso principal a la iglesia destaca la decoración de ajedrezado.', 'SantaCristina3B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(17, 'SantaCristina3', 'B', 'Capitel con tres caras (quizás la santísima trinidad)', 'SantaCristina3B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(18, 'SantaCristina3', 'C', 'Magnífico rosetón románico construido para iluminar el templo.', 'SantaCristina3C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(19, 'SantaCristina3', 'D', 'El lateral de la iglesia está decorado con esculturas románicas. Muy representativos los ventanales y las figuras de sus capiteles.  ', 'SantaCristina3D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(20, 'PenedosdoCastro2', 'A', 'El origen de este asentamiento se encuentra en la Edad de Hierro (s.VI a.C). Abandonado en época romana, fue recuperado en época visigoda. Durante la Edad Media tuvo función de vigilancia de los accesos a Santo Estevo y de lugar de refugio para los monjes en caso de peligro.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(21, 'PenedosdoCastro2', 'B', 'La muralla fue construida en el s.VI a.C con grandes piedras y sin argamasa. ', 'PenedosdoCastro2B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(22, 'PenedosdoCastro2', 'B', 'La muralla se reforzó en época medieval, llegando a un grosor considerable.', 'PenedosdoCastro2B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(23, 'PenedosdoCastro2', 'C', 'Inscripción del s.VI en referencia al rey visigodo Leovigildo', 'PenedosdoCastro2C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(24, 'BalconesdeMadrid1', 'D', 'Disfrutaremos de una de las mejores vistas de la Ribeira Sacra.', 'PenedosdoCastro2D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(25, 'PenedosdoCastro3', 'A', 'Buena ejemplo de arquitectura tradicional. Algunas casas conservan pozas, excavadas en la roca, para conservar la pesca.', 'PenedosdoCastro3A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(26, 'PenedosdoCastro3', 'B', 'El desnivel con el río es de 550 metros', 'PenedosdoCastro3B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(27, 'PenedosdoCastro3', 'C', 'Hoy abandonado, albergó a los trabajadores que levantaron la presa de Santo Estevo.', 'PenedosdoCastro3C1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(28, 'PenedosdoCastro3', 'C', 'Cuenta incluso con iglesia (s.XX), también en estado de ruina. ', 'PenedosdoCastro3C2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(29, 'PenedosdoCastro3', 'D', 'Fachadas de la iglesia y del monasterio', 'PenedosdoCastro3D1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(30, 'PenedosdoCastro3', 'D', 'Fue restaurado y transformado en Parador Nacional de Turismo', 'PenedosdoCastro3D2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(31, 'PenedosdoCastro3', 'E', 'Casa de la Audiencia o de la Cárcel, con escudo del monasterio.', 'PenedosdoCastro3E1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(32, 'PenedosdoCastro3', 'E', 'Antigua Casa del Médico, con escudo del monasterio', 'PenedosdoCastro3E2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(33, 'PenedosdoCastro3', 'E', 'Casa de los Condes de Gustavalle. Hoy totalmente en ruina', 'PenedosdoCastro3E3.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(34, 'PenedosdoCastro3', 'F', 'Puente de Sobredo. Aquí el Sil recibe al río Cabe.', 'PenedosdoCastro3F1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(35, 'PenedosdoCastro3', 'F', 'Pueblo de A Barca y pasarela peatonal sobre el Cabe.', 'PenedosdoCastro3F2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(36, 'PenedosdoCastro3', 'F', 'Cerca de la desembocadura del Cabe, había una cueva abierta hacia el río. Los que pasaban por allí al atardecer, aseguraban ver a una hermosa mujer de cabellos dorados sentada sobre una roca, peinándose con un peine de oro y mirándose a un espejo, también de oro. Pero cuando las gentes del lugar intentaban acercarse, la hermosa mujer se convertía en una serpiente, desapareciendo rápidamente en el interior de la cueva.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(37, 'PenedosdoCastro3', 'G', 'Colegio de los Escolapios (Antiguo monasterio - s.XVI)', 'PenedosdoCastro3G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(38, 'PenedosdoCastro3', 'H', 'Plaza Campo da Feira y Casa do Concello', 'PenedosdoCastro3H.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(39, 'SantoEstevoRibasSil2', 'B', 'Escudo del Reino de Castilla. Destacan el Águila Bicéfala, símbolo de la Casa de Austria, el león y el castillo, símbolos de las coronas de Castilla y de León. ', 'SantoEstevoRibasSilB.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(40, 'SantoEstevoRibasSil2', 'D', 'Escudo de la Congregación Cisterciense de Castilla. Castillo, León y báculo benedictino. ', 'SantoEstevoRibasSilD.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(41, 'SantoEstevoRibasSil2', 'E', 'En la parte izquierda, figura de San Benito de Nursia, fundador de la orden benedictina. Sujeta el libro en el que figuran las rígidas reglas que debían de cumplir las abadías de esta orden. ', 'SantoEstevoRibasSilE.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(42, 'SantoEstevoRibasSil2', 'F', 'San Vicente de León, personaje considerado mártir por su defensa del cristianismo. ', 'SantoEstevoRibasSilF.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(43, 'SantoEstevoRibasSil2', 'A', 'La fachada barroca del monasterio, rematada en 1734, fue levantada para sustituir a otra más antigua, presumiblemente románica. Se trata prácticamente de un retablo pétreo, con esculturas y escudos de diferentes épocas. Nada más atravesar la puerta que da acceso al monasterio, se encontraba el torno de las limosnas. En este habitáculo se acumulaban alimentos producidos en las tierras del monasterio. Dos días a la semana se cerraba la del claustro y se abría la exterior para ofrecer estos productos a los mendigos que hasta allí se acercaban.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(44, 'SantoEstevoRibasSil2', 'C', 'Según cuenta la tradición, nueve obispos santos, que tuvieron que huir ante la invasión sarracena, eligieron Santo Estevo como lugar de retiro en los siglos X y XI. En los años posteriores a la muerte de estos obispos, sus anillos fueron conservados en una urna, ante la que numerosos fieles y peregrinos les pedían favores. Este hecho dio gran renombre al monasterio, incluso lejos de la Ribeira Sacra, y como conmemoración les fue dedicado el escudo del monasterio, con nueve mitras (gorros), uno por cada obispo.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(45, 'SantoEstevoRibasSil2', 'G', 'La iglesia es románica del s.XIII, pero ha sufrido numerosas modificaciones posteriores. La fachada actual es del s.XVIII, y conserva dos grandes contrafuertes originales. Proponemos iniciar la visita a Santo Estevo en el interior de la iglesia, donde podremos ver algunos elementos artísticos destacados. En cuanto al exterior, la espesa vegetación y lo peligroso de la orografía desaconsejan intentar rodear el templo.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(46, 'MiradordaColumna2', 'A', 'En esta pequeña localidad existió un antiguo albergue de peregrinos, propiedad de los monjes de Santo Estevo. Un documento de restauración del rey Ordoño II, datado en el año 921, da cuenta de esta construcción, que contó con su mayor actividad durante los siglos XVI y XVII.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(47, 'MiradordaColumna2', 'A', 'Bonito Cruceiro en Alberguería, levantado en el s.XIX.', 'MiradordaColumna2A2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(48, 'MiradordaColumna2', 'B', 'Antigua Casa Rectoral, construida en la primera mitad del s.XX. ', 'MiradordaColumna2B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(49, 'MiradordaColumna2', 'B', 'Casas de piedra en el pueblo de Cerreda', 'MiradordaColumna2B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(50, 'MiradordaColumna2', 'B', 'Existen restos en la sierra de antiguos pozos subterráneos, revestidos de piedra, denominados “Casas da Neve”. En ellos se conservaba nieve hasta el verano, transportada entonces, de noche y a lomos de mulas, hasta Ourense. Allí se vendía a familias adineradas, que la utilizaban para fabricar productos refrescantes. Estas estructuras eran propiedad del Monasterio de Santo Estevo.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(51, 'BalconesdeMadrid1', 'C', 'Capilla de San Xoan de Vilouxe', 'MiradordaColumna2C1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(52, 'MiradordaColumna2', 'C', 'Bonito pueblo con numerosos ejemplos de arquitectura tradicional', 'MiradordaColumna2C2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(53, 'MiradordaColumna2', 'C', 'Los Petos de Ánimas se asocian a la idea del culto a los muertos. Su fin es el de poder orar por las almas del purgatorio, para que asciendan a los cielos.', 'MiradordaColumna2C3.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(54, 'MiradordaColumna2', 'D', 'En este punto salen los catamaranes de Santo Estevo (Ourense) y de Os Chancís (Lugo), que recorren el Sil, justo a nuestros pies.', 'MiradordaColumna2D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(55, 'MiradordaColumna2', 'E', 'El desnivel del cañón alcanza aquí los 525 metros', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(56, 'MiradordaColumna2', 'F', 'Moderna plataforma de acero, construida en 2012 y diseñada por la arquitecta Isabel Aguirre. Bonitas vistas del Cañón. ', 'MiradordaColumna2F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(57, 'MiradordaColumna2', 'G', 'En este lugar se encuentra un antiguo asentamiento castreño, ubicado sobre un espolón granítico inexpugnable. Se conservan restos de muros defensivos, un foso y algunos restos cerámicos.  ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(58, 'MiradordaColumna2', 'H', 'Conjunto Monumental de San Vicente.', 'MiradordaColumna2H.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(59, 'MiradordeCabezoas1', 'A', 'En esta pequeña localidad existió un antiguo albergue de peregrinos, propiedad de los monjes de Santo Estevo. Un documento de restauración del rey Ordoño II, datado en el año 921, da cuenta de esta construcción, que contó con su mayor actividad durante los siglos XVI y XVII. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(60, 'MiradordeCabezoas1', 'A', 'Bonito Cruceiro en Alberguería, levantado en el s.XIX.', 'MiradordeCabezoas1A2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(61, 'MiradordeCabezoas1', 'B', 'Antigua Casa Rectoral, construida en la primera mitad del s.XX. ', 'MiradordeCabezoas1B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(62, 'MiradordeCabezoas1', 'B', 'Casas de piedra en el pueblo de Cerreda', 'MiradordeCabezoas1B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(63, 'MiradordeCabezoas1', 'B', 'Existen restos en la sierra de antiguos pozos subterráneos, revestidos de piedra, denominados “Casas da Neve”. En ellos se conservaba nieve hasta el verano, transportada entonces, de noche y a lomos de mulas, hasta Ourense. Allí se vendía a familias adineradas, que la utilizaban para fabricar productos refrescantes. Estas estructuras eran propiedad del Monasterio de Santo Estevo. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(64, 'MiradordeCabezoas1', 'C', 'El desnivel del cañón alcanza aquí los 525 metros', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(65, 'MiradordeCabezoas1', 'D', 'Mirador de Cividade: Moderna plataforma de acero, construida en 2012 y diseñada por la arquitecta Isabel Aguirre. Bonitas vistas del Cañón. ', 'MiradordeCabezoas1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(66, 'MiradordeCabezoas1', 'E', 'En este lugar se encuentra un antiguo asentamiento castreño, ubicado sobre un espolón granítico inexpugnable. Se conservan restos de muros defensivos, un foso y algunos restos cerámicos.  ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(67, 'MiradordeCabezoas1', 'F', 'Estupenda panorámica del río Sil, justo enfrente del monasterio de Santa Cristina de Ribas de Sil. ', 'MiradordeCabezoas1F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(68, 'MiradordeCabezoas1', 'G', 'Nosa Señora da Antiga (Colegio de los Escolapios).', 'MiradordeCabezoas1G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(74, 'SantaCristina7', 'A', 'El claustro de Santa Cristina, renacentista del s.XVI, se encuentra incompleto. Se dice que una parte fue derruida, pero da la impresión de que su construcción fue ya en origen incompleta, siendo abandonadas las obras en la época en la que se produjo la absorción de la abadía por parte de Santo Estevo. Se conservan algunos restos románicos en el piso inferior, como una inscripción en la que se lee MCCXXX (1192), para algunos, momento de fundación del monasterio. Los dormitorios de los monjes se encontraban en el primer piso. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(75, 'SantaCristina7', 'B', 'Curioso remate en almena y pirámide triangular. Su función era la de campanario, torre vigía y de lugar de lectura de libros sagrados', 'SantaCristina7B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(76, 'SantaCristina7', 'B', 'Podemos acceder a su parte más alta, desde donde podremos disfrutar de vistas del Cañón del Sil.', 'SantaCristina7B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(77, 'SantaCristina7', 'C', 'Adosada al tramo norte del claustro, se trata de un elemento excepcional. Las iglesias románica gallegas no suelen contar con torres. ', 'SantaCristina7C1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(78, 'SantaCristina7', 'C', 'El pie de la torre conforma un rincón singular, que puede llegar a parecer una capilla funeraria. Decorado con capiteles florales y arpías.', 'SantaCristina7C2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(79, 'SantaCristina7', 'C', 'La entrada a la torre se encuentra en el primer piso. Esta singularidad cumple función defensiva, al dificultar el acceso a los intrusos. ', 'SantaCristina7C3.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(80, 'SantaCristina7', 'D', 'Podemos ver varias lápidas antiguas (s.XIV-XV) reutilizadas en las paredes. Corresponden a algunos de los abades del monasterio.', 'SantaCristina7D1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(81, 'SantaCristina7', 'D', 'Lápida del Abad Alfonso Fernández, máxima autoridad del monasterio entre 1380 Y 1416. Aparece provisto algunas insignias jerárquicas.', 'SantaCristina7D2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(82, 'MiradordeGaleana1', 'A', 'El río Mao y sus imponentes desfiladeros', 'MiradordeGaleana1A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(83, 'MiradordeGaleana1', 'B', 'Por esta ladera desciende la espectacular ruta (circular, 13 Km), uno de los principales caminos de acceso a la Ribeira en la antigüedad.', 'MiradordeGaleana1B1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(84, 'MiradordeGaleana1', 'B', 'Discurre por paisajes espectaculares y durante algunos tramos a través de un antiguo canal. Se puede comenzar en Ponte Conceliñas o en San Lourenzo.', 'MiradordeGaleana1B2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(85, 'MiradordeGaleana1', 'C', 'Por aquí discurre la famosa Ruta de las Pasarelas del Mao (2 km), que recorre el último tramo del río antes de su desembocadura en el Sil. ', 'MiradordeGaleana1C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(86, 'MiradordeGaleana1', 'D', 'Yacimiento de la Necrópolis de San Vitor', 'MiradordeGaleana1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(87, 'MiradordeGaleana1', 'E', 'En este pueblo existió una antigua abadía', 'MiradordeGaleana1E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(88, 'MiradordeGaleana1', 'F', 'Lugar en el que existió otro antiguo monasterio.', 'MiradordeGaleana1F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(89, 'MiradordeGaleana1', 'G', 'Según los textos históricos, todavía existían en la desembocadura del Mao dos viejos monasterios más, de los que aun hoy no se han encontrado restos ni se conoce su ubicación exacta. Llama la atención esta concentración de monasterios en un espacio tan reducido, posiblemente en relación con la proximidad del antiguo camino de acceso a la Ribeira Sacra y a lo privilegiado del entorno.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(90, 'MiradordeGaleana1', 'H', 'Se trata del punto más alto de la comarca (1.328 m). Cuenta una leyenda que en su cima hubo antiguamente un castillo.', 'MiradordeGaleana1H.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(91, 'MiradordeGaleana1', 'I', 'Bonito pueblo en la desembocadura del Mao', 'MiradordeGaleana1I.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(92, 'MiradordeGaleana1', 'J', 'La Ribeira de Amandi, famosa por sus vinos en toda Europa ya desde época romana.', 'MiradordeGaleana1J.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(93, 'CastroCaldelas5', 'A', 'La torre conserva el antiguo reloj, en funcionamiento, con su maquinaria original. ', 'CastroCaldelas5A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(94, 'CastroCaldelas5', 'B', 'Además de sus dependencias con aire medieval, podremos encontrar marcas de canteros en las piedras. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(95, 'CastroCaldelas5', 'C', 'Alberga una colección de herramientas e instrumentos tradicionales, además de una muestra de arquitectura local.', 'CastroCaldelas5C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(96, 'CastroCaldelas5', 'D', 'Elemento principal del castillo, podremos subir hasta la parte más alta, desde donde nos ofrece amplias vistas de la comarca. ', 'CastroCaldelas5D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(97, 'CastroCaldelas5', 'E', 'Entrada a Torre del Homenaje, así como a dependencias municipales, como la biblioteca. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(98, 'CastroCaldelas5', 'F', 'Abierta todos los días de 10:00 a 14:00 y de 16:00 a 18:00 h. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(99, 'CastroCaldelas5', 'G', 'Además de las almenas y los muros, encontraremos aquí un curioso reloj de sol. ', 'CastroCaldelas5G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(100, 'Montederramo2', 'A', 'La fachada de la iglesia, terminada en 1607, es de estilo neoclásico, muy sencillo. En ella podemos ver una imagen de Santa María y, en la parte superior, el escudo de la Congregación Cisterciense de Castilla (una mitra o gorro de obispo, una flor de lis y un bastón).', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(101, 'Montederramo2', 'B', 'Lo primero que encontramos es la verja policromada, que servía para separar a los monjes de la plebe durante los actos religiosos.', 'Montederramo2B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(102, 'Montederramo2', 'C', 'Entrada al Monasterio a través del Claustro de la Portería', 'Montederramo2C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(103, 'Montederramo2', 'D', 'Imagen deteriorada de un santo, posiblemente San Bernardo, gran impulsor de la Orden Cisterciense. ', 'Montederramo2D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(104, 'Montederramo2', 'E', 'El aspecto original de la fachada del monasterio aparece bastante modificado debido al tapiado de huecos y a la apertura de otros nuevos. ', 'Montederramo2E1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(105, 'Montederramo2', 'E', 'Esta modificaciones se hicieron para dar servicio a las viviendas y negocios que se instalaron en las dependencias del antiguo monasterio. ', 'Montederramo2E2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(106, 'MiradordeDoade4', 'A', 'Uno de los miradores más espectaculares de la zona. En él se encuentra la capilla de San Mauro.', 'MiradordeDoade4A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(107, 'MiradordeDoade4', 'B', 'Magnifica atalaya sobre el embarcadero de Doade.', 'MiradordeDoade4B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(108, 'MiradordeDoade4', 'C', 'Lugar de partida de catamaranes turísticos.', 'MiradordeDoade4C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(109, 'MiradordeDoade4', 'D', 'Bonitas vistas del Sil y de la Ribeira de Amandi.', 'MiradordeDoade4D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(110, 'MiradordeDoade4', 'E', 'Antiguas bodegas de piedra cerca del pueblo de Paradela.', 'MiradordeDoade4E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(111, 'MiradordeDoade4', 'F', 'Preciosa casa rústica en el entorno de Nogueira, en la parroquia de Abeleda.', 'MiradordeDoade4F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(112, 'MiradordeDoade4', 'G', 'Pueblo abandonado a mediados del s.XX como consecuencia de corrimientos de tierra provocados por la construcción del embalse. ', 'MiradordeDoade4G1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(113, 'MiradordeDoade4', 'G', 'En la actualidad del pueblo solo quedan algunas ruinas, invadidas por la vegetación del espeso bosque de castaños centenarios. ', 'MiradordeDoade4G2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(114, 'MiradordeDoade5', 'A', 'Capital municipal. En la foto Capilla de Santa Catalina en la plaza Cardenal Quiroga.', 'MiradordeDoade5A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(115, 'MiradordeDoade5', 'B', 'Pueblo localizado en la desembocadura del río Mao.', 'MiradordeDoade5B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(116, 'MiradordeDoade5', 'C', 'Bonito pueblo con varios ejemplos de arquitectura tradicional y restos de un antiguo monasterio.', 'MiradordeDoade5C1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(117, 'MiradordeDoade5', 'C', 'En las proximidades se encuentra el yacimiento de San Vitor, en el que se han descubierto un buen número de tumbas medievales con forma humana. ', 'MiradordeDoade5C2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(118, 'MiradordeDoade5', 'D', 'En las proximidades de Doade existen varias bodegas de la Denominación de Origen Ribeira Sacra. Algunas de ellas admiten visitas.', 'MiradordeDoade5D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(119, 'MiradordelDuque1', 'A', 'Pueblo prácticamente abandonado debido a su aislamiento. Pertenece al concello de Monforte de Lemos.', 'MiradordelDuque1A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(120, 'MiradordelDuque1', 'B', 'A este lugar se trasladó la población de San Pedro de Baños hace años, debido a su mejor comunicación con el exterior. ', 'MiradordelDuque1B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(121, 'MiradordelDuque1', 'C', 'Precioso meandro del Sil junto a la localidad, del mismo nombre, que se encuentra parcialmente restaurada por la Xunta de Galicia. ', 'MiradordelDuque1C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(122, 'MiradordelDuque1', 'D', 'Iglesia románica de Santa María de Torbeo, originaria del s.XII.', 'MiradordelDuque1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(123, 'MiradordelDuque1', 'E', 'Al fondo de la imagen está este espacio montañoso de extraordinaria belleza, que conserva valores naturales y etnográficos de gran interés. ', 'MiradordelDuque1E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(124, 'MiradordelDuque1', 'F', 'El empuje de las fuerzas de la tierra hizo que en este lugar las capas geológicas se levantasen, quedando en posición casi vertical. Las zonas blandas se desgastaron más rápidamente (hoy zonas ocupadas por vegetación), mientras que las más resistentes se mantienen mejor, conformando unas franjas de arriba a abajo, muy afiladas. En la parte más alta se encuentra el mirador de Matacás. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(125, 'MiradordelDuque2', 'A', 'Lugar desde el que salen varias embarcaciones turísticas que recorren el cañón del Sil.', 'MiradordelDuque2A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(126, 'BalconesdeMadrid1', 'B', 'Antiguas bodegas de piedra cerca del pueblo de Paradela.', 'MiradordelDuque2B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(127, 'BalconesdeMadrid1', 'C', 'Pequeña capilla entre viñas en el entorno de Nogueira (Abeleda). ', 'MiradordelDuque2C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(128, 'MiradordelDuque2', 'D', 'Detalle de la iglesia de San Pedro de Alais, en origen románica, pero muy modificada.', 'MiradordelDuque2D1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(129, 'MiradordelDuque2', 'D', 'La zona de Alais cuenta con una importante concentración de viñas y de bodegas.', 'MiradordelDuque2D2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(130, 'MiradordelDuque2', 'E', 'Torre del Homenaje en el Castillo de Castro Caldelas.', 'MiradordelDuque2E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(131, 'MiradordelDuque2', 'F', 'Antigua abadía de San Paio, con gran poder en época medieval, pero hoy totalmente abandonada.', 'MiradordelDuque2F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(132, 'MiradordelDuque2', 'G', 'Aquí desemboca el Edo, bonito río que desciende de forma vertiginosa por las laderas de la Ribeira. ', 'MiradordelDuque2G1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(133, 'MiradordelDuque2', 'G', 'En Castro Caldelas comienza una pequeña ruta que recorre las orillas de este río, pasando por varios molinos.', 'MiradordelDuque2G2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(134, 'MiradordosChelos1', 'A', 'Detalle de las calles del pueblo de San Lourenzo, donde se conservan bonitas construcciones tradicionales y vestigios de un antiguo monasterio.', 'MiradordosChelos1A1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(135, 'MiradordosChelos1', 'A', 'En las proximidades de San Lourenzo se encuentra al yacimiento medieval de San Vitor, en el que se han descubierto numerosas tumbas con forma humana.', 'MiradordosChelos1A2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(136, 'MiradordosChelos1', 'B', 'Lugar en el que existió un antiguo monasterio. ', 'MiradordosChelos1B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(137, 'MiradordosChelos1', 'C', 'Precioso bosque ocupando una gran superficie y en el que predominan los robles y los soutos de castaños. ', 'MiradordosChelos1C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(138, 'MiradordosChelos1', 'D', 'A orillas del Mao, que desemboca aquí en el Sil, discurre la famosa ruta de las pasarelas.', 'MiradordosChelos1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(139, 'MiradordosChelos1', 'E', 'Desde el monte (desnivel de 500 m.) baja una tubería con agua a gran velocidad, produciendo electricidad en esta pequeña central.', 'MiradordosChelos1E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(140, 'MiradordosChelos1', 'F', 'Este curioso topónimo hace referencia a SakarBoudio (conmemorando la victoria de un guerrero celta llamado Sakar).', 'MiradordosChelos1F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(141, 'MiradordosChelos1', 'G', 'Horno comunal de Bouzas, rehabilitado en el año 2008. ', 'MiradordosChelos1G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(142, 'MiradordosChelos1', 'H', 'Bosque de castaños (souto) en A Costrela', 'MiradordosChelos1H1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(143, 'MiradordosChelos1', 'H', 'Capilla de la Virgen de las Nieves. ', 'MiradordosChelos1H2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(144, 'MiradordosChelos1', 'I', 'Panorámica de A Barxa, a orillas del Sil y rodeado de viñedos. ', 'MiradordosChelos1I.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(145, 'MiradordosChelos1', 'J', 'Antigua bodega de piedra en Leirabella.', 'MiradordosChelos1J.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(146, 'MiradordosChelos1', 'K', 'Estrechas callejuelas y casas de piedra en Calvos. ', 'MiradordosChelos1K.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(147, 'MiradordosChelos1', 'L', 'Por este tramo del Sil discurren varias embarcaciones turísticas, con salida en los embarcaderos de Doade, Santo Estevo y Os Chancís.', 'MiradordosChelos1L.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(148, 'MiradordeSantiorxo1', 'A', 'Santuario barroco del S.XVIII de gran tradición en la comarca por los milagros que se le atribuyen aquí a la Virgen.', 'MiradordeSantiorxo1A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(149, 'MiradordeSantiorxo1', 'B', 'Casa Rectoral, situada junto a la iglesia y habilitada como alojamiento de turismo rural. ', 'MiradordeSantiorxo1B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(150, 'MiradordeSantiorxo1', 'C', 'Uno de los miradores más frecuentados de la Ribeira Sacra, muy cerca de Parada de Sil.', 'MiradordeSantiorxo1C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(151, 'MiradordeSantiorxo1', 'D', 'Moderna pasarela, que nos ofrece buenas vistas del cañón. El desnivel supera en este punto los 500 metros.', 'MiradordeSantiorxo1D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(152, 'MiradordeSantiorxo1', 'E', 'Recóndita aldea de Parada de Sil, ubicada en un entorno privilegiado justo en el borde del cañón del Sil.', 'MiradordeSantiorxo1E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(153, 'MiradordeSantiorxo1', 'F', 'Antiguo monasterio benedictino, localizado en un entorno privilegiado. Se conserva su iglesia y algunas de las dependencias de los monjes.', 'MiradordeSantiorxo1F1.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(154, 'MiradordeSantiorxo1', 'F', 'Se trata de uno de los mejores ejemplos del románico rural gallego, y uno de los puntos de interés más visitados de la Ribeira Sacra.', 'MiradordeSantiorxo1F2.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(155, 'MiradordaCividade3', 'A', 'Los Petos de Ánimas se asocian a la idea del culto a los muertos. Su fin es el de poder orar por las almas del purgatorio, para que asciendan a los cielos.', 'MiradordaCividade3A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(156, 'MiradordaCividade3', 'B', 'Antigua Casa Rectoral, construida en la primera mitad del s.XX.', 'MiradordaCividade3B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(157, 'MiradordaCividade3', 'C', 'Bonito Cruceiro en Alberguería, construido en el s.XIX. ', 'MiradordaCividade3C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(158, 'MiradordaCividade3', 'D', 'La iglesia de Vilar de Cerreda aparece en documentos medievales como parte de la feligresía de Santo Estevo de Ribas de Sil. ', 'MiradordaCividade3D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(159, 'MiradordaCividade3', 'E', 'Con sus 115 metros de altitud, construida en 1956, fue concebida en su momento como la mayor central de Europa. ', 'MiradordaCividade3E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(160, 'MiradordaCividade3', 'F', 'Se trata del punto más alto de la comarca (1.328 m). Cuenta una leyenda que en su cima hubo antiguamente un castillo.', 'MiradordaCividade3F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(161, 'SantoEstevodeAtan3', 'A', 'Tosca figura de un león protector, que representa a Cristo en el Apocalipsis. Tiene la cola erguida por encima de lomo y con la punta en forma de corazón, lo que simboliza el triunfo del bien sobre el mal. La cabeza está orientada hacia la puerta, invitándonos a entrar. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(162, 'SantoEstevodeAtan3', 'B', 'Tosca figura de un león protector, que representa a Cristo en el Apocalipsis. Tiene la cola erguida por encima de lomo y con la punta en forma de corazón, lo que simboliza el triunfo del bien sobre el mal. La cabeza está orientada hacia la puerta, invitándonos a entrar. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(163, 'SantoEstevodeAtan3', 'C', 'Enfrente del capitel de Adán y Eva, se representa una enorme serpiente entrelazada, con la cabeza en la parte central. Es el mismo Satanás, que los sedujo para cometer el pecado en el paraíso. Las constantes curvaturas indican la fatalidad y la capacidad de seducción del pecado.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(164, 'SantoEstevodeAtan3', 'D', 'Junto a la maléfica serpiente, encontramos otra figura, esta vez en representación del bien. Son las ramas del olivo, que simbolizaban la misericordia y el perdón divino.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(165, 'FerreiradePanton9', 'A', 'Máximo enemigo de Dios y del hombre, era representado como una serpiente con alas y asociado al fuego. Su imagen se utilizaba para representar al diablo y al infierno, recordando a los fieles los peligros de caer en el pecado.', 'FerreiradePanton9A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(166, 'FerreiradePanton9', 'B', 'Representación de una fiera con una presa en la boca. Simbología de la violencia y de la agresividad como atributos malignos. ', 'FerreiradePanton9B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(167, 'FerreiradePanton9', 'C', 'En la parte izquierda de la ventana, dos basiliscos entrelazados luchando. Sus cabezas están mutiladas, posiblemente en relación con el mal agüero que se suponía a este animal fabuloso, procedentes de huevos de gallo incubados por serpientes. Se representan en relación a la muerte en pecado y al anticristo.', 'FerreiradePanton9C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(168, 'FerreiradePanton9', 'D', 'Ciervo perseguido por un perro, que lo muerde en su pata trasera. De frente el cazador dispuesto a disparar su arco. ', 'FerreiradePanton9D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(169, 'FerreiradePanton9', 'E', 'En la parte inferior cuatro leones mirando hacia el suelo reafirmando su animalidad. En la parte superior, dos hombres mirando hacia el cielo, en señal de la perfección y de lo divino.', 'FerreiradePanton9E.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(170, 'FerreiradePanton9', 'F', 'Nos observa aquí un cuadrúpedo híbrido, referido en el apocalipsis como la bestia. Su monstruosidad se ve reflejada sobre todo en los dientes de sierra, preparados para devorar a sus víctimas. ', 'FerreiradePanton9F.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(171, 'FerreiradePanton9', 'G', 'Tramo dedicado a temas lúdicos, en relación con la espiritualidad del hombre. Un trobador meditativo escribiendo una composición, un músico tocando su instumento de tres cuerdas y un bailarina en actitud contorsionista.', 'FerreiradePanton9G.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(172, 'FerreiradePanton11', 'A', 'El profeta dominando a dos leones. Esta figura, muy repetida en el románico gallego, es un reclamo a la fe, e invita a los creyentes a superar peligros y tentaciones confiando en la palabra de Dios.', 'FerreiradePanton11A.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(173, 'FerreiradePanton11', 'B', 'Debajo de la imagen anterior vemos un ángel, con la mano derecha en acción de bendecir. A sus pies, dos leones humillados por su presencia.', 'FerreiradePanton11B.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(174, 'FerreiradePanton11', 'C', 'Animales mitológicos, con cabeza de águila y cuerpo de león, símbolo de la fuerza y de la vigilancia. En este caso están protegiendo con sus garras a dos bueyes y liberándolos de sus ataduras. ', 'FerreiradePanton11C.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(175, 'FerreiradePanton11', 'D', 'Vemos diez aves, picando en parejas unos frutos en forma de corazón. El fruto podría ser la palabra de Dios, mientras que el acto de picarlos significaría aceptarla y ganar la salvación.', 'FerreiradePanton11D.jpg');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(176, 'SanPaiodeDiomondi5', 'A', 'La mayor parte de los motivos que aparecen en los capiteles de Diomondi, son parejas de bestias enfrentadas, que se repiten además en muchos casos de un lado y de otro de la puerta. En este caso tenemos dos centauros, mirado hombre, mitad caballo, que en el románico habitualmente representan la brutalidad de la bajas pasiones y la lujuria. Se les representa aquí con arco y flecha, ávidos de mujeres, apuntando a las sirenas.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(177, 'SanPaiodeDiomondi5', 'B', 'Son mitad mujer, mitad pez, y representan la seducción y el sucumbir ante los placeres carnales. Están claramente relacionadas con los centauros, ambos  conformando una escena en la que se quiere advertir del peligro de sucumbir ante las tentaciones malignas derivadas de la seducción, de la lujuria y de las bajas pasiones. Los centauros sucumbirían ante el canto de las sirenas, perdiendo la razón y dejando actuar a los bajos instintos. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(178, 'SanPaiodeDiomondi5', 'C', 'Normalmente las aves en el románico tienen significado positivo. Por su capacidad de volar, simbolizan las almas y su deseo por alejarse de lo terrenal y ascender al reino de los cielos, en busca de los valores celestiales y de la vida eterna.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(179, 'SanPaiodeDiomondi5', 'D', 'Uno de los cuadrúpedos podría ser una Esfinge o una Manticora, elementos repetidos, pero poco frecuentes en el románico. Se trata de criaturas con cuerpo de león y cabeza humana. Simbolizan vigilantes míticos de castigo fulminante para cuantos osen entrar en lugares sagrados sin las disposiciones requeridas. Su simboligía es muy similar a la de las Esfinges egipcias, que también eran responsables de guardar la entrada a los templos. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(180, 'SanPaiodeDiomondi5', 'E', 'Este animal representa la nobleza y la fuerza, en clara referencia al propio Cristo. Es habitual ver a estos animales representados junto a las puertas de las iglesias románicas, como guardianes de los templos. No impiden el paso al recinto, pero advierten de que el umbral que se está a punto de traspasar separa el recinto sagrado, en el interior, y el profano del exterior, siendo el propio hombre quien se pregunte si se encuentra en condiciones de dar el paso y de entrar. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(181, 'SanMiguelEire3', 'A', 'En la segunda arquivolta de la entrada principal, destaca la figura de un cordero, flanqueado por una serie de círculos rosáceos. El animal representaría el Agnus Dei (Cordero de Dios, Jesucristo), rodeado por los círculos, desiguales entre sí, ya que cada uno representaba a un apóstol diferente. Si sumamos, veremos que unicamente hay 11 círculos por la ausencia de Judas. ', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(182, 'SanMiguelEire3', 'B', 'Sobre la puerta podemos ver una serie de círculos superpuestos grabados en la piedra. Este formato, reproducido en varias iglesias gallegas, ya aparece en vestigios anteriores al cristianismo, y eran utilizados bajo la creencia de que espantaban a los espíritus malignos. El cristianismo, como en otros muchos casos, los adoptó como propios, modificando su significado para representar la perfección y la vida eterna.', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(183, 'SanMiguelEire3', 'C', 'En la parte derecha de la puerta encontramos dos figuras, mitad hombre, mitad animal. Se trata de los evangelistas Lucas y Marcos, representados respectivamente, como es habitual en el románico, por un toro y por un león. Lo más normal sería que estuviesen acompañados por un águila (Juan) y un hombre (Mateo).', '-');
INSERT INTO `mapimage` (`id`, `mapa`, `id_punto`, `descripcion`, `uri`) VALUES(184, 'SanMiguelEire3', 'D', 'En la imposta opuesta podemos ver la representación de la cabeza de un hombre mirando hacia el suelo. La barba y el pelo simbolizan virilidad, sabiduría, fuerza y respeto. Podría ser una representación de Mateo (hombre), flanqueado por lo que algunos creen las alas de un águila (Juan), lo que completaría totalmente el tetramorfos (representación de los cuatro evangelistas). ', '-');
